/**
 * Lane Manager
 * Handles creation and management of X-parallel and Z-parallel lanes
 */

import { Cluster, Node } from "../../../../interfaces/simpleDataGenerator.interface";
import { EnhancedGeneratorConfig } from "../../../../interfaces/enhancedGenerator.interface";
import {
  LaneBounds,
  PropertyBounds,
  LaneDimensions,
  LaneManagerResult
} from "../types";
import { PropertyValueResolver } from "../../propertyValueResolver";
import { EndcapBlockCreator } from "../../blocks/endcapBlockCreator";
import { SwimLaneBlockCreator } from "../../blocks/swimlaneBlockCreator";
import { ShadowBlockCreator } from "../../blocks/shadowBlockCreator";
import { BLOCK_CONSTANTS } from "../../constants/blockConstants";
import { getDefaultXAxis, getDefaultZAxis } from "../../../../constants/axisDefaults";

export class LaneManager {
  private propertyResolver: PropertyValueResolver;
  private endcapCreator: EndcapBlockCreator;
  private swimlaneCreator: SwimLaneBlockCreator;
  private shadowCreator: ShadowBlockCreator;

  constructor() {
    this.propertyResolver = new PropertyValueResolver();
    this.endcapCreator = new EndcapBlockCreator();
    this.swimlaneCreator = new SwimLaneBlockCreator();
    this.shadowCreator = new ShadowBlockCreator();
  }

  /**
   * Creates all lanes (both X-parallel and Z-parallel) and returns the result
   */
  public createAllLanes(
    cluster: Cluster,
    targetOrigin: Vector3,
    config: EnhancedGeneratorConfig,
    lanesParent: Instance
  ): LaneManagerResult {
    // Create Z-parallel lanes first (lanes that run along Z axis)
    const zParallelModel = new Instance("Model");
    zParallelModel.Name = `ZParallel_Lanes_Group`;
    zParallelModel.Parent = lanesParent;

    const zParallelLanes = this.createZParallelLaneBlocks(
      cluster,
      zParallelModel,
      targetOrigin,
      config
    );

    // Now create X-parallel lanes (lanes that run along X axis)
    const xParallelModel = new Instance("Model");
    xParallelModel.Name = `XParallel_Lanes_Group`;
    xParallelModel.Parent = lanesParent;

    // Pass the Z-parallel lanes so we can calculate proper bounds
    const xParallelLanes = this.createXParallelLaneBlocks(
      cluster,
      xParallelModel,
      targetOrigin,
      config,
      zParallelLanes
    );

    // Calculate bounds for all lanes
    const allLaneBounds = this.calculateLaneBounds(
      xParallelLanes,
      zParallelLanes
    );

    return {
      xParallelLanes,
      zParallelLanes,
      allLaneBounds
    };
  }

  /**
   * Creates Z-parallel lane blocks (lanes that run along Z axis, grouped by X position)
   */
  public createZParallelLaneBlocks(
    cluster: Cluster,
    parent: Instance,
    _origin: Vector3,
    config: EnhancedGeneratorConfig
  ): Map<string, Part> {
    const swimlaneBlocks = new Map<string, Part>();
    // Use axis mapping if available - X axis sorts on service for Harness data
    const xAxisProperty = config.axisMapping?.xAxis || getDefaultXAxis(cluster.discoveredProperties);

    // Organize nodes by X grouping property to determine lane placement
    const nodesByType = new Map<string, Node[]>();
    const typeBounds = new Map<string, PropertyBounds>();

    // Get node radius from spacing config
    const nodeRadius = config?.spacing?.nodeRadius || 0.5;
    print(`[SWIMLANE CALC] Using nodeRadius=${nodeRadius}`);

    // Group nodes by X grouping property and calculate bounds
    cluster.groups[0].nodes.forEach((node: Node) => {
      const propertyValue = this.propertyResolver.getPropertyValue(
        node,
        xAxisProperty
      );

      if (!nodesByType.has(propertyValue)) {
        nodesByType.set(propertyValue, []);
        typeBounds.set(propertyValue, {
          minX: math.huge,
          maxX: -math.huge,
          minZ: math.huge,
          maxZ: -math.huge,
        });
      }

      nodesByType.get(propertyValue)!.push(node);

      const bounds = typeBounds.get(propertyValue)!;
      const oldMinX = bounds.minX;
      const oldMaxX = bounds.maxX;

      // Account for node radius when calculating bounds
      bounds.minX = math.min(bounds.minX, node.position.x - nodeRadius);
      bounds.maxX = math.max(bounds.maxX, node.position.x + nodeRadius);
      bounds.minZ = math.min(bounds.minZ, node.position.z - nodeRadius);
      bounds.maxZ = math.max(bounds.maxZ, node.position.z + nodeRadius);

      // Debug first few nodes of each type
      if (nodesByType.get(propertyValue)!.size() <= 3) {
        print(`[SWIMLANE CALC] ${propertyValue} node #${nodesByType.get(propertyValue)!.size()}: pos.x=${node.position.x}, bounds updated from (${oldMinX},${oldMaxX}) to (${bounds.minX},${bounds.maxX})`);
      }
    });

    // Create a block for each swimlane based on actual node positions
    let swimlaneIndex = 0;

    // Find the overall Z bounds across all lanes
    let overallMinZ = math.huge;
    let overallMaxZ = -math.huge;

    nodesByType.forEach((_nodes: Node[], typeName: string) => {
      const bounds = typeBounds.get(typeName)!;
      overallMinZ = math.min(overallMinZ, bounds.minZ);
      overallMaxZ = math.max(overallMaxZ, bounds.maxZ);
    });

    // Calculate the common center Z position for all lanes
    const commonCenterZ = (overallMinZ + overallMaxZ) / 2;

    nodesByType.forEach((nodes: Node[], typeName: string) => {
      const bounds = typeBounds.get(typeName)!;

      // Use actual node bounds to determine X position
      const centerX = (bounds.minX + bounds.maxX) / 2;
      // Use the common center Z for all lanes to ensure alignment
      const centerZ = commonCenterZ;

      // Calculate actual swimlane dimensions based on node bounds

      // Apply buffer to dimensions
      const zBuffer = BLOCK_CONSTANTS.DIMENSIONS.Z_PARALLEL_LANE_BUFFER;

      // Calculate this lane's specific width based on its actual node bounds - NO BUFFER!
      const laneWidth = bounds.maxX - bounds.minX;
      const blockWidth = laneWidth; // No buffer on X-axis to prevent overlap
      // Use the overall Z extent for all lanes to ensure uniform Z-length
      const blockDepth = (overallMaxZ - overallMinZ) + zBuffer * 2;

      // Debug: Print width of Z-parallel swimlane with node count
      print(`[Z-PARALLEL LANE] ${typeName}: nodes=${nodes.size()}, width=${blockWidth} (laneWidth=${laneWidth}, bounds=${bounds.minX} to ${bounds.maxX})`)

      // Fixed Y position for Z-parallel lane blocks - use SHADOW_LAYER_DISPLACEMENT above shadow block
      // Shadow block is at Y = 1.6 (top at 2.1)
      // X-axis blocks should have their tops at 2.1 + SHADOW_LAYER_DISPLACEMENT
      const shadowBlockTop =
        BLOCK_CONSTANTS.DIMENSIONS.UNIFORM_SHADOW_THICKNESS +
        BLOCK_CONSTANTS.DIMENSIONS.Z_FIGHTING_OFFSET +
        BLOCK_CONSTANTS.DIMENSIONS.UNIFORM_SHADOW_THICKNESS / 2;
      const blockYPosition =
        shadowBlockTop +
        BLOCK_CONSTANTS.DIMENSIONS.SHADOW_LAYER_DISPLACEMENT +
        BLOCK_CONSTANTS.DIMENSIONS.Z_TO_X_SHADOW_LANE_SPACING; // Above X shadow lanes to avoid z-fighting

      // Get color from Z_AXIS_COLORS array using swimlane index
      const colors = BLOCK_CONSTANTS.COLORS.Z_AXIS_COLORS;
      const color = colors[swimlaneIndex % colors.size()];

      // Create swimlane block
      const swimlaneBlock = this.swimlaneCreator.createSwimLaneBlock({
        position: new Vector3(centerX, blockYPosition, centerZ),
        width: blockWidth,
        depth: blockDepth,
        height: BLOCK_CONSTANTS.DIMENSIONS.UNIFORM_SHADOW_THICKNESS,
        color: color,
        typeName: typeName,
        parent: parent,
        propertyName: xAxisProperty,
      });

      // Create swimlane model with endcaps
      this.endcapCreator.createSwimlaneWithEndcaps({
        swimlaneBlock: swimlaneBlock,
        swimlaneName: typeName,
        parent: parent,
        gap: 2, // Increased gap for person endcaps
        isZAxis: false,
      });

      // Store the block in the map (still store the block, not the model)
      swimlaneBlocks.set(typeName, swimlaneBlock);

      swimlaneIndex++;
    });

    return swimlaneBlocks;
  }

  /**
   * Creates X-parallel lane blocks (lanes that run along X axis, grouped by Z position)
   */
  public createXParallelLaneBlocks(
    cluster: Cluster,
    parent: Instance,
    targetOrigin: Vector3,
    config: EnhancedGeneratorConfig | undefined,
    zParallelLanes: Map<string, Part>
  ): Map<string, Part> {
    const swimlaneBlocks = new Map<string, Part>();
    // Use axis mapping if available - Z axis sorts on type for Harness data
    const zAxisProperty = config?.axisMapping?.zAxis || getDefaultZAxis(cluster.discoveredProperties);

    // Organize nodes by z-axis property
    const nodesByProperty = new Map<string, Node[]>();

    cluster.groups[0].nodes.forEach((node: Node) => {
      const propertyValue = this.propertyResolver.getPropertyValue(
        node,
        zAxisProperty
      );
      if (!nodesByProperty.has(propertyValue)) {
        nodesByProperty.set(propertyValue, []);
      }
      nodesByProperty.get(propertyValue)!.push(node);
    });

    // Calculate dimensions based on content
    const { lanePositions } =
      this.calculateXParallelLaneDimensions(nodesByProperty);

    // Create property bounds - X-parallel lanes should span full width of Z-parallel lanes
    const propertyBounds = new Map<string, PropertyBounds>();

    // Calculate the actual X extent from the Z-parallel lanes
    let fullMinX = math.huge;
    let fullMaxX = -math.huge;

    zParallelLanes.forEach((lane: Part) => {
      const halfWidth = lane.Size.X / 2;
      fullMinX = math.min(fullMinX, lane.Position.X - halfWidth);
      fullMaxX = math.max(fullMaxX, lane.Position.X + halfWidth);
    });

    // Set same X bounds for all X-parallel lanes
    lanePositions.forEach((position: { z: number; minZ: number; maxZ: number }, propertyValue: string) => {
      propertyBounds.set(propertyValue, {
        minX: fullMinX,
        maxX: fullMaxX,
        minZ: position.minZ,
        maxZ: position.maxZ,
      });
    });

    // Create X-parallel shadow blocks with fixed dimensions
    const shadowBlockTop =
      BLOCK_CONSTANTS.DIMENSIONS.UNIFORM_SHADOW_THICKNESS +
      BLOCK_CONSTANTS.DIMENSIONS.Z_FIGHTING_OFFSET +
      BLOCK_CONSTANTS.DIMENSIONS.UNIFORM_SHADOW_THICKNESS / 2;
    const yPosition =
      shadowBlockTop + BLOCK_CONSTANTS.DIMENSIONS.SHADOW_LAYER_DISPLACEMENT;

    this.shadowCreator.createXParallelShadowBlocks(
      nodesByProperty,
      propertyBounds,
      parent,
      yPosition,
      swimlaneBlocks,
      zAxisProperty,
      targetOrigin
    );
    return swimlaneBlocks;
  }

  /**
   * Calculate the required dimensions for X-parallel lanes based on content
   */
  public calculateXParallelLaneDimensions(
    nodesByProperty: Map<string, Node[]>
  ): LaneDimensions {
    // Calculate Z positions for each lane
    const lanePositions = new Map<
      string,
      { z: number; minZ: number; maxZ: number }
    >();

    // Collect actual Z bounds for each property group and store with property names
    const boundsArray: Array<
      [string, { minZ: number; maxZ: number; centerZ: number }]
    > = [];

    nodesByProperty.forEach((nodes: Node[], propertyValue: string) => {
      let minZ = math.huge;
      let maxZ = -math.huge;
      nodes.forEach((node: Node) => {
        minZ = math.min(minZ, node.position.z);
        maxZ = math.max(maxZ, node.position.z);
      });
      const centerZ = (minZ + maxZ) / 2;
      boundsArray.push([propertyValue, { minZ, maxZ, centerZ }]);
    });

    // Sort by center Z position to maintain proper ordering
    boundsArray.sort((a: [string, { minZ: number; maxZ: number; centerZ: number }], b: [string, { minZ: number; maxZ: number; centerZ: number }]) => a[1].centerZ < b[1].centerZ);

    // Assign lane positions based on actual positions
    boundsArray.forEach(([propertyValue, bounds]: [string, { minZ: number; maxZ: number; centerZ: number }]) => {
      lanePositions.set(propertyValue, {
        z: bounds.centerZ, // Use actual center position
        minZ: bounds.minZ,
        maxZ: bounds.maxZ,
      });
    });

    return { lanePositions };
  }

  /**
   * Calculate bounds for all lanes to determine shadow size
   */
  public calculateLaneBounds(
    xParallelLanes: Map<string, Part>,
    zParallelLanes: Map<string, Part>
  ): LaneBounds {
    let minX = math.huge;
    let maxX = -math.huge;
    let minZ = math.huge;
    let maxZ = -math.huge;

    // Check X-parallel lanes
    xParallelLanes.forEach((block: Part) => {
      const halfWidth = block.Size.X / 2;
      const halfDepth = block.Size.Z / 2;
      minX = math.min(minX, block.Position.X - halfWidth);
      maxX = math.max(maxX, block.Position.X + halfWidth);
      minZ = math.min(minZ, block.Position.Z - halfDepth);
      maxZ = math.max(maxZ, block.Position.Z + halfDepth);
    });

    // Check Z-parallel lanes
    zParallelLanes.forEach((block: Part) => {
      const halfWidth = block.Size.X / 2;
      const halfDepth = block.Size.Z / 2;
      minX = math.min(minX, block.Position.X - halfWidth);
      maxX = math.max(maxX, block.Position.X + halfWidth);
      minZ = math.min(minZ, block.Position.Z - halfDepth);
      maxZ = math.max(maxZ, block.Position.Z + halfDepth);
    });

    return {
      width: maxX - minX,
      depth: maxZ - minZ,
    };
  }

  /**
   * Calculate the Z-axis offset needed to center lanes
   */
  public calculateZAxisOffset(
    cluster: Cluster,
    zAxisProperty: string
  ): number {
    // Organize nodes by z-axis property and find bounds
    const propertyBounds = new Map<string, { minZ: number; maxZ: number }>();

    cluster.groups[0].nodes.forEach((node: Node) => {
      const propertyValue = this.propertyResolver.getPropertyValue(
        node,
        zAxisProperty
      );
      if (!propertyBounds.has(propertyValue)) {
        propertyBounds.set(propertyValue, {
          minZ: math.huge,
          maxZ: -math.huge,
        });
      }

      const bounds = propertyBounds.get(propertyValue)!;
      bounds.minZ = math.min(bounds.minZ, node.position.z);
      bounds.maxZ = math.max(bounds.maxZ, node.position.z);
    });

    // Calculate collective bounds of all Z-axis lanes
    let collectiveMinZ = math.huge;
    let collectiveMaxZ = -math.huge;

    propertyBounds.forEach((bounds: { minZ: number; maxZ: number }) => {
      collectiveMinZ = math.min(collectiveMinZ, bounds.minZ);
      collectiveMaxZ = math.max(collectiveMaxZ, bounds.maxZ);
    });

    // Calculate the center of the collective Z-axis lanes
    const collectiveCenter = (collectiveMinZ + collectiveMaxZ) / 2;

    // The group shadow block is centered at Z=0, so we need to offset the lanes
    const offsetZ = 0 - collectiveCenter;

    return offsetZ;
  }
}

/**
 * Position Calculator for Unified Data Renderer
 * 
 * Handles swim lane positioning and origin centering for nodes
 * Refactored into modular internal classes for better organization
 */

import { Cluster, Node } from "../../../../interfaces/simpleDataGenerator.interface";
import { EnhancedGeneratorConfig } from "../../../../interfaces/enhancedGenerator.interface";
import { IPositionCalculator, SpacingConfig } from "../interfaces";
import { RENDERER_CONSTANTS } from "../../dataGeneratorRobloxRendererUtils/constants";
import { PropertyValueResolver } from "../../propertyValueResolver";
import { POSITION_CONSTANTS } from "../../constants/positionConstants";
import { getDefaultXAxis, getDefaultZAxis } from "../../../../constants/axisDefaults";

/**
 * Bounds Calculator - Handles cluster boundary calculations
 */
class BoundsCalculator {
  /**
   * Calculate bounds of the cluster
   */
  public calculateBounds(cluster: Cluster): { minX: number, maxX: number, minY: number, minZ: number, maxZ: number } {
    let minX = math.huge;
    let maxX = -math.huge;
    let minY = math.huge;
    let minZ = math.huge;
    let maxZ = -math.huge;

    cluster.groups[0].nodes.forEach(node => {
      minX = math.min(minX, node.position.x);
      maxX = math.max(maxX, node.position.x);
      minY = math.min(minY, node.position.y);
      minZ = math.min(minZ, node.position.z);
      maxZ = math.max(maxZ, node.position.z);
    });

    return { minX, maxX, minY, minZ, maxZ };
  }
}

/**
 * Position Mapper - Handles position transformations and property mapping
 */
class PositionMapper {
  private propertyResolver: PropertyValueResolver;

  constructor(propertyResolver: PropertyValueResolver) {
    this.propertyResolver = propertyResolver;
  }

  /**
   * Apply position offsets to all nodes
   */
  public applyOffsets(cluster: Cluster, offsetX: number, offsetY: number, offsetZ: number): void {
    cluster.groups[0].nodes.forEach(node => {
      node.position.x += offsetX;
      node.position.y += offsetY;
      node.position.z += offsetZ;
    });
  }

  /**
   * Create position mapping for a property
   */
  public createPropertyPositionMap(nodesByTypeAndLayer: Map<string, Node[]>, propertyName: string, isYAxis: boolean = false): Map<string, number> {
    const uniqueValues = new Set<string>();

    // Collect all unique values for the property
    nodesByTypeAndLayer.forEach(nodes => {
      nodes.forEach(node => {
        const value = this.propertyResolver.getPropertyValue(node, propertyName);
        uniqueValues.add(value);
      });
    });

    // Sort values and assign positions
    const sortedValues = [...uniqueValues].sort();
    const positionMap = new Map<string, number>();

    sortedValues.forEach((value, index) => {
      if (isYAxis) {
        // For Y-axis, position values vertically
        positionMap.set(value, index);
      } else {
        // Position values apart on the Z axis
        positionMap.set(value, (index - sortedValues.size() / 2) * POSITION_CONSTANTS.Z_DIMENSION_GROUP_SPACING);
      }
    });

    return positionMap;
  }
}

/**
 * Node Organizer - Handles node organization and sorting
 */
class NodeOrganizer {
  private propertyResolver: PropertyValueResolver;

  constructor(propertyResolver: PropertyValueResolver) {
    this.propertyResolver = propertyResolver;
  }

  /**
   * Organize nodes by layer and property
   */
  public organizeNodesByProperty(cluster: Cluster, propertyName: string) {
    const nodesByLayer = new Map<number, Node[]>();
    const typeCounters = new Map<string, number>();
    const nodesByTypeAndLayer = new Map<string, Node[]>();

    // First organize by layer
    cluster.groups.forEach(group => {
      group.nodes.forEach((node) => {
        const nodeWithLevel = node as Node & { level?: number };
        const layer = nodeWithLevel.level || 1;

        if (!nodesByLayer.has(layer)) {
          nodesByLayer.set(layer, []);
        }
        nodesByLayer.get(layer)!.push(node);
      });
    });

    // Then organize by property and layer
    nodesByLayer.forEach((nodes, layer) => {
      nodes.forEach(node => {
        const propertyValue = this.propertyResolver.getPropertyValue(node, propertyName);
        const key = `${propertyValue}-${layer}`;

        if (!nodesByTypeAndLayer.has(key)) {
          nodesByTypeAndLayer.set(key, []);
        }
        nodesByTypeAndLayer.get(key)!.push(node);

        // Track total count per property value
        typeCounters.set(propertyValue, (typeCounters.get(propertyValue) || 0) + 1);
      });
    });

    return { nodesByLayer, nodesByTypeAndLayer, typeCounters };
  }

  /**
   * Sort types by count (descending)
   */
  public sortTypesByCount(typeCounters: Map<string, number>): string[] {
    const sortedTypes: string[] = [];
    typeCounters.forEach((_, nodeType) => {
      sortedTypes.push(nodeType);
    });

    // Use table.sort instead of manual bubble sort
    table.sort(sortedTypes, (a, b) => {
      const countA = typeCounters.get(a) || 0;
      const countB = typeCounters.get(b) || 0;
      return countB > countA;
    });

    return sortedTypes;
  }
}

/**
 * Swim Lane Calculator - Handles swim lane positioning calculations
 */
class SwimLaneCalculator {
  private propertyResolver: PropertyValueResolver;

  constructor(propertyResolver: PropertyValueResolver) {
    this.propertyResolver = propertyResolver;
  }

  /**
   * Calculate X positions for each type column
   */
  public calculateTypeColumnPositions(
    sortedTypes: string[],
    nodesByTypeAndLayer: Map<string, Node[]>,
    numLayers: number,
    spacing: SpacingConfig
  ): Map<string, number> {
    let typeXOffset = 0;
    const typeXPositions = new Map<string, number>();

    sortedTypes.forEach((nodeType, typeIndex) => {
      typeXPositions.set(nodeType, typeXOffset);

      // Find max nodes of this type in any layer
      let maxNodesInLayer = 0;
      for (let layer = 1; layer <= numLayers; layer++) {
        const key = `${nodeType}-${layer}`;
        const nodes = nodesByTypeAndLayer.get(key) || [];
        maxNodesInLayer = math.max(maxNodesInLayer, nodes.size());
      }

      // Move to next type column
      typeXOffset += maxNodesInLayer * spacing.nodeSpacing;
      if (typeIndex < sortedTypes.size() - 1) {
        typeXOffset += spacing.swimlaneSpacing; // Gap between swim lanes
      }
    });

    return typeXPositions;
  }

  /**
   * Assign positions to nodes with property-based positioning
   */
  public assignNodePositionsWithProperties(
    sortedTypes: string[],
    nodesByTypeAndLayer: Map<string, Node[]>,
    typeXPositions: Map<string, number>,
    numLayers: number,
    spacing: SpacingConfig,
    config: EnhancedGeneratorConfig,
    zAxisProperty: string,
    zPositionMap: Map<string, number>,
    yPositionMap?: Map<string, number>
  ): void {
    const typeNodeCounters = new Map<string, number>();
    sortedTypes.forEach(nodeType => typeNodeCounters.set(nodeType, 0));

    // Check if Y-axis should use property-based positioning
    const yAxisFromMapping = config.axisMapping?.yAxis;
    const useLayerForY = !yAxisFromMapping || yAxisFromMapping === "none";
    const yAxisProperty = yAxisFromMapping || "type";

    for (let layer = 1; layer <= numLayers; layer++) {
      // Invert Y so layer 1 is at top
      const layerY = RENDERER_CONSTANTS.POSITIONING.BASE_Y + 
                    (numLayers - layer) * spacing.layerSpacing;

      sortedTypes.forEach(xValue => {
        const key = `${xValue}-${layer}`;
        const nodes = nodesByTypeAndLayer.get(key) || [];

        // Sort nodes alphabetically within type-layer group
        table.sort(nodes, (a, b) => a.name < b.name);

        nodes.forEach((node, index) => {
          const baseX = typeXPositions.get(xValue)!;

          // Find max nodes of this type for centering
          let maxNodesForType = 0;
          for (let checkLayer = 1; checkLayer <= numLayers; checkLayer++) {
            const checkKey = `${xValue}-${checkLayer}`;
            const checkNodes = nodesByTypeAndLayer.get(checkKey) || [];
            maxNodesForType = math.max(maxNodesForType, checkNodes.size());
          }

          // Calculate centering offset
          const laneWidth = maxNodesForType * spacing.nodeSpacing;
          const nodesWidth = nodes.size() * spacing.nodeSpacing;
          const centeringOffset = (laneWidth - nodesWidth) / 2;

          const x = baseX + centeringOffset + index * spacing.nodeSpacing;

          // Get Z position based on z-axis property
          const zValue = this.propertyResolver.getPropertyValue(node, zAxisProperty);
          const z = zPositionMap.get(zValue) || 0;

          // Get Y position based on Y-axis configuration
          const y = useLayerForY 
            ? layerY 
            : (yPositionMap!.get(this.propertyResolver.getPropertyValue(node, yAxisProperty)) || 0) * spacing.layerSpacing + RENDERER_CONSTANTS.POSITIONING.BASE_Y;

          // Update node position
          node.position = { x, y, z };

          // Add type number for labeling
          const typeCounter = typeNodeCounters.get(xValue)! + 1;
          typeNodeCounters.set(xValue, typeCounter);
          const paddedNumber = typeCounter < 10 ? `0${typeCounter}` : `${typeCounter}`;
          const nodeWithType = node as Node & { typeNumber?: string };
          nodeWithType.typeNumber = `${xValue.lower()}${paddedNumber}`;
        });
      });
    }
  }
}

/**
 * Main PositionCalculator - Orchestrates all modules
 */
export class PositionCalculator implements IPositionCalculator {
  private propertyResolver: PropertyValueResolver;
  private boundsCalculator: BoundsCalculator;
  private positionMapper: PositionMapper;
  private nodeOrganizer: NodeOrganizer;
  private swimLaneCalculator: SwimLaneCalculator;

  constructor() {
    this.propertyResolver = new PropertyValueResolver();
    this.boundsCalculator = new BoundsCalculator();
    this.positionMapper = new PositionMapper(this.propertyResolver);
    this.nodeOrganizer = new NodeOrganizer(this.propertyResolver);
    this.swimLaneCalculator = new SwimLaneCalculator(this.propertyResolver);
  }

  /**
   * Get bounds of the cluster (public method for external use)
   */
  public getClusterBounds(cluster: Cluster): { minX: number, maxX: number, minY: number, minZ: number, maxZ: number } {
    return this.boundsCalculator.calculateBounds(cluster);
  }

  /**
   * Centers the bottom of the group at the specified origin
   */
  public centerBottomAtOrigin(cluster: Cluster, origin: Vector3, config?: EnhancedGeneratorConfig): void {
    if (cluster.groups[0].nodes.size() === 0) return;

    // Find bounding box
    const bounds = this.boundsCalculator.calculateBounds(cluster);

    // Calculate offsets to center bottom at origin
    const centerX = (bounds.minX + bounds.maxX) / 2;
    const offsetX = origin.X - centerX;

    // Apply origin Y offset if configured
    const yOffset = config?.spacing?.originYOffset || 0;
    const offsetY = origin.Y - bounds.minY + yOffset; // Bottom of group at origin Y + offset
    const offsetZ = origin.Z;

    // Ensure nodes stay above ground level
    const minFinalY = bounds.minY + offsetY;
    const groundClearanceAdjustment = minFinalY < POSITION_CONSTANTS.MIN_GROUND_CLEARANCE ? POSITION_CONSTANTS.MIN_GROUND_CLEARANCE - minFinalY : 0;
    const finalOffsetY = offsetY + groundClearanceAdjustment;

    // Apply offsets to all nodes
    this.positionMapper.applyOffsets(cluster, offsetX, finalOffsetY, offsetZ);
  }

  /**
   * Calculates swim lane positions for layer-based data
   */
  public calculateLayerSwimLanePositions(cluster: Cluster, config: EnhancedGeneratorConfig): void {
    const spacing = this.getSpacingConfig(config);
    const numLayers = config.layers.size();

    // Use spatial grouping properties if available, otherwise use dynamic defaults
    const discoveredProperties = cluster.discoveredProperties;
    const xAxisProperty = config.axisMapping?.xAxis || getDefaultXAxis(discoveredProperties);
    const zAxisProperty = config.axisMapping?.zAxis || getDefaultZAxis(discoveredProperties);

    // Organize nodes by layer and X grouping property
    const { nodesByTypeAndLayer, typeCounters } = this.nodeOrganizer.organizeNodesByProperty(cluster, xAxisProperty);

    // Sort values by count
    const sortedTypes = this.nodeOrganizer.sortTypesByCount(typeCounters);

    // Calculate type column positions
    const typeXPositions = this.swimLaneCalculator.calculateTypeColumnPositions(
      sortedTypes,
      nodesByTypeAndLayer,
      numLayers,
      spacing
    );

    // Create Z position mapping for the z-axis property
    const zPositionMap = this.positionMapper.createPropertyPositionMap(nodesByTypeAndLayer, zAxisProperty);

    // Create Y position mapping if using property-based Y-axis
    const yAxisFromMapping = config.axisMapping?.yAxis;
    const yPositionMap = (!yAxisFromMapping || yAxisFromMapping === "none") 
      ? undefined 
      : this.positionMapper.createPropertyPositionMap(nodesByTypeAndLayer, yAxisFromMapping, true);

    // Assign positions to each node
    this.swimLaneCalculator.assignNodePositionsWithProperties(
      sortedTypes,
      nodesByTypeAndLayer,
      typeXPositions,
      numLayers,
      spacing,
      config,
      zAxisProperty,
      zPositionMap,
      yPositionMap
    );
  }

  /**
   * Get spacing configuration
   */
  private getSpacingConfig(config: EnhancedGeneratorConfig): SpacingConfig {
    return config.spacing || {
      nodeHeight: RENDERER_CONSTANTS.HEXAGON.HEIGHT,
      nodeRadius: RENDERER_CONSTANTS.HEXAGON.WIDTH / 2,
      layerSpacing: RENDERER_CONSTANTS.POSITIONING.LEVEL_SPACING,
      nodeSpacing: RENDERER_CONSTANTS.POSITIONING.COLUMN_SPACING,
      swimlaneSpacing: RENDERER_CONSTANTS.POSITIONING.COLUMN_SPACING
    };
  }
}

/**
 * Update Manager for Unified Data Renderer
 * 
 * Handles incremental updates of the graph
 */

import { Cluster, Node, Link, Group } from "../../../../interfaces/simpleDataGenerator.interface";
import { EnhancedGeneratorConfig, LayerConfig } from "../../../../interfaces/enhancedGenerator.interface";
import { IUpdateManager } from "../interfaces";
import { makeHexagon } from "../../../hexagonMaker";
import { createRopeConnectors } from "../../dataGeneratorRobloxRendererUtils/ropeCreator";
import { RENDERER_CONSTANTS } from "../../dataGeneratorRobloxRendererUtils/constants";
import { COLOR_PALETTES, NODE_TYPE_NAMES, ANIMAL_TYPES, DEFAULT_ATTACHMENTS } from "../constants";

export class UpdateManager implements IUpdateManager {
  private nodeIdCounter = 0;
  private linkIdCounter = 0;

  /**
   * Performs incremental update of the graph
   */
  public performIncrementalUpdate(
    newConfig: EnhancedGeneratorConfig, 
    nodesFolder: Folder, 
    linksFolder: Folder, 
    origin: Vector3
  ): void {
    // Build current state
    const currentNodesByLayer = this.buildCurrentState(nodesFolder);

    // Clear all existing connections (we'll rebuild them)
    linksFolder.ClearAllChildren();

    // Create update plan
    const { updatedNodeToHexagon, allNodes, nodesByLayer } = this.processLayers(
      newConfig,
      currentNodesByLayer,
      nodesFolder
    );

    // Remove layers that no longer exist
    this.removeObsoleteLayers(currentNodesByLayer, newConfig.layers.size());

    // Create cluster for positioning
    const cluster = this.createClusterFromNodes(allNodes);

    // Recalculate positions (would normally use PositionCalculator)
    // For now, keeping existing logic inline
    this.updateNodePositions(allNodes, updatedNodeToHexagon);

    // Recreate connections
    const allLinks = this.recreateConnections(newConfig, nodesByLayer);
    cluster.relations = allLinks;

    // Create rope connectors
    createRopeConnectors({
      cluster,
      nodeToHexagon: updatedNodeToHexagon,
      linksFolder,
      visualization: newConfig.visualization,
      linkDiameter: newConfig.spacing?.linkDiameter
    });

  }

  /**
   * Build current state map of nodes by layer
   */
  private buildCurrentState(nodesFolder: Folder): Map<number, Model[]> {
    const currentNodesByLayer = new Map<number, Model[]>();

    nodesFolder.GetChildren().forEach(node => {
      if (node.IsA("Model")) {
        // Extract layer number from node name (e.g., "Hexagon_L1_N2")
        const match = node.Name.match("Hexagon_L(\\d+)_N(\\d+)");
        if (match) {
          const layerNum = tonumber(match[1]) || 1;
          if (!currentNodesByLayer.has(layerNum)) {
            currentNodesByLayer.set(layerNum, []);
          }
          currentNodesByLayer.get(layerNum)!.push(node);
        }
      }
    });

    // Sort nodes within each layer by their index
    currentNodesByLayer.forEach((nodes, layer) => {
      table.sort(nodes, (a, b) => {
        const matchA = a.Name.match("Hexagon_L\\d+_N(\\d+)");
        const matchB = b.Name.match("Hexagon_L\\d+_N(\\d+)");
        const indexA = matchA ? tonumber(matchA[1]) || 0 : 0;
        const indexB = matchB ? tonumber(matchB[1]) || 0 : 0;
        return indexA < indexB;
      });
    });

    return currentNodesByLayer;
  }

  /**
   * Process layers and handle node updates
   */
  private processLayers(
    newConfig: EnhancedGeneratorConfig,
    currentNodesByLayer: Map<number, Model[]>,
    nodesFolder: Folder
  ) {
    const updatedNodeToHexagon = new Map<string, Model>();
    const allNodes: Node[] = [];
    const nodesByLayer = new Map<number, Node[]>();
    const newNodesToCreate: Node[] = [];

    newConfig.layers.forEach((newLayer, layerIndex) => {
      const layerNum = layerIndex + 1;
      const currentLayerNodes = currentNodesByLayer.get(layerNum) || [];
      const layerNodes: Node[] = [];

      // Handle node count changes
      if (newLayer.numNodes > currentLayerNodes.size()) {
        // Add new nodes
        for (let i = 0; i < newLayer.numNodes; i++) {
          if (i < currentLayerNodes.size()) {
            // Keep existing node
            const hexagon = currentLayerNodes[i];
            const node = this.createNodeFromHexagon(hexagon, newConfig, i, layerNum);
            allNodes.push(node);
            layerNodes.push(node);
            updatedNodeToHexagon.set(node.uuid, hexagon);
          } else {
            // Create new node
            const node = this.createNewNode(newConfig, i, layerNum, newLayer);
            allNodes.push(node);
            layerNodes.push(node);
            newNodesToCreate.push(node);
          }
        }
      } else if (newLayer.numNodes < currentLayerNodes.size()) {
        // Remove excess nodes
        for (let i = 0; i < currentLayerNodes.size(); i++) {
          if (i < newLayer.numNodes) {
            // Keep existing node
            const hexagon = currentLayerNodes[i];
            const node = this.createNodeFromHexagon(hexagon, newConfig, i, layerNum);
            allNodes.push(node);
            layerNodes.push(node);
            updatedNodeToHexagon.set(node.uuid, hexagon);
          } else {
            // Remove excess node
            currentLayerNodes[i].Destroy();
          }
        }
      } else {
        // Same number of nodes, just update
        currentLayerNodes.forEach((hexagon, i) => {
          const node = this.createNodeFromHexagon(hexagon, newConfig, i, layerNum);
          allNodes.push(node);
          layerNodes.push(node);
          updatedNodeToHexagon.set(node.uuid, hexagon);
        });
      }

      nodesByLayer.set(layerNum, layerNodes);
    });

    // Create hexagons for new nodes
    newNodesToCreate.forEach(node => {
      const hexagon = this.createSingleHexagon(node, nodesFolder, newConfig);
      updatedNodeToHexagon.set(node.uuid, hexagon);
    });

    return { updatedNodeToHexagon, allNodes, nodesByLayer };
  }

  /**
   * Remove layers that no longer exist
   */
  private removeObsoleteLayers(currentNodesByLayer: Map<number, Model[]>, newLayerCount: number): void {
    currentNodesByLayer.forEach((nodes, layerNum) => {
      if (layerNum > newLayerCount) {
        nodes.forEach(node => node.Destroy());
      }
    });
  }

  /**
   * Create cluster from nodes
   */
  private createClusterFromNodes(allNodes: Node[]): Cluster {
    const mainGroup: Group = {
      id: "group_1",
      name: "Group1",
      nodes: allNodes
    };

    return {
      groups: [mainGroup],
      relations: []
    };
  }

  /**
   * Update node positions by moving hexagons
   */
  private updateNodePositions(allNodes: Node[], nodeToHexagon: Map<string, Model>): void {
    allNodes.forEach(node => {
      const hexagon = nodeToHexagon.get(node.uuid);
      if (hexagon && node.position) {
        const currentPos = hexagon.PrimaryPart?.Position || new Vector3(0, 0, 0);
        const targetPos = new Vector3(node.position.x, node.position.y, node.position.z);
        const offset = targetPos.sub(currentPos);

        // Move all parts manually to preserve anchoring
        hexagon.GetChildren().forEach(part => {
          if (part.IsA("BasePart")) {
            part.Position = part.Position.add(offset);
          }
        });
      }
    });
  }

  /**
   * Recreate connections based on new configuration
   */
  private recreateConnections(
    config: EnhancedGeneratorConfig,
    nodesByLayer: Map<number, Node[]>
  ): Link[] {
    const allLinks: Link[] = [];
    this.linkIdCounter = 0;

    config.layers.forEach((layer, layerIndex) => {
      const layerNum = layerIndex + 1;
      const currentLayerNodes = nodesByLayer.get(layerNum) || [];
      const nextLayerNodes = nodesByLayer.get(layerNum + 1);

      // Create intra-layer connections
      const allowSameLevelLinks = config.visualization?.allowSameLevelLinks ?? true;
      if (allowSameLevelLinks && layer.connectionsPerNode > 0 && currentLayerNodes.size() > 1) {
        currentLayerNodes.forEach((sourceNode, nodeIndex) => {
          const availableTargets = currentLayerNodes.filter((_, idx) => idx !== nodeIndex);
          const numConnections = math.min(layer.connectionsPerNode, availableTargets.size());

          // Shuffle and select targets
          const shuffled = [...availableTargets];
          for (let i = shuffled.size() - 1; i > 0; i--) {
            const j = math.random(0, i);
            [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
          }

          for (let i = 0; i < numConnections; i++) {
            const targetNode = shuffled[i];
            const link = this.createLink(sourceNode, targetNode, config);
            allLinks.push(link);
          }
        });
      }

      // Create inter-layer connections
      if (nextLayerNodes && nextLayerNodes.size() > 0) {
        currentLayerNodes.forEach(sourceNode => {
          const targetNode = nextLayerNodes[math.random(0, nextLayerNodes.size() - 1)];
          const link = this.createLink(sourceNode, targetNode, config);
          allLinks.push(link);
        });
      }
    });

    return allLinks;
  }

  /**
   * Create a link between two nodes
   */
  private createLink(sourceNode: Node, targetNode: Node, config: EnhancedGeneratorConfig): Link {
    const linkTypeIndex = this.linkIdCounter % config.numLinkTypes;
    const linkColor = COLOR_PALETTES.LINK_COLORS[linkTypeIndex % COLOR_PALETTES.LINK_COLORS.size()];

    return {
      uuid: `link_${this.linkIdCounter++}`,
      type: `Link${linkTypeIndex + 1}`,
      sourceNodeUuid: sourceNode.uuid,
      targetNodeUuid: targetNode.uuid,
      color: linkColor
    };
  }

  /**
   * Creates a node from an existing hexagon model
   */
  private createNodeFromHexagon(hexagon: Model, config: EnhancedGeneratorConfig, nodeIndex: number, layerNum: number): Node {
    const nodeTypeIndex = nodeIndex % config.numNodeTypes;
    const nodeTypeName = NODE_TYPE_NAMES[math.min(nodeTypeIndex, NODE_TYPE_NAMES.size() - 1)];
    const color = COLOR_PALETTES.NODE_COLORS[nodeTypeIndex % COLOR_PALETTES.NODE_COLORS.size()];

    const primaryPart = hexagon.PrimaryPart;
    const position = primaryPart ? primaryPart.Position : new Vector3(0, 0, 0);

    const node: Node = {
      uuid: `node_${layerNum}_${nodeIndex}`,
      name: `${nodeTypeName} ${nodeIndex + 1}`,
      type: nodeTypeName as "People" | "Animals",
      position: { x: position.X, y: position.Y, z: position.Z },
      color: color,
      attachmentNames: DEFAULT_ATTACHMENTS
    };

    // Add level property for swim lane algorithm
    const nodeWithLevel = node as Node & { level: number };
    nodeWithLevel.level = layerNum;

    // Add type-specific properties
    if (nodeTypeName === "Animals") {
      node.properties = { animalType: ANIMAL_TYPES[math.random(0, ANIMAL_TYPES.size() - 1)] };
    }

    return node;
  }

  /**
   * Creates a new node
   */
  private createNewNode(config: EnhancedGeneratorConfig, nodeIndex: number, layerNum: number, layer: LayerConfig): Node {
    const nodeTypeIndex = nodeIndex % config.numNodeTypes;
    const nodeTypeName = NODE_TYPE_NAMES[math.min(nodeTypeIndex, NODE_TYPE_NAMES.size() - 1)];
    const color = COLOR_PALETTES.NODE_COLORS[nodeTypeIndex % COLOR_PALETTES.NODE_COLORS.size()];

    const node: Node = {
      uuid: `node_${layerNum}_${nodeIndex}`,
      name: `${nodeTypeName} ${nodeIndex + 1}`,
      type: nodeTypeName as "People" | "Animals",
      position: { x: 0, y: 0, z: 0 }, // Will be set by swim lane positioning
      color: color,
      attachmentNames: DEFAULT_ATTACHMENTS
    };

    // Add level property for swim lane algorithm
    const nodeWithLevel = node as Node & { level: number };
    nodeWithLevel.level = layerNum;

    // Add type-specific properties
    if (nodeTypeName === "Animals") {
      node.properties = { animalType: ANIMAL_TYPES[math.random(0, ANIMAL_TYPES.size() - 1)] };
    }

    return node;
  }

  /**
   * Creates a single hexagon for a node
   */
  private createSingleHexagon(node: Node, nodesFolder: Folder, config?: EnhancedGeneratorConfig): Model {
    // Use spacing from config if provided, otherwise use defaults
    const spacing = config?.spacing || {
      nodeHeight: RENDERER_CONSTANTS.HEXAGON.HEIGHT,
      nodeRadius: RENDERER_CONSTANTS.HEXAGON.WIDTH / 2,
      layerSpacing: RENDERER_CONSTANTS.POSITIONING.LEVEL_SPACING,
      nodeSpacing: RENDERER_CONSTANTS.POSITIONING.COLUMN_SPACING,
      swimlaneSpacing: RENDERER_CONSTANTS.POSITIONING.COLUMN_SPACING
    };

    const WIDTH = spacing.nodeRadius * 2;
    const HEIGHT = spacing.nodeHeight;

    const labels: string[] = [
      node.name,
      node.type,
      ""  // typeNumber would be added later
    ];

    if (node.properties?.animalType) {
      labels.push(node.properties.animalType);
    }

    const hexagon = makeHexagon({
      id: this.nodeIdCounter++,
      position: new Vector3(node.position.x, node.position.y, node.position.z),
      width: WIDTH,
      height: HEIGHT,
      barColor: new Color3(node.color[0], node.color[1], node.color[2]),
      barMaterial: Enum.Material.SmoothPlastic,
      castShadow: false,
      labels: labels,
      stackIndex: 1,
      hexIndex: this.nodeIdCounter,
      guid: node.uuid
    });

    // Set name to match pattern for layer tracking
    const layerNum = tonumber(node.uuid.match("node_(\\d+)_")?.[1]) || 1;
    const nodeIndex = tonumber(node.uuid.match("node_\\d+_(\\d+)")?.[1]) || 1;
    hexagon.Name = `Hexagon_L${layerNum}_N${nodeIndex}`;
    hexagon.Parent = nodesFolder;

    return hexagon;
  }
}

/**
 * State Manager for Configuration GUI
 * 
 * Manages the centralized state for the configuration GUI,
 * providing a clean interface for state updates and subscriptions.
 */

import { GUIState, EnhancedGeneratorConfig } from "./interfaces";
import { GUI_CONSTANTS } from "./constants";
import type { SpacingConfig, VisualizationOptions, AxisMapping, VisualMapping, YAxisConfig } from "../../../shared/interfaces/enhancedGenerator.interface";
import { getDefaultXAxis, getDefaultZAxis, isUsingLegacyDefaults } from "../../../shared/constants/axisDefaults";
import { updateAvailableProperties } from "./components/axisMappingControls/constants";

export class GUIStateManager {
  private state: GUIState;
  private listeners: ((state: GUIState) => void)[] = [];

  constructor(initialConfig?: EnhancedGeneratorConfig) {
    // Initialize spacing with defaults
    const defaultSpacing: SpacingConfig = {
      nodeHeight: GUI_CONSTANTS.SPACING_DEFAULTS.NODE_HEIGHT,
      nodeRadius: GUI_CONSTANTS.SPACING_DEFAULTS.NODE_RADIUS,
      layerSpacing: GUI_CONSTANTS.SPACING_DEFAULTS.LAYER_SPACING,
      nodeSpacing: GUI_CONSTANTS.SPACING_DEFAULTS.NODE_SPACING,
      swimlaneSpacing: GUI_CONSTANTS.SPACING_DEFAULTS.SWIMLANE_SPACING,
      linkDiameter: GUI_CONSTANTS.SPACING_DEFAULTS.LINK_DIAMETER
    };

    // Initialize visualization options with defaults
    const defaultVisualization: VisualizationOptions = {
      showNodes: true,
      showLinkLabels: false,
      showConnectors: true,
      allowSameLevelLinks: false
    };

    // Initialize spatial grouping with defaults (using legacy interface for compatibility)
    const defaultAxisMapping: AxisMapping = {
      xAxis: getDefaultXAxis(),
      zAxis: getDefaultZAxis(),
      yAxis: "none", // Default to none, will be updated with discovered properties
      xGroupingProperty: getDefaultXAxis(),
      zGroupingProperty: getDefaultZAxis()
    };

    // Initialize visual mapping with defaults
    const defaultVisualMapping: VisualMapping = {
      backgroundColor: "none",  // Will be updated to first discovered property
      borderColor: "none"
    };

    // Initialize Y-axis config with defaults
    const defaultYAxisConfig: YAxisConfig = {
      useLayer: true,
      property: undefined
    };

    this.state = {
      isVisible: false,
      enhancedConfig: initialConfig || {
        numNodeTypes: 4,
        numLinkTypes: 3,
        layers: [],
        spacing: defaultSpacing,
        visualization: defaultVisualization,
        axisMapping: defaultAxisMapping,
        visualMapping: defaultVisualMapping,
        yAxisConfig: defaultYAxisConfig,
        numPetTypes: 5
      },
      layerRows: []
    };

    // Ensure spacing is always defined
    if (!this.state.enhancedConfig.spacing) {
      this.state.enhancedConfig.spacing = defaultSpacing;
    }

    // Ensure visualization is always defined
    if (!this.state.enhancedConfig.visualization) {
      this.state.enhancedConfig.visualization = defaultVisualization;
    }

    // Ensure axis mapping is always defined
    if (!this.state.enhancedConfig.axisMapping) {
      this.state.enhancedConfig.axisMapping = defaultAxisMapping;
    }

    // Ensure visual mapping is always defined
    if (!this.state.enhancedConfig.visualMapping) {
      this.state.enhancedConfig.visualMapping = defaultVisualMapping;
    }

    // Ensure Y-axis config is always defined
    if (!this.state.enhancedConfig.yAxisConfig) {
      this.state.enhancedConfig.yAxisConfig = defaultYAxisConfig;
    }

    // Ensure numPetTypes is always defined
    if (!this.state.enhancedConfig.numPetTypes) {
      this.state.enhancedConfig.numPetTypes = 5;
    }
  }

  /**
   * Gets the current state
   */
  public getState(): GUIState {
    return { ...this.state };
  }

  /**
   * Gets the enhanced configuration
   */
  public getEnhancedConfig(): EnhancedGeneratorConfig {
    return { ...this.state.enhancedConfig };
  }

  /**
   * Updates the GUI visibility
   */
  public setVisible(visible: boolean): void {
    this.state.isVisible = visible;
    this.notifyListeners();
  }

  /**
   * Updates the GUI instance
   */
  public setGUI(gui: ScreenGui | undefined): void {
    this.state.gui = gui;
    this.notifyListeners();
  }

  /**
   * Updates the config frame
   */
  public setConfigFrame(frame: Frame | undefined): void {
    this.state.configFrame = frame;
    this.notifyListeners();
  }

  /**
   * Updates the status label
   */
  public setStatusLabel(label: TextLabel | undefined): void {
    this.state.statusLabel = label;
    this.notifyListeners();
  }


  /**
   * Updates the layers configuration
   */
  public setLayers(layers: EnhancedGeneratorConfig["layers"]): void {
    this.state.enhancedConfig.layers = layers;
    this.notifyListeners();
  }

  /**
   * Updates a spacing field
   */
  public updateSpacing<K extends keyof SpacingConfig>(field: K, value: SpacingConfig[K]): void {
    if (this.state.enhancedConfig.spacing) {
      this.state.enhancedConfig.spacing[field] = value;
      this.notifyListeners();
    }
  }

  /**
   * Updates a visualization field
   */
  public updateVisualization<K extends keyof VisualizationOptions>(field: K, value: VisualizationOptions[K]): void {
    if (this.state.enhancedConfig.visualization) {
      this.state.enhancedConfig.visualization[field] = value;
      this.notifyListeners();
    }
  }

  /**
   * Resets the configuration to defaults
   */
  public resetConfig(): void {
    const defaultSpacing: SpacingConfig = {
      nodeHeight: GUI_CONSTANTS.SPACING_DEFAULTS.NODE_HEIGHT,
      nodeRadius: GUI_CONSTANTS.SPACING_DEFAULTS.NODE_RADIUS,
      layerSpacing: GUI_CONSTANTS.SPACING_DEFAULTS.LAYER_SPACING,
      nodeSpacing: GUI_CONSTANTS.SPACING_DEFAULTS.NODE_SPACING,
      swimlaneSpacing: GUI_CONSTANTS.SPACING_DEFAULTS.SWIMLANE_SPACING,
      linkDiameter: GUI_CONSTANTS.SPACING_DEFAULTS.LINK_DIAMETER
    };

    const defaultVisualization: VisualizationOptions = {
      showNodes: true,
      showLinkLabels: false,
      showConnectors: true,
      allowSameLevelLinks: false
    };

    this.state.enhancedConfig = {
      numNodeTypes: 4,
      numLinkTypes: 3,
      layers: [],
      spacing: defaultSpacing,
      visualization: defaultVisualization
    };

    this.notifyListeners();
  }

  /**
   * Updates the entire enhanced configuration
   */
  public updateEnhancedConfig(config: EnhancedGeneratorConfig): void {
    // Preserve important properties if not provided in the new config
    const axisMapping = config.axisMapping || this.state.enhancedConfig.axisMapping;
    const visualMapping = config.visualMapping || this.state.enhancedConfig.visualMapping;
    const yAxisConfig = config.yAxisConfig || this.state.enhancedConfig.yAxisConfig;
    const spacing = config.spacing || this.state.enhancedConfig.spacing;
    const visualization = config.visualization || this.state.enhancedConfig.visualization;

    this.state.enhancedConfig = { 
      ...config, 
      axisMapping,
      visualMapping,
      yAxisConfig,
      spacing,
      visualization
    };
    this.notifyListeners();
  }

  /**
   * Updates the discovered properties from the cluster data
   */
  public updateDiscoveredProperties(properties: string[]): void {
    this.state.discoveredProperties = properties;

    // Update the available properties in the dropdown controls
    updateAvailableProperties(properties);

    // If we have at least 2 properties and no axis mapping is set, use the first two
    if (properties.size() >= 2 && this.state.enhancedConfig.axisMapping) {
      const currentMapping = this.state.enhancedConfig.axisMapping;

      // Always set Y-axis if we have 3+ properties and it's not already set
      const shouldUpdateYAxis = properties.size() >= 3 && 
                               (!currentMapping.yAxis || currentMapping.yAxis === "none");

      // Only update X,Z if current values are the hardcoded defaults
      if (isUsingLegacyDefaults(currentMapping.xAxis, currentMapping.zAxis)) {
        this.state.enhancedConfig.axisMapping = {
          xAxis: getDefaultXAxis(properties),
          zAxis: getDefaultZAxis(properties),
          yAxis: properties.size() >= 3 ? properties[2] : "none", // Set to 3rd property if available
          xGroupingProperty: getDefaultXAxis(properties),
          zGroupingProperty: getDefaultZAxis(properties)
        };
      } else if (shouldUpdateYAxis) {
        // Update only Y-axis if X,Z are already properly set
        this.state.enhancedConfig.axisMapping = {
          ...currentMapping,
          yAxis: properties[2] // Set to 3rd property
        };

        print(`[GUIStateManager] Updated Y-axis to discovered property: ${properties[2]}`);
      }

      if (isUsingLegacyDefaults(currentMapping.xAxis, currentMapping.zAxis) || shouldUpdateYAxis) {
        print(`[GUIStateManager] Updated axis mapping to discovered properties:`);
        print(`  X-axis: ${this.state.enhancedConfig.axisMapping.xAxis}`);
        print(`  Z-axis: ${this.state.enhancedConfig.axisMapping.zAxis}`);
        print(`  Y-axis: ${this.state.enhancedConfig.axisMapping.yAxis}`);
      }
    }

    // Update visual mapping to use httpMethod as default background color if available
    if (properties.size() > 0 && this.state.enhancedConfig.visualMapping) {
      const currentVisualMapping = this.state.enhancedConfig.visualMapping;

      // Only update if background is still "None" or "none"
      if (currentVisualMapping.backgroundColor === "None" || currentVisualMapping.backgroundColor === "none") {
        // Prefer "httpMethod" if it exists in discovered properties
        const httpMethodIndex = properties.indexOf("httpMethod");
        if (httpMethodIndex !== -1) {
          this.state.enhancedConfig.visualMapping.backgroundColor = "httpMethod";
          print(`[GUIStateManager] Updated background color to httpMethod`);
        } else {
          this.state.enhancedConfig.visualMapping.backgroundColor = properties[0];
          print(`[GUIStateManager] Updated background color to first property: ${properties[0]}`);
        }
      }
    }


    // Notify listeners about the changes
    this.notifyListeners();
  }

  /**
   * Sets the axis mapping configuration for delayed creation
   */
  public setAxisMappingConfig(config: any): void {
    this.state.axisMappingConfig = config;
  }

  /**
   * Gets the stored axis mapping configuration
   */
  public getAxisMappingConfig(): any {
    return this.state.axisMappingConfig;
  }

  /**
   * Updates spatial grouping (formerly axis mapping)
   */
  public updateAxisMapping(axis: "xAxis" | "zAxis" | "yAxis", value: string): void {
    if (!this.state.enhancedConfig.axisMapping) {
      this.state.enhancedConfig.axisMapping = {
        xAxis: getDefaultXAxis(this.state.discoveredProperties),
        zAxis: getDefaultZAxis(this.state.discoveredProperties),
        yAxis: this.state.discoveredProperties && this.state.discoveredProperties.size() >= 3 ? this.state.discoveredProperties[2] : "none",
        xGroupingProperty: getDefaultXAxis(this.state.discoveredProperties),
        zGroupingProperty: getDefaultZAxis(this.state.discoveredProperties)
      };
    }

    // Update both legacy and new property names for compatibility
    this.state.enhancedConfig.axisMapping[axis] = value;
    if (axis === "xAxis") {
      this.state.enhancedConfig.axisMapping.xGroupingProperty = value;
    } else if (axis === "zAxis") {
      this.state.enhancedConfig.axisMapping.zGroupingProperty = value;
    }

    this.notifyListeners();
  }

  /**
   * Updates visual mapping
   */
  public updateVisualMapping(mapping: "backgroundColor" | "borderColor", value: string): void {
    if (!this.state.enhancedConfig.visualMapping) {
      this.state.enhancedConfig.visualMapping = {
        backgroundColor: "type",
        borderColor: "none"
      };
    }
    this.state.enhancedConfig.visualMapping[mapping] = value;
    this.notifyListeners();
  }

  /**
   * Updates Y-axis configuration
   */
  public updateYAxisConfig(config: { useLayer: boolean; property?: string }): void {
    this.state.enhancedConfig.yAxisConfig = config;
    this.notifyListeners();
  }

  /**
   * Subscribes to state changes
   */
  public subscribe(listener: (state: GUIState) => void): () => void {
    this.listeners.push(listener);

    // Return unsubscribe function
    return () => {
      const index = this.listeners.indexOf(listener);
      if (index >= 0) {
        const newListeners: ((state: GUIState) => void)[] = [];
        for (let i = 0; i < this.listeners.size(); i++) {
          if (i !== index) {
            newListeners.push(this.listeners[i]);
          }
        }
        this.listeners = newListeners;
      }
    };
  }

  /**
   * Notifies all listeners of state changes
   */
  private notifyListeners(): void {
    const currentState = this.getState();
    this.listeners.forEach(listener => listener(currentState));
  }

  /**
   * Cleans up the state manager
   */
  public destroy(): void {
    this.listeners = [];
    if (this.state.gui) {
      this.state.gui.Destroy();
    }
    this.state.gui = undefined;
    this.state.configFrame = undefined;
    this.state.statusLabel = undefined;
    this.state.isVisible = false;
  }
}

import { GUI_CONSTANTS } from "../constants";
import { LayerConfig } from "../interfaces";

interface LayerGridProps {
  parent: Frame;
  onLayerUpdate: (layers: LayerConfig[]) => void;
  nodeTypes: string[];
  linkTypes: string[];
  initialLayers?: LayerConfig[];
}

interface LayerRow {
  frame: Frame;
  config: LayerConfig;
}

export function createLayerGrid({
  parent,
  onLayerUpdate,
  nodeTypes,
  linkTypes,
  initialLayers
}: LayerGridProps): Frame {
  const layers: LayerRow[] = [];

  // Create container for the grid
  const gridContainer = new Instance("Frame");
  gridContainer.Name = "LayerGrid";
  const topOffset = 40 + GUI_CONSTANTS.ENHANCED.GLOBAL_SETTINGS_HEIGHT + 10 + GUI_CONSTANTS.ENHANCED.NODE_LINK_TYPES_HEIGHT + 10;
  // Use remaining space after fixed elements and before buttons (80px from bottom)
  gridContainer.Size = new UDim2(1, -20, 1, -topOffset - 80);
  gridContainer.Position = new UDim2(0, 0, 0, 0); // Position will be set by layout manager
  gridContainer.BackgroundColor3 = new Color3(0.15, 0.15, 0.15);
  gridContainer.BorderSizePixel = 0;
  gridContainer.Parent = parent;

  const gridCorner = new Instance("UICorner");
  gridCorner.CornerRadius = new UDim(0, 4);
  gridCorner.Parent = gridContainer;

  // Create header
  createGridHeader(gridContainer);

  // Create scrolling frame for rows
  const scrollFrame = new Instance("ScrollingFrame");
  scrollFrame.Name = "LayerRows";
  scrollFrame.Size = new UDim2(1, -10, 1, -GUI_CONSTANTS.ENHANCED.GRID_HEADER_HEIGHT - 40);
  scrollFrame.Position = new UDim2(0, 5, 0, GUI_CONSTANTS.ENHANCED.GRID_HEADER_HEIGHT);
  scrollFrame.BackgroundTransparency = 1;
  scrollFrame.BorderSizePixel = 0;
  scrollFrame.ScrollBarThickness = 6;
  scrollFrame.CanvasSize = new UDim2(0, 0, 0, 0);
  scrollFrame.Parent = gridContainer;

  // Create list layout for rows
  const rowLayout = new Instance("UIListLayout");
  rowLayout.SortOrder = Enum.SortOrder.LayoutOrder;
  rowLayout.Padding = new UDim(0, 2);
  rowLayout.Parent = scrollFrame;

  // Function to update canvas size
  const updateCanvasSize = () => {
    const contentHeight = (layers.size() * (GUI_CONSTANTS.ENHANCED.GRID_ROW_HEIGHT + 2)) + 40;
    scrollFrame.CanvasSize = new UDim2(0, 0, 0, contentHeight);
  };

  // Function to notify about layer changes
  const notifyLayerChange = () => {
    const configs = layers.map(row => row.config);
    onLayerUpdate(configs);
  };

  // Function to add a new layer
  const addLayer = () => {
    if (layers.size() >= GUI_CONSTANTS.ENHANCED.MAX_LAYERS) {
      return;
    }

    const layerNumber = layers.size() + 1;

    // Copy values from the previous layer if it exists
    let layerConfig: LayerConfig;
    if (layers.size() > 0) {
      const previousLayer = layers[layers.size() - 1].config;
      layerConfig = {
        layerNumber,
        numNodes: previousLayer.numNodes,
        connectionsPerNode: previousLayer.connectionsPerNode,
        nodeType: previousLayer.nodeType,
        linkType: previousLayer.linkType
      };
    } else {
      // Default values for first layer
      layerConfig = {
        layerNumber,
        numNodes: 3,
        connectionsPerNode: 2,
        nodeType: nodeTypes[0],
        linkType: linkTypes[0]
      };
    }

    const row = createLayerRow({
      parent: scrollFrame,
      config: layerConfig,
      nodeTypes,
      linkTypes,
      onUpdate: (updatedConfig) => {
        const index = layers.findIndex(r => r.config.layerNumber === updatedConfig.layerNumber);
        if (index >= 0) {
          layers[index].config = updatedConfig;
          notifyLayerChange();
        }
      },
      onDelete: () => {
        const index = layers.findIndex(r => r.config.layerNumber === layerConfig.layerNumber);
        if (index >= 0) {
          layers[index].frame.Destroy();
          layers.remove(index);
          // Renumber remaining layers
          layers.forEach((row, idx) => {
            row.config.layerNumber = idx + 1;
            updateLayerNumber(row.frame, idx + 1);
          });
          updateCanvasSize();
          notifyLayerChange();
        }
      }
    });

    layers.push({ frame: row, config: layerConfig });
    updateCanvasSize();
    notifyLayerChange();
  };

  // Create Add Layer button
  const addButton = new Instance("TextButton");
  addButton.Name = "AddLayerButton";
  addButton.Size = new UDim2(0, 120, 0, 30);
  addButton.Position = new UDim2(0, 5, 1, -35);
  addButton.BackgroundColor3 = GUI_CONSTANTS.COLORS.BUTTON.DEFAULT;
  addButton.BorderSizePixel = 0;
  addButton.Font = GUI_CONSTANTS.TYPOGRAPHY.BUTTON_FONT;
  addButton.Text = "[+] Add Layer";
  addButton.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  addButton.TextScaled = true;
  addButton.Parent = gridContainer;

  const addButtonCorner = new Instance("UICorner");
  addButtonCorner.CornerRadius = new UDim(0, 4);
  addButtonCorner.Parent = addButton;

  addButton.MouseButton1Click.Connect(addLayer);

  // Add initial layers from config or default to 3 empty layers
  if (initialLayers && initialLayers.size() > 0) {
    initialLayers.forEach((layerConfig) => {
      const row = createLayerRow({
        parent: scrollFrame,
        config: layerConfig,
        nodeTypes,
        linkTypes,
        onUpdate: (updatedConfig) => {
          const index = layers.findIndex(r => r.config.layerNumber === updatedConfig.layerNumber);
          if (index >= 0) {
            layers[index].config = updatedConfig;
            notifyLayerChange();
          }
        },
        onDelete: () => {
          const index = layers.findIndex(r => r.config.layerNumber === layerConfig.layerNumber);
          if (index >= 0) {
            layers[index].frame.Destroy();
            layers.remove(index);
            // Renumber remaining layers
            layers.forEach((row, idx) => {
              row.config.layerNumber = idx + 1;
              updateLayerNumber(row.frame, idx + 1);
            });
            updateCanvasSize();
            notifyLayerChange();
          }
        }
      });
      layers.push({ frame: row, config: layerConfig });
    });
    updateCanvasSize();
  } else {
    // Default to 3 empty layers
    for (let i = 0; i < 3; i++) {
      addLayer();
    }
  }

  return gridContainer;
}

function createGridHeader(parent: Frame): Frame {
  const header = new Instance("Frame");
  header.Name = "GridHeader";
  header.Size = new UDim2(1, -10, 0, GUI_CONSTANTS.ENHANCED.GRID_HEADER_HEIGHT);
  header.Position = new UDim2(0, 5, 0, 5);
  header.BackgroundColor3 = new Color3(0.1, 0.1, 0.1);
  header.BorderSizePixel = 0;
  header.Parent = parent;

  const headerCorner = new Instance("UICorner");
  headerCorner.CornerRadius = new UDim(0, 4);
  headerCorner.Parent = header;

  // Create column headers
  const columns = [
    { text: "Layer", width: GUI_CONSTANTS.ENHANCED.COLUMN_WIDTHS.LAYER },
    { text: "# Nodes", width: GUI_CONSTANTS.ENHANCED.COLUMN_WIDTHS.NODES },
    { text: "Connections/Node", width: GUI_CONSTANTS.ENHANCED.COLUMN_WIDTHS.CONNECTIONS },
    { text: "Del", width: GUI_CONSTANTS.ENHANCED.COLUMN_WIDTHS.DELETE }
  ];

  let xOffset = 5;
  columns.forEach(col => {
    const label = new Instance("TextLabel");
    label.Size = new UDim2(0, col.width, 1, 0);
    label.Position = new UDim2(0, xOffset, 0, 0);
    label.BackgroundTransparency = 1;
    label.Font = GUI_CONSTANTS.TYPOGRAPHY.LABEL_FONT;
    label.Text = col.text;
    label.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
    label.TextScaled = true;
    label.Parent = header;
    xOffset += col.width + 5;
  });

  return header;
}

interface LayerRowProps {
  parent: ScrollingFrame;
  config: LayerConfig;
  nodeTypes: string[];
  linkTypes: string[];
  onUpdate: (config: LayerConfig) => void;
  onDelete: () => void;
}

function createLayerRow({
  parent,
  config,
  nodeTypes,
  linkTypes,
  onUpdate,
  onDelete
}: LayerRowProps): Frame {
  const row = new Instance("Frame");
  row.Name = `LayerRow_${config.layerNumber}`;
  row.Size = new UDim2(1, 0, 0, GUI_CONSTANTS.ENHANCED.GRID_ROW_HEIGHT);
  row.BackgroundColor3 = new Color3(0.18, 0.18, 0.18);
  row.BorderSizePixel = 0;
  row.Parent = parent;

  const rowCorner = new Instance("UICorner");
  rowCorner.CornerRadius = new UDim(0, 4);
  rowCorner.Parent = row;

  let xOffset = 5;

  // Layer number (read-only)
  const layerLabel = new Instance("TextLabel");
  layerLabel.Name = "LayerNumber";
  layerLabel.Size = new UDim2(0, GUI_CONSTANTS.ENHANCED.COLUMN_WIDTHS.LAYER, 1, -4);
  layerLabel.Position = new UDim2(0, xOffset, 0, 2);
  layerLabel.BackgroundTransparency = 1;
  layerLabel.Font = GUI_CONSTANTS.TYPOGRAPHY.LABEL_FONT;
  layerLabel.Text = tostring(config.layerNumber);
  layerLabel.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  layerLabel.TextScaled = true;
  layerLabel.Parent = row;
  xOffset += GUI_CONSTANTS.ENHANCED.COLUMN_WIDTHS.LAYER + 5;

  // Number of nodes input
  const nodesInput = new Instance("TextBox");
  nodesInput.Name = "NodesInput";
  nodesInput.Size = new UDim2(0, GUI_CONSTANTS.ENHANCED.COLUMN_WIDTHS.NODES, 1, -4);
  nodesInput.Position = new UDim2(0, xOffset, 0, 2);
  nodesInput.BackgroundColor3 = new Color3(0.25, 0.25, 0.25);
  nodesInput.BorderSizePixel = 0;
  nodesInput.Font = GUI_CONSTANTS.TYPOGRAPHY.INPUT_FONT;
  nodesInput.Text = tostring(config.numNodes);
  nodesInput.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  nodesInput.TextScaled = true;
  nodesInput.Parent = row;
  xOffset += GUI_CONSTANTS.ENHANCED.COLUMN_WIDTHS.NODES + 5;

  const nodesCorner = new Instance("UICorner");
  nodesCorner.CornerRadius = new UDim(0, 4);
  nodesCorner.Parent = nodesInput;

  // Connections per node input
  const connectionsInput = new Instance("TextBox");
  connectionsInput.Name = "ConnectionsInput";
  connectionsInput.Size = new UDim2(0, GUI_CONSTANTS.ENHANCED.COLUMN_WIDTHS.CONNECTIONS, 1, -4);
  connectionsInput.Position = new UDim2(0, xOffset, 0, 2);
  connectionsInput.BackgroundColor3 = new Color3(0.25, 0.25, 0.25);
  connectionsInput.BorderSizePixel = 0;
  connectionsInput.Font = GUI_CONSTANTS.TYPOGRAPHY.INPUT_FONT;
  connectionsInput.Text = tostring(config.connectionsPerNode);
  connectionsInput.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  connectionsInput.TextScaled = true;
  connectionsInput.Parent = row;
  xOffset += GUI_CONSTANTS.ENHANCED.COLUMN_WIDTHS.CONNECTIONS + 5;

  const connectionsCorner = new Instance("UICorner");
  connectionsCorner.CornerRadius = new UDim(0, 4);
  connectionsCorner.Parent = connectionsInput;

  // Delete button
  const deleteButton = new Instance("TextButton");
  deleteButton.Name = "DeleteButton";
  deleteButton.Size = new UDim2(0, GUI_CONSTANTS.ENHANCED.COLUMN_WIDTHS.DELETE, 1, -4);
  deleteButton.Position = new UDim2(0, xOffset, 0, 2);
  deleteButton.BackgroundColor3 = GUI_CONSTANTS.COLORS.ERROR;
  deleteButton.BorderSizePixel = 0;
  deleteButton.Font = GUI_CONSTANTS.TYPOGRAPHY.BUTTON_FONT;
  deleteButton.Text = "[X]";
  deleteButton.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  deleteButton.TextScaled = true;
  deleteButton.Parent = row;

  const deleteCorner = new Instance("UICorner");
  deleteCorner.CornerRadius = new UDim(0, 4);
  deleteCorner.Parent = deleteButton;

  deleteButton.MouseButton1Click.Connect(onDelete);

  // Input validation
  nodesInput.FocusLost.Connect(() => {
    const value = tonumber(nodesInput.Text);
    if (value && value > 0) {
      config.numNodes = math.floor(value);
      nodesInput.Text = tostring(config.numNodes);
      onUpdate(config);
    } else {
      nodesInput.Text = tostring(config.numNodes);
    }
  });

  connectionsInput.FocusLost.Connect(() => {
    const value = tonumber(connectionsInput.Text);
    if (value && value >= 0) {
      config.connectionsPerNode = math.floor(value);
      connectionsInput.Text = tostring(config.connectionsPerNode);
      onUpdate(config);
    } else {
      connectionsInput.Text = tostring(config.connectionsPerNode);
    }
  });

  return row;
}

function updateLayerNumber(row: Frame, newNumber: number): void {
  const layerLabel = row.FindFirstChild("LayerNumber") as TextLabel;
  if (layerLabel) {
    layerLabel.Text = tostring(newNumber);
  }
}

/**
 * Configuration GUI Service
 *
 * Implements the Screen GUI specification defined in:
 * 000ProjectSpecification/002ScreenGuiSpec.md
 *
 * This service provides the user interface for configuring the simple data generator
 * with level-based node counts and other parameters.
 */

import { Players } from "@rbxts/services";
import { GUI_CONSTANTS } from "./constants";
import { EnhancedGeneratorConfig, ConfigGUIServiceOptions } from "./interfaces";
import { createCollapsibleFrame } from "./components/collapsibleFrame";
import { createGlobalSettings } from "./components/globalSettings";
import { createLayerGrid } from "./components/layerGrid";
import { createStatusArea } from "./components/status";
import { createVisualizationControls } from "./components/visualizationControls";
import { createAxisMappingControls } from "./components/axisMappingControls/index";
import { refreshAxisDropdownGUI } from "./components/axisMappingControls/utils/screenGuiManager";
import { GUIStateManager } from "./stateManager";
import { GUIEventHandlers } from "./eventHandlers";
import { ComponentFactory } from "./componentFactory";
import { GuiLayoutManager, COMPONENT_HEIGHTS } from "./guiLayout";

export class ConfigGUIService {
  private stateManager: GUIStateManager;
  private eventHandlers: GUIEventHandlers;

  constructor(options: ConfigGUIServiceOptions) {
    // Initialize state manager
    this.stateManager = new GUIStateManager(options.initialConfig);

    // Initialize event handlers
    this.eventHandlers = new GUIEventHandlers({
      stateManager: this.stateManager,
      onEnhancedConfigChange: options.onEnhancedConfigChange,
      onClearRequest: options.onClearRequest,
      onUpdateRequest: options.onUpdateRequest,
    });
  }

  /**
   * Creates and displays the configuration GUI
   */
  public createGUI(): void {
    const player = Players.LocalPlayer;
    const playerGui = player.WaitForChild("PlayerGui") as PlayerGui;

    // Check if GUI already exists
    const existingGui = playerGui.FindFirstChild(
      GUI_CONSTANTS.NAMES.SCREEN_GUI
    );
    if (existingGui) {
      existingGui.Destroy();
    }

    // Create ScreenGui
    const gui = new Instance("ScreenGui");
    gui.Name = GUI_CONSTANTS.NAMES.SCREEN_GUI;
    gui.ResetOnSpawn = false;
    gui.Parent = playerGui;
    this.stateManager.setGUI(gui);

    // Create collapsible main frame - full height
    const mainFrameSize = new UDim2(
      0,
      GUI_CONSTANTS.FRAME.ENHANCED_WIDTH,
      0.9,
      0
    );
    const collapsibleFrame = createCollapsibleFrame({
      parent: gui,
      size: mainFrameSize,
      title: "Graph Configuration",
      position: new UDim2(0, 10, 0, 10),
    });

    // Store the main frame
    this.stateManager.setConfigFrame(collapsibleFrame.frame);

    // Create visualization controls on the right side
    const vizFrameSize = new UDim2(0, 300, 0, 200);
    const vizCollapsibleFrame = createCollapsibleFrame({
      parent: gui,
      size: vizFrameSize,
      title: "Visualization Controls",
      position: new UDim2(0, GUI_CONSTANTS.FRAME.ENHANCED_WIDTH + 20, 0, 10), // Position to the right
    });

    // Create unified UI in the content frames
    this.createUnifiedUI(
      collapsibleFrame.contentFrame,
      vizCollapsibleFrame.contentFrame
    );

    this.stateManager.setVisible(true);
  }

  /**
   * Creates the unified UI
   */
  private createUnifiedUI(contentFrame?: Frame, vizContentFrame?: Frame): void {
    const state = this.stateManager.getState();
    const parentFrame = contentFrame || state.configFrame;
    if (!parentFrame) return;

    const config = this.stateManager.getEnhancedConfig();

    // Create layout manager
    const layoutManager = new GuiLayoutManager(parentFrame);

    // Create scrolling frame for content
    const scrollFrame = layoutManager.createScrollingFrame();

    // Create global settings with spacing controls
    const globalSettings = createGlobalSettings({
      parent: scrollFrame,
      spacing: config.spacing!,
      onSpacingChange: (field, value) => {
        this.eventHandlers.handleSpacingChange(field, value);
      },
    });
    globalSettings.Position = layoutManager.getNextPosition(
      COMPONENT_HEIGHTS.GLOBAL_SETTINGS
    );


    // Generate default type arrays (legacy layer grid support)
    const nodeTypes = this.eventHandlers.generateTypeArray("Type", 5);
    const linkTypes = this.eventHandlers.generateTypeArray("Link", 3);

    // Create layer grid with initial layers
    const layerGrid = createLayerGrid({
      parent: scrollFrame,
      onLayerUpdate: (layers) => {
        this.eventHandlers.handleLayerUpdate(layers);
      },
      nodeTypes,
      linkTypes,
      initialLayers: config.layers,
    });
    layerGrid.Position = layoutManager.getNextPosition(
      COMPONENT_HEIGHTS.LAYER_GRID_HEADER +
        config.layers.size() * COMPONENT_HEIGHTS.LAYER_ROW +
        50
    );

    // Move visualization controls to separate frame
    if (vizContentFrame) {
      const visualizationControls = createVisualizationControls({
        parent: vizContentFrame,
        visualization: config.visualization!,
        onVisualizationChange: (field, value) => {
          this.eventHandlers.handleVisualizationChange(field, value);
        },
      });
      visualizationControls.Position = new UDim2(0, 10, 0, 10);
      visualizationControls.Size = new UDim2(1, -20, 1, -20);
    }

    // Store axis mapping config for later creation
    this.stateManager.setAxisMappingConfig({
      parent: parentFrame, // Not actually used since dropdowns are separate GUIs
      axisMapping: config.axisMapping,
      visualMapping: config.visualMapping,
      useLayerForYAxis: config.yAxisConfig?.useLayer !== false, // Default to true
      yAxisProperty: config.yAxisConfig?.property,
      onAxisMappingChange: (axis: "xAxis" | "zAxis" | "yAxis", value: string) => {
        this.stateManager.updateAxisMapping(axis, value);
        // Trigger re-render with new axis mapping
        this.eventHandlers.handleRegenerateClick();
      },
      onVisualMappingChange: (
        mapping: "backgroundColor" | "borderColor",
        value: string
      ) => {
        this.stateManager.updateVisualMapping(mapping, value);
        // Trigger re-render with new visual mapping
        this.eventHandlers.handleRegenerateClick();
      },
      onYAxisModeChange: (useLayer: boolean) => {
        this.stateManager.updateYAxisConfig({ useLayer });
        // Trigger re-render with new Y-axis mode
        this.eventHandlers.handleRegenerateClick();
      },
      onYAxisPropertyChange: (property: string) => {
        this.stateManager.updateYAxisConfig({ useLayer: false, property });
        // Trigger re-render with new Y-axis property
        this.eventHandlers.handleRegenerateClick();
      },
    });

    // Update scrolling frame canvas size
    layoutManager.updateCanvasSize(scrollFrame);

    // Create action buttons (outside scrolling frame)
    this.createActionButtons(parentFrame);

    // Create status area (outside scrolling frame)
    const statusLabel = createStatusArea({
      parent: parentFrame,
    });
    this.stateManager.setStatusLabel(statusLabel);
  }

  /**
   * Creates action buttons for enhanced mode
   */
  private createActionButtons(parentFrame?: Frame): void {
    const state = this.stateManager.getState();
    const parent = parentFrame || state.configFrame;
    if (!parent) return;

    // Position buttons 70 pixels from bottom using scale
    const buttonYScale = 1;
    const buttonYOffset = -70;

    // Regenerate button
    ComponentFactory.createButton({
      name: "RegenerateButton",
      text: "Regenerate",
      position: new UDim2(0, 10, buttonYScale, buttonYOffset),
      parent: parent,
      onClick: () => this.eventHandlers.handleRegenerateClick(),
    });

    // Update button
    ComponentFactory.createButton({
      name: "UpdateButton",
      text: "Update",
      position: new UDim2(
        0,
        10 + GUI_CONSTANTS.BUTTON.WIDTH + GUI_CONSTANTS.BUTTON.SPACING,
        buttonYScale,
        buttonYOffset
      ),
      parent: parent,
      onClick: () => this.eventHandlers.handleUpdateClick(),
    });

    // Clear button
    ComponentFactory.createButton({
      name: "ClearButton",
      text: "Clear",
      position: new UDim2(
        0,
        10 + (GUI_CONSTANTS.BUTTON.WIDTH + GUI_CONSTANTS.BUTTON.SPACING) * 2,
        buttonYScale,
        buttonYOffset
      ),
      parent: parent,
      onClick: () =>
        this.eventHandlers.handleClearClick(() => this.createGUI()),
    });

    // Export Config button
    ComponentFactory.createButton({
      name: "ExportConfigButton",
      text: "Export",
      position: new UDim2(
        0,
        10 + (GUI_CONSTANTS.BUTTON.WIDTH + GUI_CONSTANTS.BUTTON.SPACING) * 3,
        buttonYScale,
        buttonYOffset
      ),
      parent: parent,
      onClick: () => this.eventHandlers.handleExportConfigClick(),
    });
  }

  /**
   * Updates the enhanced configuration
   */
  public updateEnhancedConfig(config: EnhancedGeneratorConfig): void {
    this.stateManager.updateEnhancedConfig(config);

    // If GUI is visible, update the display
    const state = this.stateManager.getState();
    if (state.configFrame) {
      // Recreate the GUI to reflect new configuration
      state.configFrame.Destroy();
      this.createGUI();
    }
  }

  /**
   * Updates the discovered properties from the data
   */
  public updateDiscoveredProperties(properties: string[]): void {
    properties.forEach((prop, index) => {});

    // Update the state manager with discovered properties
    this.stateManager.updateDiscoveredProperties(properties);

    // First refresh the axis dropdown GUI to clear old dropdowns
    refreshAxisDropdownGUI();

    // Create axis mapping controls now that we have properties
    const axisMappingConfig = this.stateManager.getAxisMappingConfig();
    if (axisMappingConfig) {
      // Update the config with the new axis mapping values from state
      const currentState = this.stateManager.getState();
      if (currentState.enhancedConfig.axisMapping) {
        axisMappingConfig.axisMapping = currentState.enhancedConfig.axisMapping;
      }
      createAxisMappingControls(axisMappingConfig);
    }
  }

  /**
   * Gets the current visibility state
   */
  public isVisible(): boolean {
    return this.stateManager.getState().isVisible;
  }

  /**
   * Shows an error message to the user
   */
  public showError(message: string): void {
    const state = this.stateManager.getState();
    const screenGui = state.configFrame?.Parent?.Parent as ScreenGui;
    if (!screenGui) return;

    // Create error notification
    const errorFrame = new Instance("Frame");
    errorFrame.Name = "ErrorNotification";
    errorFrame.Size = new UDim2(0, 400, 0, 100);
    errorFrame.Position = new UDim2(0.5, -200, 0, 50);
    errorFrame.BackgroundColor3 = new Color3(0.8, 0.2, 0.2);
    errorFrame.BorderSizePixel = 2;
    errorFrame.BorderColor3 = new Color3(0.6, 0, 0);
    errorFrame.Parent = screenGui;

    const errorText = new Instance("TextLabel");
    errorText.Text = " " + message;
    errorText.Size = new UDim2(1, -20, 1, -20);
    errorText.Position = new UDim2(0, 10, 0, 10);
    errorText.BackgroundTransparency = 1;
    errorText.TextColor3 = new Color3(1, 1, 1);
    errorText.TextScaled = true;
    errorText.TextWrapped = true;
    errorText.Font = Enum.Font.SourceSansBold;
    errorText.Parent = errorFrame;

    // Auto-dismiss after 5 seconds
    wait(5);
    errorFrame.Destroy();
  }

  /**
   * Destroys the GUI
   */
  public destroy(): void {
    this.stateManager.destroy();
  }
}

