import { Players } from "@rbxts/services";
import { Node } from "../../../shared/interfaces/simpleDataGenerator.interface";

export class NodePropertiesInspectorService {
  private static instance: NodePropertiesInspectorService;
  private gui?: ScreenGui;
  private panel?: Frame;
  private currentNode?: Node;
  private clickConnection?: RBXScriptConnection;

  private constructor() {}

  public static getInstance(): NodePropertiesInspectorService {
    if (!NodePropertiesInspectorService.instance) {
      NodePropertiesInspectorService.instance = new NodePropertiesInspectorService();
    }
    return NodePropertiesInspectorService.instance;
  }

  public initialize(): void {
    const player = Players.LocalPlayer;
    const playerGui = player.WaitForChild("PlayerGui") as PlayerGui;

    // Create the GUI
    this.gui = new Instance("ScreenGui");
    this.gui.Name = "NodePropertiesInspector";
    this.gui.ResetOnSpawn = false;
    this.gui.Parent = playerGui;

    // Create the panel
    this.createPanel();

    // Set up click detection
    this.setupClickDetection();
  }

  private createPanel(): void {
    if (!this.gui) return;

    // Create main panel frame
    this.panel = new Instance("Frame");
    this.panel.Name = "PropertiesPanel";
    this.panel.Size = new UDim2(0, 300, 0, 400);
    this.panel.Position = new UDim2(1, -310, 0, 10); // Upper right corner
    this.panel.BackgroundColor3 = new Color3(0.15, 0.15, 0.15);
    this.panel.BorderSizePixel = 0;
    this.panel.Visible = false;
    this.panel.Parent = this.gui;

    // Add corner radius
    const corner = new Instance("UICorner");
    corner.CornerRadius = new UDim(0, 8);
    corner.Parent = this.panel;

    // Add title bar
    const titleBar = new Instance("Frame");
    titleBar.Name = "TitleBar";
    titleBar.Size = new UDim2(1, 0, 0, 40);
    titleBar.Position = new UDim2(0, 0, 0, 0);
    titleBar.BackgroundColor3 = new Color3(0.1, 0.1, 0.1);
    titleBar.BorderSizePixel = 0;
    titleBar.Parent = this.panel;

    const titleCorner = new Instance("UICorner");
    titleCorner.CornerRadius = new UDim(0, 8);
    titleCorner.Parent = titleBar;

    // Fix bottom corners of title bar
    const titleFix = new Instance("Frame");
    titleFix.Size = new UDim2(1, 0, 0, 8);
    titleFix.Position = new UDim2(0, 0, 1, -8);
    titleFix.BackgroundColor3 = new Color3(0.1, 0.1, 0.1);
    titleFix.BorderSizePixel = 0;
    titleFix.Parent = titleBar;

    // Add title text
    const titleText = new Instance("TextLabel");
    titleText.Name = "Title";
    titleText.Size = new UDim2(1, -50, 1, 0);
    titleText.Position = new UDim2(0, 10, 0, 0);
    titleText.BackgroundTransparency = 1;
    titleText.Text = "Node Properties";
    titleText.TextColor3 = new Color3(1, 1, 1);
    titleText.TextScaled = true;
    titleText.Font = Enum.Font.SourceSansBold;
    titleText.TextXAlignment = Enum.TextXAlignment.Left;
    titleText.Parent = titleBar;

    // Add close button
    const closeButton = new Instance("TextButton");
    closeButton.Name = "CloseButton";
    closeButton.Size = new UDim2(0, 30, 0, 30);
    closeButton.Position = new UDim2(1, -35, 0.5, -15);
    closeButton.BackgroundColor3 = new Color3(0.8, 0.2, 0.2);
    closeButton.Text = "X";
    closeButton.TextColor3 = new Color3(1, 1, 1);
    closeButton.TextScaled = true;
    closeButton.Font = Enum.Font.SourceSansBold;
    closeButton.Parent = titleBar;

    const closeCorner = new Instance("UICorner");
    closeCorner.CornerRadius = new UDim(0, 4);
    closeCorner.Parent = closeButton;

    closeButton.MouseButton1Click.Connect(() => {
      this.hide();
    });

    // Create scrolling frame for properties
    const scrollFrame = new Instance("ScrollingFrame");
    scrollFrame.Name = "PropertiesScroll";
    scrollFrame.Size = new UDim2(1, -20, 1, -60);
    scrollFrame.Position = new UDim2(0, 10, 0, 50);
    scrollFrame.BackgroundTransparency = 1;
    scrollFrame.BorderSizePixel = 0;
    scrollFrame.ScrollBarThickness = 6;
    scrollFrame.CanvasSize = new UDim2(0, 0, 0, 0);
    scrollFrame.Parent = this.panel;

    // Create list layout
    const listLayout = new Instance("UIListLayout");
    listLayout.SortOrder = Enum.SortOrder.LayoutOrder;
    listLayout.Padding = new UDim(0, 5);
    listLayout.Parent = scrollFrame;

    // Update canvas size when layout changes
    listLayout.GetPropertyChangedSignal("AbsoluteContentSize").Connect(() => {
      scrollFrame.CanvasSize = new UDim2(0, 0, 0, listLayout.AbsoluteContentSize.Y);
    });
  }

  private setupClickDetection(): void {
    const player = Players.LocalPlayer;
    const mouse = player.GetMouse();

    mouse.Button1Down.Connect(() => {
      const target = mouse.Target;
      if (!target) return;

      // Check if the target is part of a hexagon
      const model = target.FindFirstAncestorOfClass("Model");
      if (!model) return;

      // Check if it's a hexagon model (has guid attribute)
      const guid = model.GetAttribute("guid") as string | undefined;
      if (!guid) return;

      // Find the node data
      const node = this.findNodeByGuid(guid);
      if (node) {
        this.showNodeProperties(node);
      }
    });
  }

  private findNodeByGuid(guid: string): Node | undefined {
    // This will need to be connected to the actual node data
    // For now, we'll store nodes in the model as attributes
    const workspace = game.GetService("Workspace");
    const graphMaker = workspace.FindFirstChild("GraphMaker", true);
    if (!graphMaker) return;

    const nodesFolder = graphMaker.FindFirstChild("Nodes", true);
    if (!nodesFolder) return;

    // Look for the model with matching guid
    for (const child of nodesFolder.GetChildren()) {
      if (child.IsA("Model") && child.GetAttribute("guid") === guid) {
        // Reconstruct node data from attributes
        const node: Node = {
          uuid: guid,
          name: child.GetAttribute("nodeName") as string || "",
          type: child.GetAttribute("nodeType") as string || "",
          color: [0, 0, 0], // We don't need color for display
          position: { x: 0, y: 0, z: 0 }, // We don't need position for display
          attachmentNames: [],
          properties: {}
        };

        // Get all properties dynamically
        // First, get standard properties that might be on Person nodes
        const standardProperties = [
          "age", "petType", "petColor", "firstName", "lastName", 
          "countryOfBirth", "countryOfResidence"
        ];

        // Also check for Harness properties
        const harnessProperties = [
          "service", "component", "language", "size", "type",
          "resourceDomain", "operationType", "apiPattern", 
          "apiComplexity", "httpMethod", "path"
        ];

        // Check all known properties
        const allProperties = [...standardProperties, ...harnessProperties];

        allProperties.forEach(propName => {
          const value = child.GetAttribute(propName);
          if (value !== undefined) {
            const props = node.properties as Record<string, unknown>;
            props[propName] = value;
          }
        });

        // Also get any other attributes that might be present
        const attributes = child.GetAttributes();
        for (const [key, value] of pairs(attributes)) {
          // Skip system attributes
          if (key !== "nodeName" && key !== "nodeType" && key !== "nodeGuid" && 
              !allProperties.includes(key as string)) {
            const props = node.properties as Record<string, unknown>;
            props[key as string] = value;
          }
        }

        return node;
      }
    }

    return undefined;
  }

  private showNodeProperties(node: Node): void {
    this.currentNode = node;
    this.updatePropertiesDisplay();
    this.show();
  }

  private updatePropertiesDisplay(): void {
    if (!this.panel || !this.currentNode) return;

    const scrollFrame = this.panel.FindFirstChild("PropertiesScroll") as ScrollingFrame;
    if (!scrollFrame) return;

    // Clear existing properties
    for (const child of scrollFrame.GetChildren()) {
      if (child.IsA("Frame")) {
        child.Destroy();
      }
    }

    // Add node basic properties
    this.addPropertyRow(scrollFrame, "UUID", this.currentNode.uuid, 1);
    this.addPropertyRow(scrollFrame, "Name", this.currentNode.name, 2);
    this.addPropertyRow(scrollFrame, "Type", this.currentNode.type, 3);

    // Add dynamic properties
    let order = 4;
    if (this.currentNode.properties) {
      for (const [key, value] of pairs(this.currentNode.properties)) {
        this.addPropertyRow(scrollFrame, key as string, tostring(value), order);
        order++;
      }
    }
  }

  private addPropertyRow(parent: ScrollingFrame, key: string, value: string, order: number): void {
    const row = new Instance("Frame");
    row.Name = `Property_${key}`;
    row.Size = new UDim2(1, 0, 0, 30);
    row.BackgroundColor3 = new Color3(0.2, 0.2, 0.2);
    row.BorderSizePixel = 0;
    row.LayoutOrder = order;
    row.Parent = parent;

    const rowCorner = new Instance("UICorner");
    rowCorner.CornerRadius = new UDim(0, 4);
    rowCorner.Parent = row;

    // Key label
    const keyLabel = new Instance("TextLabel");
    keyLabel.Size = new UDim2(0.4, -5, 1, 0);
    keyLabel.Position = new UDim2(0, 5, 0, 0);
    keyLabel.BackgroundTransparency = 1;
    keyLabel.Text = key + ":";
    keyLabel.TextColor3 = new Color3(0.7, 0.7, 0.7);
    keyLabel.TextScaled = true;
    keyLabel.Font = Enum.Font.SourceSans;
    keyLabel.TextXAlignment = Enum.TextXAlignment.Left;
    keyLabel.Parent = row;

    // Value label
    const valueLabel = new Instance("TextLabel");
    valueLabel.Size = new UDim2(0.6, -5, 1, 0);
    valueLabel.Position = new UDim2(0.4, 0, 0, 0);
    valueLabel.BackgroundTransparency = 1;
    valueLabel.Text = value;
    valueLabel.TextColor3 = new Color3(1, 1, 1);
    valueLabel.TextScaled = true;
    valueLabel.Font = Enum.Font.SourceSans;
    valueLabel.TextXAlignment = Enum.TextXAlignment.Left;
    valueLabel.Parent = row;
  }

  private show(): void {
    if (this.panel) {
      this.panel.Visible = true;
    }
  }

  private hide(): void {
    if (this.panel) {
      this.panel.Visible = false;
    }
  }

  public cleanup(): void {
    if (this.clickConnection) {
      this.clickConnection.Disconnect();
    }
    if (this.gui) {
      this.gui.Destroy();
    }
  }
}

/**
 * Configuration validation utilities for deep object validation
 */

import { validateArray, ARRAY_LIMITS, hasCircularReference } from "./arrayValidation";
import { EnhancedGeneratorConfig, LayerConfig } from "../../interfaces/enhancedGenerator.interface";

interface ConfigValidationResult {
  isValid: boolean;
  errors: string[];
  sanitizedConfig?: EnhancedGeneratorConfig;
}

/**
 * Deep validation for EnhancedGeneratorConfig
 */
export function validateEnhancedGeneratorConfig(
  config: unknown
): ConfigValidationResult {
  const errors: string[] = [];

  // Check if config is an object
  if (!typeIs(config, "table") || config === undefined) {
    return {
      isValid: false,
      errors: ["Configuration must be an object"],
    };
  }

  // Check for circular references
  if (hasCircularReference([config])) {
    return {
      isValid: false,
      errors: ["Configuration contains circular references"],
    };
  }

  const cfg = config as EnhancedGeneratorConfig;
  const sanitized: EnhancedGeneratorConfig = {} as EnhancedGeneratorConfig;

  // Validate numeric fields
  if (!typeIs(cfg.numNodeTypes, "number") || cfg.numNodeTypes < 1 || cfg.numNodeTypes > 50) {
    errors.push("numNodeTypes must be a number between 1 and 50");
  } else {
    sanitized.numNodeTypes = cfg.numNodeTypes;
  }

  if (!typeIs(cfg.numLinkTypes, "number") || cfg.numLinkTypes < 1 || cfg.numLinkTypes > 50) {
    errors.push("numLinkTypes must be a number between 1 and 50");
  } else {
    sanitized.numLinkTypes = cfg.numLinkTypes;
  }

  // Validate layers array
  const layersResult = validateArray<LayerConfig>(cfg.layers, {
    maxSize: ARRAY_LIMITS.MAX_LAYERS,
    minSize: 1,
    allowEmpty: false,
  });

  if (!layersResult.isValid) {
    errors.push(`Layers: ${layersResult.error}`);
  } else {
    // Validate each layer
    const validatedLayers: LayerConfig[] = [];

    for (let i = 0; i < cfg.layers.size(); i++) {
      const layerErrors = validateLayerConfig(cfg.layers[i], i + 1);
      if (layerErrors.size() > 0) {
        for (const err of layerErrors) {
          errors.push(err);
        }
      } else {
        validatedLayers.push(cfg.layers[i]);
      }
    }

    sanitized.layers = validatedLayers;
  }

  // Validate optional spacing config
  if (cfg.spacing) {
    const spacingErrors = validateSpacingConfig(cfg.spacing);
    if (spacingErrors.size() > 0) {
      for (const err of spacingErrors) {
        errors.push(err);
      }
    } else {
      sanitized.spacing = cfg.spacing;
    }
  }

  // Validate optional visualization config
  if (cfg.visualization) {
    const vizErrors = validateVisualizationConfig(cfg.visualization);
    if (vizErrors.size() > 0) {
      for (const err of vizErrors) {
        errors.push(err);
      }
    } else {
      sanitized.visualization = cfg.visualization;
    }
  }

  // Validate optional axis mapping / spatial grouping config
  if (cfg.axisMapping || cfg.spatialGrouping) {
    const mappingConfig = cfg.spatialGrouping || cfg.axisMapping;
    const mappingErrors = validateAxisMappingConfig(mappingConfig);
    if (mappingErrors.size() > 0) {
      for (const err of mappingErrors) {
        errors.push(err);
      }
    } else {
      // Support both old and new property names
      if (cfg.spatialGrouping) {
        sanitized.spatialGrouping = cfg.spatialGrouping;
      }
      if (cfg.axisMapping) {
        sanitized.axisMapping = cfg.axisMapping;
      }
    }
  }

  // Validate optional visual mapping config
  if (cfg.visualMapping) {
    const visualMappingErrors = validateVisualMappingConfig(cfg.visualMapping);
    if (visualMappingErrors.size() > 0) {
      for (const err of visualMappingErrors) {
        errors.push(err);
      }
    } else {
      sanitized.visualMapping = cfg.visualMapping;
    }
  }

  // Validate optional Y-axis config
  if (cfg.yAxisConfig) {
    const yAxisErrors = validateYAxisConfig(cfg.yAxisConfig);
    if (yAxisErrors.size() > 0) {
      for (const err of yAxisErrors) {
        errors.push(err);
      }
    } else {
      sanitized.yAxisConfig = cfg.yAxisConfig;
    }
  }

  // Validate optional numPetTypes
  if (cfg.numPetTypes !== undefined) {
    if (!typeIs(cfg.numPetTypes, "number") || cfg.numPetTypes < 1 || cfg.numPetTypes > 50) {
      errors.push("numPetTypes must be a number between 1 and 50");
    } else {
      sanitized.numPetTypes = cfg.numPetTypes;
    }
  }

  // Validate optional maxDataItems
  if (cfg.maxDataItems !== undefined) {
    if (!typeIs(cfg.maxDataItems, "number") || cfg.maxDataItems < 1 || cfg.maxDataItems > 10000) {
      errors.push("maxDataItems must be a number between 1 and 10000");
    } else {
      sanitized.maxDataItems = cfg.maxDataItems;
    }
  }

  return {
    isValid: errors.size() === 0,
    errors,
    sanitizedConfig: errors.size() === 0 ? sanitized : undefined,
  };
}

/**
 * Validates a single layer configuration
 */
function validateLayerConfig(layer: LayerConfig, layerIndex: number): string[] {
  const errors: string[] = [];
  const prefix = `Layer ${layerIndex}:`;

  // Validate layer number
  if (!typeIs(layer.layerNumber, "number") || layer.layerNumber < 1) {
    errors.push(`${prefix} layerNumber must be a positive number`);
  }

  // Validate numNodes
  if (!typeIs(layer.numNodes, "number") || layer.numNodes < 1 || layer.numNodes > ARRAY_LIMITS.MAX_NODES) {
    errors.push(`${prefix} numNodes must be between 1 and ${ARRAY_LIMITS.MAX_NODES}`);
  }

  // Validate connectionsPerNode
  if (!typeIs(layer.connectionsPerNode, "number") || layer.connectionsPerNode < 0 || layer.connectionsPerNode > 100) {
    errors.push(`${prefix} connectionsPerNode must be between 0 and 100`);
  }

  return errors;
}

/**
 * Validates spacing configuration
 */
function validateSpacingConfig(spacing: unknown): string[] {
  const errors: string[] = [];

  if (!typeIs(spacing, "table")) {
    errors.push("Spacing must be an object");
    return errors;
  }

  const spacingObj = spacing as Record<string, unknown>;

  // Validate numeric spacing values
  const numericFields = ["nodeHeight", "nodeRadius", "layerSpacing", "nodeSpacing", "swimlaneSpacing", "linkDiameter"];

  for (const field of numericFields) {
    const value = spacingObj[field];
    if (value !== undefined) {
      if (!typeIs(value, "number") || value <= 0 || value > 1000) {
        errors.push(`Spacing.${field} must be a positive number less than 1000`);
      }
    }
  }

  return errors;
}

/**
 * Validates visualization configuration
 */
function validateVisualizationConfig(viz: unknown): string[] {
  const errors: string[] = [];

  if (!typeIs(viz, "table")) {
    errors.push("Visualization must be an object");
    return errors;
  }

  const vizObj = viz as Record<string, unknown>;

  // Validate boolean fields
  const booleanFields = ["showNodes", "showLinkLabels", "showConnectors", "allowSameLevelLinks"];

  for (const field of booleanFields) {
    const value = vizObj[field];
    if (value !== undefined && !typeIs(value, "boolean")) {
      errors.push(`Visualization.${field} must be a boolean`);
    }
  }

  return errors;
}

/**
 * Validates axis mapping / spatial grouping configuration
 */
function validateAxisMappingConfig(mapping: unknown): string[] {
  const errors: string[] = [];

  if (!typeIs(mapping, "table")) {
    errors.push("Axis mapping / spatial grouping must be an object");
    return errors;
  }

  const mappingObj = mapping as Record<string, unknown>;

  // Check for required fields (support both old and new names)
  const hasOldNames = mappingObj["xAxis"] !== undefined && mappingObj["zAxis"] !== undefined;
  const hasNewNames = mappingObj["xGroupingProperty"] !== undefined && mappingObj["zGroupingProperty"] !== undefined;

  if (!hasOldNames && !hasNewNames) {
    errors.push("Axis mapping must have either xAxis/zAxis or xGroupingProperty/zGroupingProperty");
  }

  // Validate string fields
  const stringFields = ["xAxis", "zAxis", "xGroupingProperty", "zGroupingProperty"];

  for (const field of stringFields) {
    const value = mappingObj[field];
    if (value !== undefined && !typeIs(value, "string")) {
      errors.push(`AxisMapping.${field} must be a string`);
    }
  }

  return errors;
}

/**
 * Validates visual mapping configuration
 */
function validateVisualMappingConfig(visualMapping: unknown): string[] {
  const errors: string[] = [];

  if (!typeIs(visualMapping, "table")) {
    errors.push("Visual mapping must be an object");
    return errors;
  }

  const mappingObj = visualMapping as Record<string, unknown>;

  // Validate string fields
  const stringFields = ["backgroundColor", "borderColor"];

  for (const field of stringFields) {
    const value = mappingObj[field];
    if (value !== undefined && !typeIs(value, "string")) {
      errors.push(`VisualMapping.${field} must be a string`);
    }
  }

  return errors;
}

/**
 * Validates Y-axis configuration
 */
function validateYAxisConfig(yAxisConfig: unknown): string[] {
  const errors: string[] = [];

  if (!typeIs(yAxisConfig, "table")) {
    errors.push("Y-axis config must be an object");
    return errors;
  }

  const configObj = yAxisConfig as Record<string, unknown>;

  // Validate boolean field
  if (configObj["useLayer"] !== undefined && !typeIs(configObj["useLayer"], "boolean")) {
    errors.push("YAxisConfig.useLayer must be a boolean");
  }

  // Validate string field
  if (configObj["property"] !== undefined && !typeIs(configObj["property"], "string")) {
    errors.push("YAxisConfig.property must be a string");
  }

  return errors;
}


/**
 * Sanitizes and validates remote data
 */
export function validateRemoteData(data: unknown): { isValid: boolean; error?: string } {
  // For Roblox, we can't easily check serialized size, so check structure depth
  function checkDepth(obj: unknown, currentDepth: number): boolean {
    if (currentDepth > 20) return false; // Max depth of 20
    if (typeIs(obj, "table")) {
      for (const [_, value] of pairs(obj)) {
        if (!checkDepth(value, currentDepth + 1)) return false;
      }
    }
    return true;
  }

  if (!checkDepth(data, 0)) {
    return { isValid: false, error: "Data structure too deep (max 20 levels)" };
  }

  // Check for suspicious patterns
  if (hasCircularReference([data])) {
    return { isValid: false, error: "Data contains circular references" };
  }

  return { isValid: true };
}

/**
 * Link Generator for Unified Data Renderer
 * 
 * Handles creation and management of relationships between nodes
 */

import { COLOR_PALETTES } from "../../constants";
import {
  EnhancedGeneratorConfig,
  LayerConfig,
} from "../../../../../interfaces/enhancedGenerator.interface";
import { 
  Link, 
  Node 
} from "../../../../../interfaces/simpleDataGenerator.interface";
import { ILinkGenerator } from "./interfaces";

export class LinkGenerator implements ILinkGenerator {
  private linkIdCounter = 0;

  /**
   * Generate all links between and within layers
   */
  public generateAllLinks(
    nodesByLayer: Map<number, Node[]>,
    config: EnhancedGeneratorConfig
  ): Link[] {
    const allLinks: Link[] = [];
    this.linkIdCounter = 0;

    // Generate links for each layer
    const layerNumbers: number[] = [];
    nodesByLayer.forEach((_, layerNumber) => {
      layerNumbers.push(layerNumber);
    });
    table.sort(layerNumbers, (a, b) => a < b);

    layerNumbers.forEach((layerNumber) => {
      const currentLayerNodes = nodesByLayer.get(layerNumber)!;
      const nextLayerNodes = nodesByLayer.get(layerNumber + 1);

      // Create a mock layer config for the link generation
      const layer: LayerConfig = {
        layerNumber,
        numNodes: currentLayerNodes.size(),
        connectionsPerNode: this.getDefaultConnectionsPerNode(config),
      };

      this.generateLayerLinks(
        currentLayerNodes,
        nextLayerNodes,
        layer,
        config,
        allLinks
      );

      // Add backward connections for first layer (connecting to second layer)
      if (layerNumber === 1 && nextLayerNodes) {
        this.ensureBackwardConnections(currentLayerNodes, nextLayerNodes, config, allLinks);
      }
    });

    return allLinks;
  }

  /**
   * Generate links for a layer
   */
  private generateLayerLinks(
    currentLayerNodes: Node[],
    nextLayerNodes: Node[] | undefined,
    layer: LayerConfig,
    config: EnhancedGeneratorConfig,
    allLinks: Link[]
  ): void {
    currentLayerNodes.forEach((sourceNode) => {
      // Create intra-layer connections
      this.generateIntraLayerLinks(
        sourceNode,
        currentLayerNodes,
        layer,
        config,
        allLinks
      );

      // Create inter-layer connections
      if (nextLayerNodes && nextLayerNodes.size() > 0) {
        this.generateInterLayerLink(
          sourceNode,
          nextLayerNodes,
          config,
          allLinks
        );
      } else if (layer.connectionsPerNode > 0 && currentLayerNodes.size() > 1) {
        // For the last layer, ensure at least some connections within the layer
        this.generateIntraLayerLinks(
          sourceNode,
          currentLayerNodes,
          {
            ...layer,
            connectionsPerNode: math.max(1, layer.connectionsPerNode),
          },
          config,
          allLinks
        );
      }
    });
  }

  /**
   * Generate intra-layer connections
   */
  private generateIntraLayerLinks(
    sourceNode: Node,
    currentLayerNodes: Node[],
    layer: LayerConfig,
    config: EnhancedGeneratorConfig,
    allLinks: Link[]
  ): void {
    const allowSameLevelLinks =
      config.visualization?.allowSameLevelLinks ?? true;

    if (
      !allowSameLevelLinks ||
      layer.connectionsPerNode <= 0 ||
      currentLayerNodes.size() <= 1
    ) {
      return;
    }

    const availableTargets = currentLayerNodes.filter(
      (n) => n.uuid !== sourceNode.uuid
    );
    const numConnections = math.min(
      layer.connectionsPerNode,
      availableTargets.size()
    );

    // Randomly select target nodes
    const shuffled = this.shuffleArray([...availableTargets]);

    for (let i = 0; i < numConnections; i++) {
      const targetNode = shuffled[i];
      const link = this.createLink(sourceNode, targetNode, config);
      allLinks.push(link);
    }
  }

  /**
   * Generate inter-layer connection
   */
  private generateInterLayerLink(
    sourceNode: Node,
    nextLayerNodes: Node[],
    config: EnhancedGeneratorConfig,
    allLinks: Link[]
  ): void {
    if (nextLayerNodes.size() === 0) return;

    // Prefer connecting to nodes of the same type
    const sameTypeNodes = nextLayerNodes.filter(
      (n) => n.type === sourceNode.type
    );
    const candidateNodes =
      sameTypeNodes.size() > 0 ? sameTypeNodes : nextLayerNodes;

    // Connect to at least one node, potentially more for better connectivity
    const numConnections = math.min(2, candidateNodes.size()); // Connect to up to 2 nodes
    const shuffled = this.shuffleArray([...candidateNodes]);

    for (let i = 0; i < numConnections; i++) {
      const targetNode = shuffled[i];
      // Check if this link already exists
      const linkExists = allLinks.some(
        (link) =>
          (link.sourceNodeUuid === sourceNode.uuid &&
            link.targetNodeUuid === targetNode.uuid) ||
          (link.sourceNodeUuid === targetNode.uuid &&
            link.targetNodeUuid === sourceNode.uuid)
      );

      if (!linkExists) {
        const link = this.createLink(sourceNode, targetNode, config);
        allLinks.push(link);
      }
    }
  }

  /**
   * Create a link between two nodes
   */
  public createLink(
    sourceNode: Node,
    targetNode: Node,
    config: EnhancedGeneratorConfig
  ): Link {
    const linkTypeIndex = this.linkIdCounter % config.numLinkTypes;
    const linkColor =
      COLOR_PALETTES.LINK_COLORS[
        linkTypeIndex % COLOR_PALETTES.LINK_COLORS.size()
      ];

    const link: Link = {
      uuid: `link-${++this.linkIdCounter}`,
      type: `Link${linkTypeIndex + 1}`,
      sourceNodeUuid: sourceNode.uuid,
      targetNodeUuid: targetNode.uuid,
      color: linkColor,
    };

    return link;
  }

  /**
   * Ensure all nodes have at least one connection
   */
  public ensureConnectivity(
    nodes: Node[],
    links: Link[],
    config: EnhancedGeneratorConfig
  ): void {
    // Create a map to track connections per node
    const nodeConnections = new Map<string, number>();
    nodes.forEach((node) => nodeConnections.set(node.uuid, 0));

    // Count existing connections
    links.forEach((link) => {
      nodeConnections.set(
        link.sourceNodeUuid,
        (nodeConnections.get(link.sourceNodeUuid) || 0) + 1
      );
      nodeConnections.set(
        link.targetNodeUuid,
        (nodeConnections.get(link.targetNodeUuid) || 0) + 1
      );
    });

    // Find isolated nodes
    const isolatedNodes: Node[] = [];
    nodeConnections.forEach((count, uuid) => {
      if (count === 0) {
        const node = nodes.find((n) => n.uuid === uuid);
        if (node) isolatedNodes.push(node);
      }
    });

    if (isolatedNodes.size() > 0) {
      // Create fallback connections
      isolatedNodes.forEach((isolatedNode) => {
        // Find a suitable connection target
        const targetNode = this.findConnectionTarget(isolatedNode, nodes);
        if (targetNode) {
          const fallbackLink: Link = {
            uuid: `link_fallback_${this.linkIdCounter++}`,
            type: "Fallback",
            sourceNodeUuid: isolatedNode.uuid,
            targetNodeUuid: targetNode.uuid,
            color: [0.5, 0.5, 0.5], // Gray color for fallback links
          };
          links.push(fallbackLink);
        }
      });
    }
  }

  /**
   * Fisher-Yates shuffle algorithm
   */
  private shuffleArray<T>(array: T[]): T[] {
    for (let i = array.size() - 1; i > 0; i--) {
      const j = math.random(0, i);
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  }

  /**
   * Find a suitable connection target for an isolated node
   */
  private findConnectionTarget(
    isolatedNode: Node,
    allNodes: Node[]
  ): Node | undefined {
    // Try to connect to a node of the same type first
    const sameTypeNodes = allNodes.filter(
      (n) => n.type === isolatedNode.type && n.uuid !== isolatedNode.uuid
    );
    if (sameTypeNodes.size() > 0) {
      return sameTypeNodes[math.random(0, sameTypeNodes.size() - 1)];
    }

    // Otherwise, connect to any other node
    const otherNodes = allNodes.filter((n) => n.uuid !== isolatedNode.uuid);
    if (otherNodes.size() > 0) {
      return otherNodes[math.random(0, otherNodes.size() - 1)];
    }

    return undefined;
  }

  /**
   * Ensure backward connections for first layer
   */
  private ensureBackwardConnections(
    currentLayerNodes: Node[],
    nextLayerNodes: Node[],
    config: EnhancedGeneratorConfig,
    allLinks: Link[]
  ): void {
    nextLayerNodes.forEach((nextNode) => {
      // Ensure each node in layer 2 connects back to at least one node in layer 1
      const hasBackwardConnection = allLinks.some(
        (link) =>
          link.targetNodeUuid === nextNode.uuid &&
          currentLayerNodes.some((n) => n.uuid === link.sourceNodeUuid)
      );

      if (!hasBackwardConnection) {
        const sourceNode =
          currentLayerNodes[math.random(0, currentLayerNodes.size() - 1)];
        const link = this.createLink(sourceNode, nextNode, config);
        allLinks.push(link);
      }
    });
  }

  /**
   * Get default connections per node from config or use reasonable default
   */
  private getDefaultConnectionsPerNode(config: EnhancedGeneratorConfig): number {
    // Check if the config has a default value, otherwise use 2
    return 2; // Default to 2 connections per node
  }
}

import { GUI_CONSTANTS } from "../constants";
import { getAvailableProperties } from "./axisMappingControls/constants";

interface YAxisControlsProps {
  parent: Frame;
  useLayerForYAxis: boolean;
  yAxisProperty?: string;
  onYAxisModeChange: (useLayer: boolean) => void;
  onYAxisPropertyChange: (property: string) => void;
}

// Y-axis properties now use the same dynamic properties as X and Z axes
function getYAxisProperties(): string[] {
  return getAvailableProperties();
}

export function createYAxisControls({
  parent,
  useLayerForYAxis,
  yAxisProperty,
  onYAxisModeChange,
  onYAxisPropertyChange
}: YAxisControlsProps): Frame {
  // Create container WITHOUT clipping for dropdowns to show
  const container = new Instance("Frame");
  container.Name = "YAxisControls";
  container.Size = new UDim2(1, -20, 0, 80);
  container.Position = new UDim2(0, 0, 0, 0); // Position will be set by layout manager
  container.BackgroundColor3 = new Color3(0.15, 0.15, 0.15);
  container.BorderSizePixel = 0;
  container.ClipsDescendants = false; // Disable clipping so dropdowns can extend
  container.ZIndex = 2;
  container.Parent = parent;

  const containerCorner = new Instance("UICorner");
  containerCorner.CornerRadius = new UDim(0, 4);
  containerCorner.Parent = container;

  // Title
  const title = new Instance("TextLabel");
  title.Size = new UDim2(1, -20, 0, 20);
  title.Position = new UDim2(0, 10, 0, 5);
  title.BackgroundTransparency = 1;
  title.Font = GUI_CONSTANTS.TYPOGRAPHY.TITLE_FONT;
  title.Text = "Y-Axis Configuration";
  title.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  title.TextSize = 16;
  title.TextXAlignment = Enum.TextXAlignment.Left;
  title.Parent = container;

  // Toggle frame
  const toggleFrame = new Instance("Frame");
  toggleFrame.Size = new UDim2(0.5, -20, 0, 40);
  toggleFrame.Position = new UDim2(0, 10, 0, 30);
  toggleFrame.BackgroundTransparency = 1;
  toggleFrame.Parent = container;

  // Toggle label
  const toggleLabel = new Instance("TextLabel");
  toggleLabel.Size = new UDim2(0.6, 0, 0, 20);
  toggleLabel.Position = new UDim2(0, 0, 0, 0);
  toggleLabel.BackgroundTransparency = 1;
  toggleLabel.Font = GUI_CONSTANTS.TYPOGRAPHY.LABEL_FONT;
  toggleLabel.Text = "Use Layer:";
  toggleLabel.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  toggleLabel.TextSize = 14;
  toggleLabel.TextXAlignment = Enum.TextXAlignment.Left;
  toggleLabel.Parent = toggleFrame;

  // Toggle checkbox
  const checkbox = new Instance("TextButton");
  checkbox.Size = new UDim2(0, 25, 0, 25);
  checkbox.Position = new UDim2(0.6, 0, 0, -2);
  checkbox.BackgroundColor3 = useLayerForYAxis 
    ? GUI_CONSTANTS.COLORS.BUTTON.DEFAULT 
    : new Color3(0.3, 0.3, 0.3);
  checkbox.BorderSizePixel = 2;
  checkbox.BorderColor3 = new Color3(0.5, 0.5, 0.5);
  checkbox.Text = useLayerForYAxis ? "" : "";
  checkbox.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  checkbox.TextScaled = true;
  checkbox.Font = GUI_CONSTANTS.TYPOGRAPHY.BUTTON_FONT;
  checkbox.Parent = toggleFrame;

  const checkboxCorner = new Instance("UICorner");
  checkboxCorner.CornerRadius = new UDim(0, 4);
  checkboxCorner.Parent = checkbox;

  // Property dropdown (only visible when not using layer)
  const dropdownFrame = new Instance("Frame");
  dropdownFrame.Size = new UDim2(0.5, -20, 0, 40);
  dropdownFrame.Position = new UDim2(0.5, 10, 0, 30);
  dropdownFrame.BackgroundTransparency = 1;
  dropdownFrame.Visible = !useLayerForYAxis;
  dropdownFrame.Parent = container;

  // Property dropdown
  createYAxisDropdown({
    parent: dropdownFrame,
    label: "Y-Axis Property:",
    currentValue: yAxisProperty || "type",
    onValueChange: onYAxisPropertyChange
  });

  // Toggle handler
  checkbox.MouseButton1Click.Connect(() => {
    const newUseLayer = !useLayerForYAxis;

    // Update checkbox appearance
    checkbox.BackgroundColor3 = newUseLayer 
      ? GUI_CONSTANTS.COLORS.BUTTON.DEFAULT 
      : new Color3(0.3, 0.3, 0.3);
    checkbox.Text = newUseLayer ? "" : "";

    // Toggle dropdown visibility
    dropdownFrame.Visible = !newUseLayer;

    // Notify parent
    onYAxisModeChange(newUseLayer);
  });

  return container;
}

interface YAxisDropdownProps {
  parent: Frame;
  label: string;
  currentValue: string;
  onValueChange: (value: string) => void;
}

function createYAxisDropdown({
  parent,
  label,
  currentValue,
  onValueChange
}: YAxisDropdownProps): void {
  // Label
  const dropdownLabel = new Instance("TextLabel");
  dropdownLabel.Size = new UDim2(1, 0, 0, 20);
  dropdownLabel.Position = new UDim2(0, 0, 0, 0);
  dropdownLabel.BackgroundTransparency = 1;
  dropdownLabel.Font = GUI_CONSTANTS.TYPOGRAPHY.LABEL_FONT;
  dropdownLabel.Text = label;
  dropdownLabel.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  dropdownLabel.TextSize = 14;
  dropdownLabel.TextXAlignment = Enum.TextXAlignment.Left;
  dropdownLabel.Parent = parent;

  // Dropdown button
  const dropdownButton = new Instance("TextButton");
  dropdownButton.Size = new UDim2(1, 0, 0, 25);
  dropdownButton.Position = new UDim2(0, 0, 0, 20);
  dropdownButton.BackgroundColor3 = new Color3(0.25, 0.25, 0.25);
  dropdownButton.BorderSizePixel = 0;
  dropdownButton.Font = GUI_CONSTANTS.TYPOGRAPHY.INPUT_FONT;
  dropdownButton.Text = currentValue + " ";
  dropdownButton.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  dropdownButton.TextSize = 14;
  dropdownButton.Parent = parent;

  const buttonCorner = new Instance("UICorner");
  buttonCorner.CornerRadius = new UDim(0, 4);
  buttonCorner.Parent = dropdownButton;

  // Dropdown list - always use ScrollingFrame for consistency
  const yAxisProperties = getYAxisProperties();
  const itemHeight = 25;
  const maxItems = 6;
  const actualHeight = math.min(yAxisProperties.size() * itemHeight, maxItems * itemHeight);

  const dropdownList = new Instance("ScrollingFrame");
  dropdownList.Size = new UDim2(1, 0, 0, actualHeight);
  dropdownList.Position = new UDim2(0, 0, 0, -actualHeight - 5); // Position above button with gap
  dropdownList.BackgroundColor3 = new Color3(0.1, 0.1, 0.1);
  dropdownList.BorderSizePixel = 1;
  dropdownList.BorderColor3 = new Color3(0.3, 0.3, 0.3);
  dropdownList.ScrollBarThickness = 4;
  dropdownList.ScrollBarImageColor3 = new Color3(0.5, 0.5, 0.5);
  dropdownList.CanvasSize = new UDim2(0, 0, 0, yAxisProperties.size() * itemHeight);
  dropdownList.Visible = false;
  dropdownList.ZIndex = 100; // Higher z-index
  dropdownList.Parent = dropdownButton;

  const listCorner = new Instance("UICorner");
  listCorner.CornerRadius = new UDim(0, 4);
  listCorner.Parent = dropdownList;

  // Create option buttons
  yAxisProperties.forEach((property, index) => {
    const optionButton = new Instance("TextButton");
    optionButton.Size = new UDim2(1, -10, 0, itemHeight); // Always account for scrollbar
    optionButton.Position = new UDim2(0, 0, 0, index * itemHeight);
    optionButton.BackgroundColor3 = new Color3(0.1, 0.1, 0.1);
    optionButton.BackgroundTransparency = 0;
    optionButton.Font = GUI_CONSTANTS.TYPOGRAPHY.INPUT_FONT;
    optionButton.Text = property;
    optionButton.TextColor3 = new Color3(0.9, 0.9, 0.9);
    optionButton.TextSize = 14;
    optionButton.TextXAlignment = Enum.TextXAlignment.Center;
    optionButton.BorderSizePixel = 0;
    optionButton.Parent = dropdownList; // Parent directly to dropdownList

    // Hover effect
    optionButton.MouseEnter.Connect(() => {
      optionButton.BackgroundColor3 = new Color3(0.3, 0.3, 0.3);
      optionButton.TextColor3 = new Color3(1, 1, 1);
    });

    optionButton.MouseLeave.Connect(() => {
      optionButton.BackgroundColor3 = new Color3(0.1, 0.1, 0.1);
      optionButton.TextColor3 = new Color3(0.9, 0.9, 0.9);
    });

    // Selection
    optionButton.MouseButton1Click.Connect(() => {
      dropdownButton.Text = property + " ";
      dropdownList.Visible = false;
      onValueChange(property);
    });
  });

  // Toggle dropdown
  let closeConnection: RBXScriptConnection | undefined;

  dropdownButton.MouseButton1Click.Connect(() => {
    const isOpening = !dropdownList.Visible;
    dropdownList.Visible = isOpening;

    if (isOpening) {
      if (closeConnection) {
        closeConnection.Disconnect();
      }

      game.GetService("RunService").Heartbeat.Wait();

      const userInputService = game.GetService("UserInputService");
      closeConnection = userInputService.InputBegan.Connect((input, gameProcessed) => {
        if (input.UserInputType === Enum.UserInputType.MouseButton1 && !gameProcessed) {
          const mouse = game.GetService("Players").LocalPlayer.GetMouse();
          const target = mouse.Target;

          if (!target || !target.IsDescendantOf(dropdownButton)) {
            dropdownList.Visible = false;
            if (closeConnection) {
              closeConnection.Disconnect();
              closeConnection = undefined;
            }
          }
        }
      });
    } else if (closeConnection) {
      closeConnection.Disconnect();
      closeConnection = undefined;
    }
  });
}

/**
 * Y-Parallel Shadow Block Creator
 * Creates floating transparent shadow blocks at different Y levels
 * Part of F008 Vertical Swimlanes feature
 */

import { BaseBlockCreator } from "./baseBlockCreator";
import { BLOCK_CONSTANTS } from "../constants/blockConstants";
import { LAYOUT_CONSTANTS } from "../constants/layoutConstants";
import { Y_AXIS_COLORS } from "../constants/robloxColors";
import { Node } from "../../../interfaces/simpleDataGenerator.interface";
import { PropertyValueResolver } from "../propertyValueResolver";

export interface YParallelShadowConfig {
  nodes: Node[];
  yAxisProperty: string;
  parent: Instance;
  shadowWidth?: number; // Optional: use group shadow width
  shadowDepth?: number; // Optional: use group shadow depth
  side?: "left" | "right" | "back"; // Optional: which side to place shadows (default: "right")
}

export interface YGroupBounds {
  minX: number;
  maxX: number;
  minZ: number;
  maxZ: number;
  yPosition: number;
  propertyValue: string;
  minY: number;
  maxY: number;
}

export class YParallelShadowCreator extends BaseBlockCreator {
  private propertyResolver: PropertyValueResolver;

  constructor() {
    super();
    this.propertyResolver = new PropertyValueResolver();
  }

  /**
   * Create Y-parallel shadow blocks for each Y property value
   */
  public createYParallelShadows(config: YParallelShadowConfig): Map<string, Part> {
    const { nodes, yAxisProperty, parent, shadowWidth, shadowDepth, side = "right" } = config;
    const shadows = new Map<string, Part>();

    // Group nodes by Y property value and calculate bounds
    const yGroupBounds = this.calculateYGroupBounds(nodes, yAxisProperty);

    // Don't apply layer-based spacing - use the actual node Y positions

    // Create a shadow block for each Y group with different colors
    let colorIndex = 0;
    yGroupBounds.forEach((bounds, propertyValue) => {
      const color = Y_AXIS_COLORS[colorIndex % Y_AXIS_COLORS.size()] || new Color3(0.3, 0.3, 0.8);
      const shadow = this.createYShadowBlock(bounds, parent, shadowWidth, shadowDepth, color, side);
      shadows.set(propertyValue, shadow);

      print(`[YParallelShadow] Created ${side} shadow for ${propertyValue}: X(${string.format("%.1f", bounds.minX)},${string.format("%.1f", bounds.maxX)}) Z(${string.format("%.1f", bounds.minZ)},${string.format("%.1f", bounds.maxZ)}) Y=${string.format("%.1f", bounds.yPosition)} NodeYRange=(${string.format("%.1f", bounds.minY)}-${string.format("%.1f", bounds.maxY)}) Height=${string.format("%.1f", bounds.maxY - bounds.minY)} Color=${colorIndex}`);
      colorIndex++;
    });

    return shadows;
  }

  /**
   * Calculate bounds for each Y property group
   */
  private calculateYGroupBounds(nodes: Node[], yAxisProperty: string): Map<string, YGroupBounds> {
    const boundsMap = new Map<string, YGroupBounds>();
    const nodeRadius = 0.5; // Default node radius

    // Initialize bounds for each Y property value
    nodes.forEach(node => {
      const yValue = this.propertyResolver.getPropertyValue(node, yAxisProperty);

      if (!boundsMap.has(yValue)) {
        boundsMap.set(yValue, {
          minX: math.huge,
          maxX: -math.huge,
          minZ: math.huge,
          maxZ: -math.huge,
          yPosition: node.position.y,
          propertyValue: yValue,
          minY: node.position.y,
          maxY: node.position.y
        });
      }

      const bounds = boundsMap.get(yValue)!;
      // Account for node radius when calculating bounds
      bounds.minX = math.min(bounds.minX, node.position.x - nodeRadius);
      bounds.maxX = math.max(bounds.maxX, node.position.x + nodeRadius);
      bounds.minZ = math.min(bounds.minZ, node.position.z - nodeRadius);
      bounds.maxZ = math.max(bounds.maxZ, node.position.z + nodeRadius);
      // Track Y bounds for height calculation
      bounds.minY = math.min(bounds.minY, node.position.y);
      bounds.maxY = math.max(bounds.maxY, node.position.y);
      // Y position should be consistent for all nodes with same property value
      bounds.yPosition = node.position.y;
    });

    return boundsMap;
  }


  /**
   * Create a single Y-parallel shadow block
   */
  private createYShadowBlock(bounds: YGroupBounds, parent: Instance, shadowWidth?: number, shadowDepth?: number, color?: Color3, side: "left" | "right" | "back" = "right"): Part {
    // T9.8.1: Y shadows start from vertical wall and extend 10 units away
    const shadowExtension = 10; // Units to extend from wall
    let width: number;
    let depth: number;
    let positionX: number;
    let positionZ: number;

    if (shadowWidth !== undefined && shadowDepth !== undefined) {
      // Position Y shadows to start from vertical wall edge
      if (side === "right") {
        // The right wall is at shadowWidth/2
        const wallPosition = shadowWidth / 2;
        // Y shadow starts at wall position and extends outward to the right (positive X)
        width = shadowExtension;
        depth = shadowDepth;
        positionX = wallPosition + shadowExtension / 2 - 2; // Center of extended shadow (extends right) - 2 units offset
        positionZ = 0; // Center at origin in Z direction
      } else if (side === "left") {
        // The left wall is at -shadowWidth/2
        const wallPosition = -shadowWidth / 2;
        // Y shadow starts at wall position and extends inward by 1 unit
        width = shadowExtension;
        depth = shadowDepth;
        positionX = wallPosition - shadowExtension / 2 + 1; // Center of extended shadow (extends left) + 1 unit into node area
        positionZ = 0; // Center at origin in Z direction
      } else {
        // The back wall is at shadowDepth/2
        const wallPosition = shadowDepth / 2;
        // Y shadow starts at wall position and extends forward (negative Z) by 1 unit into node area
        width = shadowWidth; // Full width along X axis
        depth = shadowExtension;
        positionX = 0; // Center at origin in X direction
        positionZ = wallPosition - shadowExtension / 2 + 1; // Center of extended shadow (extends forward) + 1 unit into node area
      }
    } else {
      // Fallback: Calculate from node bounds with padding
      const padding = LAYOUT_CONSTANTS.SHADOW_PADDING.Y_SHADOW_PADDING;
      width = bounds.maxX - bounds.minX + padding * 2;
      depth = bounds.maxZ - bounds.minZ + padding * 2;
      positionX = (bounds.minX + bounds.maxX) / 2;
      positionZ = (bounds.minZ + bounds.maxZ) / 2;
    }

    // Use fixed height for Y shadows
    const shadowHeight = BLOCK_CONSTANTS.DIMENSIONS.Y_SHADOW_THICKNESS || 5;

    print(`[YParallelShadow] Creating shadow ${bounds.propertyValue}: Y=${string.format("%.1f", bounds.yPosition)}, height=${string.format("%.1f", shadowHeight)}`);

    // Create the shadow block
    const shadow = this.createBlock({
      name: `YShadow_${side}_${bounds.propertyValue}`,
      size: new Vector3(
        width,
        shadowHeight, // Use height from nodes
        depth
      ),
      position: new Vector3(
        positionX,
        bounds.yPosition, // Use layer Y position
        positionZ
      ),
      color: color || BLOCK_CONSTANTS.COLORS.Y_SHADOW_COLOR || new Color3(0.5, 0.5, 0.5),
      transparency: BLOCK_CONSTANTS.TRANSPARENCY.Y_SHADOW || 0, // Fully opaque
      material: Enum.Material.Concrete,
      canCollide: false
    });

    shadow.Parent = parent;

    // Print actual shadow position
    const actualPos = shadow.Position;
    print(`[YParallelShadow] Shadow block ${bounds.propertyValue} actual position: X=${string.format("%.1f", actualPos.X)}, Y=${string.format("%.1f", actualPos.Y)}, Z=${string.format("%.1f", actualPos.Z)} (Y offset from nodes: ${string.format("%.1f", actualPos.Y - bounds.yPosition)})`);

    // Add label
    this.addLabel(shadow, bounds.propertyValue);

    return shadow;
  }

  /**
   * Add labels to all faces of the Y shadow block with black color
   */
  private addLabel(shadow: Part, text: string): void {
    // All faces use black text color
    const faces = [
      Enum.NormalId.Top,
      Enum.NormalId.Bottom,
      Enum.NormalId.Front,
      Enum.NormalId.Back,
      Enum.NormalId.Left,
      Enum.NormalId.Right
    ];

    // Add label to each face
    faces.forEach((face) => {
      const surfaceGui = new Instance("SurfaceGui");
      surfaceGui.Face = face;
      surfaceGui.SizingMode = Enum.SurfaceGuiSizingMode.PixelsPerStud;
      surfaceGui.PixelsPerStud = 50;
      surfaceGui.Parent = shadow;

      const label = new Instance("TextLabel");
      label.Size = new UDim2(1, 0, 1, 0);
      label.BackgroundTransparency = 1;
      label.Text = text;
      label.TextColor3 = new Color3(0, 0, 0); // Black text color
      label.TextScaled = true;
      label.Font = Enum.Font.SourceSansBold;
      label.TextStrokeTransparency = 0;
      label.TextStrokeColor3 = new Color3(0, 0, 0);
      label.Parent = surfaceGui;
    });
  }
}

