// Default colors
const DEFAULT_BACKGROUND_COLOR = new Color3(0.5, 0.5, 0.5);
const DEFAULT_BORDER_COLOR = new Color3(0, 0, 0);

/**
 * Get property value from node for color mapping
 */
function getNodePropertyValue(node: Node, propertyName: string): string {
  if (propertyName === "type") {
    return node.type;
  } else if (propertyName === "age" && node.properties?.age !== undefined) {
    const age = node.properties.age;
    if (age < 20) return "0-19";
    if (age < 40) return "20-39";
    if (age < 60) return "40-59";
    if (age < 80) return "60-79";
    return "80+";
  } else if (node.properties && propertyName in node.properties) {
    const value = node.properties[propertyName as keyof typeof node.properties];
    return value ? tostring(value) : "None";
  }
  return "Unknown";
}

/**
 * Get color for a property value
 */
function getColorForPropertyValue(propertyName: string, value: string): Color3 {
  const scheme = COLOR_SCHEMES[propertyName as keyof typeof COLOR_SCHEMES];
  if (!scheme) return DEFAULT_BACKGROUND_COLOR;

  // Check if value exists in scheme
  if (value in scheme) {
    return scheme[value as keyof typeof scheme];
  }

  // Use default color if available
  if ("default" in scheme) {
    return scheme.default;
  }

  return DEFAULT_BACKGROUND_COLOR;
}

/**
 * Get background color for a node based on visual mapping
 */
export function getNodeBackgroundColor(node: Node, visualMapping?: VisualMapping): Color3 {
  if (!visualMapping || visualMapping.backgroundColor === "none") {
    // Use node's default color
    return new Color3(node.color[0], node.color[1], node.color[2]);
  }

  const propertyValue = getNodePropertyValue(node, visualMapping.backgroundColor);
  const mappedColor = getColorForPropertyValue(visualMapping.backgroundColor, propertyValue);

  return mappedColor;
}

/**
 * Get border color for a node based on visual mapping
 */
export function getNodeBorderColor(node: Node, visualMapping?: VisualMapping): Color3 {
  if (!visualMapping || visualMapping.borderColor === "none") {
    return DEFAULT_BORDER_COLOR;
  }

  const propertyValue = getNodePropertyValue(node, visualMapping.borderColor);
  return getColorForPropertyValue(visualMapping.borderColor, propertyValue);
}

/**
 * Get text properties for a node (for label visibility)
 */
export function getNodeTextProperties(backgroundColor: Color3): { textColor: Color3; borderColor: Color3 } {
  // Calculate brightness of background
  const brightness = (backgroundColor.R + backgroundColor.G + backgroundColor.B) / 3;

  // Use contrasting text color
  const textColor = brightness > 0.5 
    ? new Color3(0, 0, 0)  // Black text on light background
    : new Color3(1, 1, 1); // White text on dark background

  return {
    textColor,
    borderColor: textColor // Use same color for text border
  };
}

/**
 * Component Factory for Configuration GUI
 * 
 * Provides a centralized factory for creating GUI components
 * with consistent styling and behavior.
 */

import { GUI_CONSTANTS } from "./constants";
import { createDropdown } from "./components/dropdown";
import { createFrame } from "./components/frame";
import { createTitle } from "./components/title";

interface ButtonOptions {
  name: string;
  text: string;
  position: UDim2;
  size?: UDim2;
  parent: GuiObject;
  onClick?: () => void;
  backgroundColor?: Color3;
}

interface TextInputOptions {
  name: string;
  placeholderText?: string;
  text?: string;
  position: UDim2;
  size?: UDim2;
  parent: GuiObject;
  onFocusLost?: (enterPressed: boolean, inputObject: InputObject) => void;
}

export interface LabelOptions {
  name: string;
  text: string;
  position: UDim2;
  size?: UDim2;
  parent: GuiObject;
  textColor?: Color3;
  font?: Enum.Font;
  textScaled?: boolean;
}

export interface FrameOptions {
  name: string;
  position?: UDim2;
  size?: UDim2;
  parent: GuiObject;
  backgroundColor?: Color3;
  borderSizePixel?: number;
}

interface DropdownOptions {
  name: string;
  items: string[];
  defaultValue: string;
  position: UDim2;
  size?: UDim2;
  parent: Frame;
  onChange: (value: string) => void;
}

export interface TitleOptions {
  text: string;
  parent: Frame;
  position?: UDim2;
  size?: UDim2;
}

export class ComponentFactory {
  /**
   * Creates a styled button
   */
  public static createButton(options: ButtonOptions): TextButton {
    const button = new Instance("TextButton");
    button.Name = options.name;
    button.Size = options.size || new UDim2(0, GUI_CONSTANTS.BUTTON.WIDTH, 0, GUI_CONSTANTS.BUTTON.HEIGHT);
    button.Position = options.position;
    button.BackgroundColor3 = options.backgroundColor || GUI_CONSTANTS.COLORS.BUTTON.DEFAULT;
    button.BorderSizePixel = 0;
    button.Font = GUI_CONSTANTS.TYPOGRAPHY.BUTTON_FONT;
    button.Text = options.text;
    button.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
    button.TextScaled = true;
    button.Parent = options.parent;

    // Add corner radius
    const corner = new Instance("UICorner");
    corner.CornerRadius = new UDim(0, 4);
    corner.Parent = button;

    // Add hover effect
    button.MouseEnter.Connect(() => {
      button.BackgroundColor3 = GUI_CONSTANTS.COLORS.BUTTON.HOVER;
    });

    button.MouseLeave.Connect(() => {
      button.BackgroundColor3 = options.backgroundColor || GUI_CONSTANTS.COLORS.BUTTON.DEFAULT;
    });

    // Add click feedback
    button.MouseButton1Down.Connect(() => {
      button.BackgroundColor3 = GUI_CONSTANTS.COLORS.BUTTON.ACTIVE;
    });

    button.MouseButton1Up.Connect(() => {
      button.BackgroundColor3 = GUI_CONSTANTS.COLORS.BUTTON.HOVER;
    });

    // Connect click handler
    if (options.onClick) {
      button.MouseButton1Click.Connect(options.onClick);
    }

    return button;
  }

  /**
   * Creates a styled text input
   */
  public static createTextInput(options: TextInputOptions): TextBox {
    const input = new Instance("TextBox");
    input.Name = options.name;
    input.Size = options.size || new UDim2(0.4, 0, 0, GUI_CONSTANTS.INPUT.HEIGHT);
    input.Position = options.position;
    input.BackgroundColor3 = new Color3(0.3, 0.3, 0.3);
    input.BorderSizePixel = 0;
    input.Font = GUI_CONSTANTS.TYPOGRAPHY.INPUT_FONT;
    input.Text = options.text || "";
    input.PlaceholderText = options.placeholderText || "";
    input.PlaceholderColor3 = new Color3(0.7, 0.7, 0.7);
    input.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
    input.TextScaled = true;
    input.ClearTextOnFocus = false;
    input.Parent = options.parent;

    // Add corner radius
    const corner = new Instance("UICorner");
    corner.CornerRadius = new UDim(0, 4);
    corner.Parent = input;

    // Connect focus lost handler
    if (options.onFocusLost) {
      input.FocusLost.Connect(options.onFocusLost);
    }

    return input;
  }

  /**
   * Creates a styled label
   */
  public static createLabel(options: LabelOptions): TextLabel {
    const label = new Instance("TextLabel");
    label.Name = options.name;
    label.Size = options.size || new UDim2(0.5, 0, 0, GUI_CONSTANTS.INPUT.HEIGHT);
    label.Position = options.position;
    label.BackgroundTransparency = 1;
    label.Font = options.font || GUI_CONSTANTS.TYPOGRAPHY.LABEL_FONT;
    label.Text = options.text;
    label.TextColor3 = options.textColor || GUI_CONSTANTS.COLORS.TEXT;
    label.TextScaled = options.textScaled !== false;
    label.TextXAlignment = Enum.TextXAlignment.Left;
    label.Parent = options.parent;

    return label;
  }

  /**
   * Creates a styled frame
   */
  public static createFrame(options: FrameOptions): Frame {
    const frame = new Instance("Frame");
    frame.Name = options.name;
    frame.Size = options.size || new UDim2(1, 0, 1, 0);
    frame.Position = options.position || new UDim2(0, 0, 0, 0);
    frame.BackgroundColor3 = options.backgroundColor || GUI_CONSTANTS.COLORS.BACKGROUND;
    frame.BorderSizePixel = options.borderSizePixel !== undefined ? options.borderSizePixel : 0;
    frame.Parent = options.parent;

    return frame;
  }

  /**
   * Creates a checkbox (using a TextButton)
   */
  public static createCheckbox(options: ButtonOptions & { checked?: boolean }): TextButton {
    const checkbox = this.createButton({
      ...options,
      size: new UDim2(0, 20, 0, 20),
      text: options.checked ? "" : "",
      backgroundColor: new Color3(0.3, 0.3, 0.3)
    });

    let checked = options.checked || false;

    checkbox.MouseButton1Click.Connect(() => {
      checked = !checked;
      checkbox.Text = checked ? "" : "";
      if (options.onClick) {
        options.onClick();
      }
    });

    return checkbox;
  }

  /**
   * Creates a full dropdown with item selection
   */
  public static createDropdown(options: DropdownOptions): TextButton {
    return createDropdown({
      parent: options.parent,
      position: options.position,
      size: options.size || new UDim2(0, 100, 0, GUI_CONSTANTS.INPUT.HEIGHT),
      items: options.items,
      defaultValue: options.defaultValue,
      onChange: options.onChange
    });
  }

  /**
   * Creates a styled frame using the modular component
   */
  public static createStyledFrame(options: FrameOptions): Frame {
    return createFrame({
      name: options.name,
      parent: options.parent,
      position: options.position,
      size: options.size,
      backgroundColor: options.backgroundColor,
      borderSizePixel: options.borderSizePixel
    });
  }

  /**
   * Creates a title component
   */
  public static createTitle(options: TitleOptions): TextLabel {
    return createTitle(options);
  }

  /**
   * Creates a numeric input with validation
   */
  public static createNumericInput(options: TextInputOptions & { min?: number; max?: number }): TextBox {
    const input = this.createTextInput(options);

    input.FocusLost.Connect((enterPressed) => {
      const value = tonumber(input.Text);
      if (value === undefined) {
        input.Text = "0";
      } else if (options.min !== undefined && value < options.min) {
        input.Text = tostring(options.min);
      } else if (options.max !== undefined && value > options.max) {
        input.Text = tostring(options.max);
      }

      if (options.onFocusLost) {
        options.onFocusLost(enterPressed, {} as InputObject);
      }
    });

    return input;
  }

  /**
   * Creates a section divider
   */
  public static createDivider(parent: GuiObject, yPosition: number): Frame {
    const divider = new Instance("Frame");
    divider.Name = "Divider";
    divider.Size = new UDim2(0.9, 0, 0, 1);
    divider.Position = new UDim2(0.05, 0, 0, yPosition);
    divider.BackgroundColor3 = new Color3(0.4, 0.4, 0.4);
    divider.BorderSizePixel = 0;
    divider.Parent = parent;

    return divider;
  }

  /**
   * Creates a scrolling frame
   */
  public static createScrollingFrame(options: FrameOptions & { canvasSize?: UDim2 }): ScrollingFrame {
    const scrollFrame = new Instance("ScrollingFrame");
    scrollFrame.Name = options.name;
    scrollFrame.Size = options.size || new UDim2(1, 0, 1, 0);
    scrollFrame.Position = options.position || new UDim2(0, 0, 0, 0);
    scrollFrame.BackgroundColor3 = options.backgroundColor || GUI_CONSTANTS.COLORS.BACKGROUND;
    scrollFrame.BorderSizePixel = options.borderSizePixel !== undefined ? options.borderSizePixel : 0;
    scrollFrame.ScrollBarThickness = 8;
    scrollFrame.ScrollBarImageColor3 = new Color3(0.5, 0.5, 0.5);
    scrollFrame.CanvasSize = options.canvasSize || new UDim2(0, 0, 0, 0);
    scrollFrame.Parent = options.parent;

    return scrollFrame;
  }
}

/**
 * Node Renderer for Unified Data Renderer
 * 
 * Handles creation and rendering of hexagon nodes
 */

import { Cluster, Node } from "../../../../interfaces/simpleDataGenerator.interface";
import { INodeRenderer, SpacingConfig } from "../interfaces";

import { EnhancedGeneratorConfig } from "../../../../interfaces/enhancedGenerator.interface";
import { RENDERER_CONSTANTS } from "../../dataGeneratorRobloxRendererUtils/constants";
import { createRopeConnectors } from "../../dataGeneratorRobloxRendererUtils/ropeCreator";
import { getNodeBackgroundColor } from "../utils/colorMapper";
import { makeHexagon } from "../../../hexagonMaker";
import { PropertyValueResolver } from "../../propertyValueResolver";
import { getDefaultXAxis } from "../../../../constants/axisDefaults";

export class NodeRenderer implements INodeRenderer {
  /**
   * Renders the cluster with positioned nodes
   */
  public renderCluster(cluster: Cluster, parentFolder: Folder, config?: EnhancedGeneratorConfig): void {

    // Look for existing GraphMaker folder and delete it
    const existingGraphMaker = parentFolder.FindFirstChild("GraphMaker");
    if (existingGraphMaker) {
      existingGraphMaker.Destroy();
    }

    // Create GraphMaker folder
    const graphMakerFolder = new Instance("Folder");
    graphMakerFolder.Name = "GraphMaker";
    graphMakerFolder.Parent = parentFolder;

    // Create folder structure inside GraphMaker
    const clusterFolder = new Instance("Folder");
    clusterFolder.Name = "UnifiedDataCluster";
    clusterFolder.Parent = graphMakerFolder;

    const nodesFolder = new Instance("Folder");
    nodesFolder.Name = "Nodes";
    nodesFolder.Parent = clusterFolder;

    const linksFolder = new Instance("Folder");
    linksFolder.Name = "Links";
    linksFolder.Parent = clusterFolder;

    // Create hexagons for all nodes if showNodes is true
    const showNodes = config?.visualization?.showNodes !== false; // Default to true
    const nodeToHexagon = showNodes 
      ? this.createHexagons(cluster, nodesFolder, config)
      : new Map<string, Model>();

    // Create links/ropes for relationships only if nodes are shown and connectors are enabled
    if (showNodes && config?.visualization?.showConnectors !== false) {
      print(`[NodeRenderer] Creating links for ${cluster.relations.size()} relations`);
      createRopeConnectors({
        cluster,
        nodeToHexagon,
        linksFolder,
        visualization: config?.visualization,
        linkDiameter: config?.spacing?.linkDiameter
      });
    } else {
      print(`[NodeRenderer] Not creating links - showNodes: ${showNodes}, showConnectors: ${config?.visualization?.showConnectors}`);
    }

  }

  /**
   * Creates hexagons for all nodes in the cluster
   */
  public createHexagons(cluster: Cluster, nodesFolder: Folder, config?: EnhancedGeneratorConfig): Map<string, Model> {
    const nodeToHexagon = new Map<string, Model>();
    let hexIndex = 1;

    // Use spacing from config if provided, otherwise use defaults
    const spacing = this.getSpacingConfig(config);

    // Create property resolver to group nodes
    const propertyResolver = new PropertyValueResolver();

    // Get the X-axis property for swimlane grouping
    const xAxisProperty = config?.axisMapping?.xAxis || getDefaultXAxis(cluster.discoveredProperties);

    // Group nodes by swimlane property
    const nodesBySwimlane = new Map<string, Node[]>();

    cluster.groups.forEach(group => {
      group.nodes.forEach(node => {
        const propertyValue = propertyResolver.getPropertyValue(node, xAxisProperty);

        if (!nodesBySwimlane.has(propertyValue)) {
          nodesBySwimlane.set(propertyValue, []);
        }
        nodesBySwimlane.get(propertyValue)!.push(node);
      });
    });

    // Create a Model for each swimlane group
    nodesBySwimlane.forEach((nodes, swimlaneName) => {
      const swimlaneModel = new Instance("Model");
      swimlaneModel.Name = `Swimlane_${swimlaneName}`;
      swimlaneModel.Parent = nodesFolder;

      // Create hexagons for nodes in this swimlane
      nodes.forEach(node => {
        const hexagon = this.createSingleHexagon(node, hexIndex, spacing, config);
        hexagon.Parent = swimlaneModel;
        nodeToHexagon.set(node.uuid, hexagon);
        hexIndex++;
      });
    });

    return nodeToHexagon;
  }

  /**
   * Create a single hexagon for a node
   */
  private createSingleHexagon(node: Node, hexIndex: number, spacing: SpacingConfig, config?: EnhancedGeneratorConfig): Model {
    const WIDTH = spacing.nodeRadius * 2; // Diameter from radius
    const HEIGHT = spacing.nodeHeight;

    const labels = this.createNodeLabels(node);

    // Get colors based on visual mapping
    const backgroundColor = getNodeBackgroundColor(node, config?.visualMapping);
    // const borderColor = getNodeBorderColor(node, config?.visualMapping); // Not used in standardized version

    const hexagon = makeHexagon({
      id: hexIndex,
      position: new Vector3(node.position.x, node.position.y, node.position.z),
      width: WIDTH,
      height: HEIGHT,
      barColor: backgroundColor,
      barMaterial: Enum.Material.SmoothPlastic, // Performance optimization
      castShadow: false, // Performance optimization
      labels: labels,
      stackIndex: 1,
      hexIndex: hexIndex,
      guid: node.uuid
    });

    // Debug: Log node position for Y shadow comparison
    if (node.properties && "component" in node.properties) {
      const component = node.properties.component;
      if (component) {
        print(`[NodeRenderer] Node ${node.name} (${component}): Position Y=${string.format("%.1f", node.position.y)}`);
      }
    }

    // Set hexagon name based on UUID pattern
    this.setHexagonName(hexagon, node.uuid);

    // Store node properties as attributes for the inspector
    hexagon.SetAttribute("nodeName", node.name);
    hexagon.SetAttribute("nodeType", node.type);

    // Dynamically store all properties as attributes
    if (node.properties) {
      for (const [key, value] of pairs(node.properties)) {
        if (value !== undefined) {
          // Convert value to appropriate type for SetAttribute
          if (typeIs(value, "string") || typeIs(value, "number") || typeIs(value, "boolean")) {
            hexagon.SetAttribute(key as string, value);
          } else {
            // For complex types, convert to string
            hexagon.SetAttribute(key as string, tostring(value));
          }
        }
      }
    }

    return hexagon;
  }

  /**
   * Create labels for a node
   */
  private createNodeLabels(node: Node): string[] {
    // For person nodes, use full name if available
    const isPersonNode = ["man", "woman", "child", "grandparent"].includes(node.type);
    const fullName = isPersonNode && node.properties?.firstName && node.properties?.lastName
      ? `${node.properties.firstName} ${node.properties.lastName}`
      : node.name;

    const labels: string[] = [
      fullName,
      node.type,
      (node as Node & { typeNumber?: string }).typeNumber || ""
    ];

    // Add type-specific properties
    if (isPersonNode && node.properties?.age) {
      labels.push(`Age: ${node.properties.age}`);
    } else if (node.type === "Animals" && node.properties?.animalType) {
      labels.push(node.properties.animalType);
    }

    return labels;
  }

  /**
   * Set hexagon name based on UUID pattern
   */
  private setHexagonName(hexagon: Model, uuid: string): void {
    // Extract layer and node index from uuid if available
    const layerMatch = uuid.match("node_(\\d+)_(\\d+)");
    if (layerMatch) {
      const layerNum = layerMatch[1];
      const nodeIdx = layerMatch[2];
      hexagon.Name = `Hexagon_L${layerNum}_N${nodeIdx}`;
    } else {
      hexagon.Name = `Hexagon_${uuid}`;
    }
  }

  /**
   * Get spacing configuration
   */
  private getSpacingConfig(config?: EnhancedGeneratorConfig): SpacingConfig {
    return config?.spacing || {
      nodeHeight: RENDERER_CONSTANTS.HEXAGON.HEIGHT,
      nodeRadius: RENDERER_CONSTANTS.HEXAGON.WIDTH / 2,
      layerSpacing: RENDERER_CONSTANTS.POSITIONING.LEVEL_SPACING,
      nodeSpacing: RENDERER_CONSTANTS.POSITIONING.COLUMN_SPACING,
      swimlaneSpacing: RENDERER_CONSTANTS.POSITIONING.COLUMN_SPACING
    };
  }
}

/**
 * Shadow Block Creator
 * Creates shadow blocks for visual depth and grouping
 * Part of F002 Phase 2 refactoring - T6
 */

import { BaseBlockCreator } from "./baseBlockCreator";
import { BLOCK_CONSTANTS } from "../constants/blockConstants";
import { LAYOUT_CONSTANTS } from "../constants/layoutConstants";
import { EndcapBlockCreator } from "./endcapBlockCreator";

export interface ShadowBlockConfig {
  origin: Vector3;
  parent: Instance;
  height?: number;
  width?: number;
  depth?: number;
  buffer?: number; // DEPRECATED: Padding is handled by LAYOUT_CONSTANTS.SHADOW_PADDING
}

export class ShadowBlockCreator extends BaseBlockCreator {
  private endcapCreator: EndcapBlockCreator;

  constructor() {
    super();
    this.endcapCreator = new EndcapBlockCreator();
  }
  /**
   * Create a group shadow block
   */
  public createGroupShadowBlock(config: ShadowBlockConfig): Part {
    const {
      origin,
      parent,
      width = BLOCK_CONSTANTS.DIMENSIONS.DEFAULT_WIDTH,
      depth = BLOCK_CONSTANTS.DIMENSIONS.DEFAULT_DEPTH,
    } = config;

    // Note: buffer parameter is deprecated - padding is handled by LAYOUT_CONSTANTS.SHADOW_PADDING
    const shadowBlock = this.createBlock({
      name: "GroupShadowBlock",
      size: new Vector3(
        width,
        BLOCK_CONSTANTS.DIMENSIONS.UNIFORM_SHADOW_THICKNESS,
        depth
      ),
      position: new Vector3(
        origin.X,
        BLOCK_CONSTANTS.DIMENSIONS.UNIFORM_SHADOW_THICKNESS / 2 +
          BLOCK_CONSTANTS.DIMENSIONS.Z_FIGHTING_OFFSET, // Raised by 0.1 to prevent z-fighting
        origin.Z
      ),
      material: BLOCK_CONSTANTS.MATERIALS.SHADOW,
      color: BLOCK_CONSTANTS.COLORS.SHADOW,
      transparency: BLOCK_CONSTANTS.TRANSPARENCY.OPAQUE,
      canCollide: true,
    });

    shadowBlock.CastShadow = false;
    shadowBlock.Parent = parent;

    return shadowBlock;
  }

  /**
   * Create X-parallel shadow blocks (run along X axis, grouped by Z position)
   */
  public createXParallelShadowBlocks(
    nodesByProperty: Map<string, any[]>,
    propertyBounds: Map<
      string,
      { minX: number; maxX: number; minZ: number; maxZ: number }
    >,
    parent: Instance,
    yPosition: number = 0.5,
    blocksMap?: Map<string, Part>,
    propertyName?: string,
    origin?: Vector3
  ): void {
    // First, calculate the collective bounds of all pet lanes
    let collectiveMinZ = math.huge;
    let collectiveMaxZ = -math.huge;

    propertyBounds.forEach((bounds, propertyValue) => {
      collectiveMinZ = math.min(collectiveMinZ, bounds.minZ);
      collectiveMaxZ = math.max(collectiveMaxZ, bounds.maxZ);
    });

    // Calculate the center of the collective pet lanes
    const collectiveCenter = (collectiveMinZ + collectiveMaxZ) / 2;

    // The group shadow block is centered at Z=0, so we need to offset the pet lanes
    const offsetZ = 0 - collectiveCenter;

    let blockIndex = 0;

    nodesByProperty.forEach((nodes, propertyValue) => {
      const bounds = propertyBounds.get(propertyValue);
      if (!bounds) {
        warn(
          `[ShadowBlockCreator] No bounds found for property value: ${propertyValue}`
        );
        return;
      }
      const block = this.createXParallelBlock(
        propertyValue,
        bounds,
        yPosition,
        blockIndex,
        propertyName,
        offsetZ
      );
      block.Parent = parent;

      // Create swimlane model with endcaps
      this.endcapCreator.createSwimlaneWithEndcaps({
        swimlaneBlock: block,
        swimlaneName: propertyValue,
        parent: parent,
        gap: 1,
        isZAxis: true,
      });

      if (blocksMap) {
        blocksMap.set(propertyValue, block);
      }

      blockIndex++;
    });
  }

  /**
   * Create a single X-parallel shadow block
   */
  private createXParallelBlock(
    propertyValue: string,
    bounds: { minX: number; maxX: number; minZ: number; maxZ: number },
    yPosition: number,
    colorIndex: number,
    propertyName?: string,
    offsetZ: number = 0
  ): Part {
    // Use actual bounds for dimensions - NO BUFFER to match actual content
    const width = bounds.maxX - bounds.minX + 4; // Add 4 to length as requested
    // Use the correct depth for X-parallel lanes
    const depth = LAYOUT_CONSTANTS.LANE_DIMENSIONS.X_PARALLEL_LANE_DEPTH;

    const centerX = (bounds.minX + bounds.maxX) / 2;
    const centerZ = (bounds.minZ + bounds.maxZ) / 2;

    // Generate unique ID for the lane
    const laneId = `${propertyName || "default"}_${propertyValue}`;
    const blockName = `XParallel_Lane_${laneId}`;

    // Apply the offset to center the collection of lanes
    const adjustedZPosition = centerZ + offsetZ;

    const block = this.createBlock({
      name: blockName,
      size: new Vector3(
        width,
        LAYOUT_CONSTANTS.LANE_DIMENSIONS.X_PARALLEL_LANE_HEIGHT,
        depth
      ),
      position: new Vector3(centerX, yPosition, adjustedZPosition),
      material: BLOCK_CONSTANTS.MATERIALS.SWIMLANE,
      color: this.getColorFromArray(
        BLOCK_CONSTANTS.COLORS.X_PARALLEL_LANE_COLORS,
        colorIndex
      ),
      transparency: BLOCK_CONSTANTS.TRANSPARENCY.OPAQUE,
      canCollide: true,
    });

    block.CastShadow = false;

    // Add surface labels to all faces
    this.addSurfaceLabelsToAllFaces(block, propertyValue);

    return block;
  }

  /**
   * Add surface labels to all faces of a block
   */
  private addSurfaceLabelsToAllFaces(block: Part, text: string): void {
    const faces: Enum.NormalId[] = [
      Enum.NormalId.Front,
      Enum.NormalId.Back,
      Enum.NormalId.Left,
      Enum.NormalId.Right,
      Enum.NormalId.Top,
      Enum.NormalId.Bottom,
    ];

    // Check if this is an X-parallel block
    const isXParallel = block.Name.match("^XParallel_Lane_")[0] !== undefined;

    faces.forEach((face) => {
      // Skip Front face (red labels), Back face (green labels), and Left face (blue labels) for X-parallel blocks
      if (
        isXParallel &&
        (face === Enum.NormalId.Front ||
          face === Enum.NormalId.Back ||
          face === Enum.NormalId.Left)
      ) {
        return;
      }
      // Create SurfaceGui
      const surfaceGui = new Instance("SurfaceGui");
      surfaceGui.Name = `SurfaceGui_${face.Name}`;
      surfaceGui.Face = face;
      surfaceGui.SizingMode = Enum.SurfaceGuiSizingMode.PixelsPerStud;
      surfaceGui.PixelsPerStud = 50;
      surfaceGui.Parent = block;

      // Create Frame for background with border
      const frame = new Instance("Frame");
      frame.Size = new UDim2(1, 0, 1, 0);
      frame.BackgroundColor3 = block.Color; // Match block color
      frame.BackgroundTransparency =
        BLOCK_CONSTANTS.LABEL_STYLING.BACKGROUND_TRANSPARENCY;
      frame.BorderSizePixel = BLOCK_CONSTANTS.LABEL_STYLING.BORDER_SIZE_PIXEL;
      frame.BorderColor3 = BLOCK_CONSTANTS.LABEL_STYLING.BORDER_COLOR;
      frame.Parent = surfaceGui;

      // Create TextLabel
      const textLabel = new Instance("TextLabel");
      textLabel.Size = new UDim2(0.9, 0, 0.9, 0);
      textLabel.Position = new UDim2(0.05, 0, 0.05, 0);
      textLabel.BackgroundTransparency = 1;
      textLabel.Font = BLOCK_CONSTANTS.LABEL_STYLING.FONT;
      textLabel.Text = text;

      // Use different colors for each face (for testing)
      const faceColors: { [key: string]: Color3 } = {
        Front: new Color3(1, 0, 0), // Red
        Back: new Color3(0, 1, 0), // Green
        Left: new Color3(0, 0, 1), // Blue
        Right: new Color3(1, 1, 0), // Yellow
        Top: new Color3(1, 0, 1), // Magenta
        Bottom: new Color3(0, 1, 1), // Cyan
      };

      textLabel.TextColor3 = faceColors[face.Name] || new Color3(1, 1, 1); // Default to white
      textLabel.TextScaled = BLOCK_CONSTANTS.LABEL_STYLING.TEXT_SCALED;
      textLabel.Rotation = 90; // Rotate 90 degrees clockwise
      textLabel.Parent = frame;
    });
  }
}

import { GUI_CONSTANTS } from "../constants";
import type { VisualMapping } from "../../../../shared/interfaces/enhancedGenerator.interface";

interface VisualCustomizationControlsProps {
  parent: Frame;
  visualMapping?: VisualMapping;
  onVisualMappingChange: (mapping: keyof VisualMapping, value: string) => void;
}

// Available properties for visual mapping
const VISUAL_PROPERTIES = ["none", "type", "petType", "petColor", "age", "firstName", "lastName", "countryOfBirth", "countryOfResidence"];

export function createVisualCustomizationControls({
  parent,
  visualMapping,
  onVisualMappingChange
}: VisualCustomizationControlsProps): Frame {
  // Provide default visual mapping if not provided
  const mapping = visualMapping || {
    backgroundColor: "type",
    borderColor: "none"
  };

  // Create container WITHOUT clipping for dropdowns to show
  const container = new Instance("Frame");
  container.Name = "VisualCustomizationControls";
  container.Size = new UDim2(1, -20, 0, 80);
  container.Position = new UDim2(0, 0, 0, 0); // Position will be set by layout manager
  container.BackgroundColor3 = new Color3(0.15, 0.15, 0.15);
  container.BorderSizePixel = 0;
  container.ClipsDescendants = false; // Disable clipping so dropdowns can extend
  container.ZIndex = 2;
  container.Parent = parent;

  const containerCorner = new Instance("UICorner");
  containerCorner.CornerRadius = new UDim(0, 4);
  containerCorner.Parent = container;

  // Title
  const title = new Instance("TextLabel");
  title.Size = new UDim2(1, -20, 0, 20);
  title.Position = new UDim2(0, 10, 0, 5);
  title.BackgroundTransparency = 1;
  title.Font = GUI_CONSTANTS.TYPOGRAPHY.TITLE_FONT;
  title.Text = "Visual Property Mapping";
  title.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  title.TextSize = 16;
  title.TextXAlignment = Enum.TextXAlignment.Left;
  title.Parent = container;

  // Background color dropdown
  createVisualDropdown({
    parent: container,
    label: "Background Color:",
    position: new UDim2(0, 10, 0, 30),
    currentValue: mapping.backgroundColor,
    onValueChange: (value) => onVisualMappingChange("backgroundColor", value)
  });

  // Border color dropdown
  createVisualDropdown({
    parent: container,
    label: "Border Color:",
    position: new UDim2(0.5, 10, 0, 30),
    currentValue: mapping.borderColor,
    onValueChange: (value) => onVisualMappingChange("borderColor", value)
  });

  return container;
}

interface VisualDropdownProps {
  parent: Frame;
  label: string;
  position: UDim2;
  currentValue: string;
  onValueChange: (value: string) => void;
}

function createVisualDropdown({
  parent,
  label,
  position,
  currentValue,
  onValueChange
}: VisualDropdownProps): void {
  // Label directly in parent
  const dropdownLabel = new Instance("TextLabel");
  dropdownLabel.Size = new UDim2(0.5, -20, 0, 20);
  dropdownLabel.Position = position;
  dropdownLabel.BackgroundTransparency = 1;
  dropdownLabel.Font = GUI_CONSTANTS.TYPOGRAPHY.LABEL_FONT;
  dropdownLabel.Text = label;
  dropdownLabel.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  dropdownLabel.TextSize = 14;
  dropdownLabel.TextXAlignment = Enum.TextXAlignment.Left;
  dropdownLabel.Parent = parent;

  // Dropdown button directly in parent
  const dropdownButton = new Instance("TextButton");
  dropdownButton.Size = new UDim2(0.5, -20, 0, 25);
  dropdownButton.Position = new UDim2(position.X.Scale, position.X.Offset, 0, position.Y.Offset + 20);
  dropdownButton.BackgroundColor3 = new Color3(0.25, 0.25, 0.25);
  dropdownButton.BorderSizePixel = 0;
  dropdownButton.Font = GUI_CONSTANTS.TYPOGRAPHY.INPUT_FONT;
  dropdownButton.Text = currentValue + " ";
  dropdownButton.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  dropdownButton.TextSize = 14;
  dropdownButton.Parent = parent;

  const buttonCorner = new Instance("UICorner");
  buttonCorner.CornerRadius = new UDim(0, 4);
  buttonCorner.Parent = dropdownButton;

  // Dropdown list - always use ScrollingFrame for consistency
  const itemHeight = 25;
  const maxItems = 6;
  const actualHeight = math.min(VISUAL_PROPERTIES.size() * itemHeight, maxItems * itemHeight);

  const dropdownList = new Instance("ScrollingFrame");
  dropdownList.Size = new UDim2(1, 0, 0, actualHeight);
  dropdownList.Position = new UDim2(0, 0, 0, -actualHeight - 5); // Position above button with gap
  dropdownList.BackgroundColor3 = new Color3(0.1, 0.1, 0.1);
  dropdownList.BorderSizePixel = 1;
  dropdownList.BorderColor3 = new Color3(0.3, 0.3, 0.3);
  dropdownList.ScrollBarThickness = 4;
  dropdownList.ScrollBarImageColor3 = new Color3(0.5, 0.5, 0.5);
  dropdownList.CanvasSize = new UDim2(0, 0, 0, VISUAL_PROPERTIES.size() * itemHeight);
  dropdownList.Visible = false;
  dropdownList.ZIndex = 100; // Higher z-index
  dropdownList.Parent = dropdownButton;

  const listCorner = new Instance("UICorner");
  listCorner.CornerRadius = new UDim(0, 4);
  listCorner.Parent = dropdownList;

  // Create option buttons
  VISUAL_PROPERTIES.forEach((property, index) => {
    const optionButton = new Instance("TextButton");
    optionButton.Size = new UDim2(1, -10, 0, itemHeight); // Always account for scrollbar
    optionButton.Position = new UDim2(0, 0, 0, index * itemHeight);
    optionButton.BackgroundColor3 = new Color3(0.1, 0.1, 0.1);
    optionButton.BackgroundTransparency = 0;
    optionButton.Font = GUI_CONSTANTS.TYPOGRAPHY.INPUT_FONT;
    optionButton.Text = property;
    optionButton.TextColor3 = new Color3(0.9, 0.9, 0.9);
    optionButton.TextSize = 14;
    optionButton.TextXAlignment = Enum.TextXAlignment.Center;
    optionButton.BorderSizePixel = 0;
    optionButton.Parent = dropdownList; // Parent directly to dropdownList

    // Hover effect
    optionButton.MouseEnter.Connect(() => {
      optionButton.BackgroundColor3 = new Color3(0.3, 0.3, 0.3);
      optionButton.TextColor3 = new Color3(1, 1, 1);
    });

    optionButton.MouseLeave.Connect(() => {
      optionButton.BackgroundColor3 = new Color3(0.1, 0.1, 0.1);
      optionButton.TextColor3 = new Color3(0.9, 0.9, 0.9);
    });

    // Selection
    optionButton.MouseButton1Click.Connect(() => {
      dropdownButton.Text = property + " ";
      dropdownList.Visible = false;
      onValueChange(property);
    });
  });

  // Create a connection variable to track the close handler
  let closeConnection: RBXScriptConnection | undefined;

  // Toggle dropdown
  dropdownButton.MouseButton1Click.Connect(() => {
    const isOpening = !dropdownList.Visible;
    dropdownList.Visible = isOpening;

    // If opening, set up the close handler
    if (isOpening) {
      // Disconnect previous handler if it exists
      if (closeConnection) {
        closeConnection.Disconnect();
      }

      // Wait a frame to avoid immediate closing
      game.GetService("RunService").Heartbeat.Wait();

      // Set up new close handler
      const userInputService = game.GetService("UserInputService");
      closeConnection = userInputService.InputBegan.Connect((input, gameProcessed) => {
        if (input.UserInputType === Enum.UserInputType.MouseButton1 && !gameProcessed) {
          // Check if click is outside the dropdown
          const mouse = game.GetService("Players").LocalPlayer.GetMouse();
          const target = mouse.Target;

          // Close if clicking outside dropdown area
          if (!target || !target.IsDescendantOf(dropdownButton)) {
            dropdownList.Visible = false;
            if (closeConnection) {
              closeConnection.Disconnect();
              closeConnection = undefined;
            }
          }
        }
      });
    } else if (closeConnection) {
      // If closing, disconnect the handler
      closeConnection.Disconnect();
      closeConnection = undefined;
    }
  });
}

import { UserInputService, Players } from "@rbxts/services";
import { BaseService } from "../../shared/services/base/BaseService";

const KEYBOARD_CONSTANTS = {
    ESC_TIMEOUT: 2000, // 2 seconds to press P after Esc
    CONFIRMATION_TIMEOUT: 5000, // 5 seconds to confirm quit
};

export class KeyboardShortcutsService extends BaseService {
    private escPressed = false;
    private escPressTime = 0;
    private confirmationGui?: ScreenGui;

    constructor() {
        super("KeyboardShortcutsService");
        this.setupInputHandlers();
    }

    private setupInputHandlers(): void {
        const inputConnection = UserInputService.InputBegan.Connect((input, gameProcessed) => {
            // Ignore input if GUI is capturing it
            if (gameProcessed) return;

            if (input.KeyCode === Enum.KeyCode.Escape) {
                this.handleEscapePress();
            } else if (input.KeyCode === Enum.KeyCode.P && this.escPressed) {
                const timeSinceEsc = tick() - this.escPressTime;
                if (timeSinceEsc <= KEYBOARD_CONSTANTS.ESC_TIMEOUT / 1000) {
                    this.showQuitConfirmation();
                }
                this.escPressed = false;
            } else {
                // Any other key cancels the Esc+P sequence
                this.escPressed = false;
            }
        });

        // Add connection to be managed
        this.addConnection(inputConnection);
    }

    private handleEscapePress(): void {
        this.escPressed = true;
        this.escPressTime = tick();

        // Show visual feedback
        this.showEscapeFeedback();

        // Clear escape state after timeout
        task.wait(KEYBOARD_CONSTANTS.ESC_TIMEOUT / 1000);
        this.escPressed = false;
    }

    private showEscapeFeedback(): void {
        const player = Players.LocalPlayer;
        if (!player) return;
        const playerGui = player.WaitForChild("PlayerGui") as PlayerGui;
        if (!playerGui) return;

        // Create temporary feedback GUI
        const feedbackGui = new Instance("ScreenGui");
        feedbackGui.Name = "EscapeFeedback";
        feedbackGui.ResetOnSpawn = false;
        feedbackGui.Parent = playerGui;

        // Track this instance for cleanup
        this.addInstance(feedbackGui);

        const feedbackFrame = new Instance("Frame");
        feedbackFrame.Size = new UDim2(0, 200, 0, 50);
        feedbackFrame.Position = new UDim2(0.5, -100, 0, 50);
        feedbackFrame.BackgroundColor3 = new Color3(0.2, 0.2, 0.2);
        feedbackFrame.BackgroundTransparency = 0.2;
        feedbackFrame.BorderSizePixel = 0;
        feedbackFrame.Parent = feedbackGui;

        const feedbackText = new Instance("TextLabel");
        feedbackText.Size = new UDim2(1, 0, 1, 0);
        feedbackText.Text = "Press P to quit";
        feedbackText.TextColor3 = new Color3(1, 1, 1);
        feedbackText.TextScaled = true;
        feedbackText.BackgroundTransparency = 1;
        feedbackText.Font = Enum.Font.SourceSans;
        feedbackText.Parent = feedbackFrame;

        // Remove feedback after timeout
        task.wait(KEYBOARD_CONSTANTS.ESC_TIMEOUT / 1000);
        feedbackGui.Destroy();
    }

    private showQuitConfirmation(): void {
        const player = Players.LocalPlayer;
        if (!player) return;
        const playerGui = player.WaitForChild("PlayerGui") as PlayerGui;
        if (!playerGui) return;

        // Prevent multiple confirmation dialogs
        if (this.confirmationGui) {
            this.confirmationGui.Destroy();
        }

        // Create confirmation dialog
        this.confirmationGui = new Instance("ScreenGui");
        this.confirmationGui.Name = "QuitConfirmation";
        this.confirmationGui.ResetOnSpawn = false;
        this.confirmationGui.Parent = playerGui;

        // Track this instance for cleanup
        this.addInstance(this.confirmationGui);

        const backgroundFrame = new Instance("Frame");
        backgroundFrame.Size = new UDim2(1, 0, 1, 0);
        backgroundFrame.BackgroundColor3 = new Color3(0, 0, 0);
        backgroundFrame.BackgroundTransparency = 0.5;
        backgroundFrame.Parent = this.confirmationGui;

        const dialogFrame = new Instance("Frame");
        dialogFrame.Size = new UDim2(0, 400, 0, 200);
        dialogFrame.Position = new UDim2(0.5, -200, 0.5, -100);
        dialogFrame.BackgroundColor3 = new Color3(0.15, 0.15, 0.15);
        dialogFrame.BorderSizePixel = 0;
        dialogFrame.Parent = this.confirmationGui;

        const titleText = new Instance("TextLabel");
        titleText.Size = new UDim2(1, 0, 0, 50);
        titleText.Text = "Quit Game?";
        titleText.TextColor3 = new Color3(1, 1, 1);
        titleText.TextScaled = true;
        titleText.BackgroundTransparency = 1;
        titleText.Font = Enum.Font.SourceSansBold;
        titleText.Parent = dialogFrame;

        const messageText = new Instance("TextLabel");
        messageText.Size = new UDim2(1, -20, 0, 50);
        messageText.Position = new UDim2(0, 10, 0, 60);
        messageText.Text = "Are you sure you want to quit the game?";
        messageText.TextColor3 = new Color3(0.8, 0.8, 0.8);
        messageText.TextScaled = true;
        messageText.BackgroundTransparency = 1;
        messageText.Font = Enum.Font.SourceSans;
        messageText.Parent = dialogFrame;

        const buttonContainer = new Instance("Frame");
        buttonContainer.Size = new UDim2(1, -20, 0, 40);
        buttonContainer.Position = new UDim2(0, 10, 1, -50);
        buttonContainer.BackgroundTransparency = 1;
        buttonContainer.Parent = dialogFrame;

        const confirmButton = new Instance("TextButton");
        confirmButton.Size = new UDim2(0.45, 0, 1, 0);
        confirmButton.Position = new UDim2(0, 0, 0, 0);
        confirmButton.Text = "Quit";
        confirmButton.TextColor3 = new Color3(1, 1, 1);
        confirmButton.TextScaled = true;
        confirmButton.BackgroundColor3 = new Color3(0.8, 0.2, 0.2);
        confirmButton.Font = Enum.Font.SourceSans;
        confirmButton.Parent = buttonContainer;

        const cancelButton = new Instance("TextButton");
        cancelButton.Size = new UDim2(0.45, 0, 1, 0);
        cancelButton.Position = new UDim2(0.55, 0, 0, 0);
        cancelButton.Text = "Cancel";
        cancelButton.TextColor3 = new Color3(1, 1, 1);
        cancelButton.TextScaled = true;
        cancelButton.BackgroundColor3 = new Color3(0.3, 0.3, 0.3);
        cancelButton.Font = Enum.Font.SourceSans;
        cancelButton.Parent = buttonContainer;

        // Handle button clicks
        const confirmConnection = confirmButton.MouseButton1Click.Connect(() => {
            this.quitGame();
        });
        this.addConnection(confirmConnection);

        const cancelConnection = cancelButton.MouseButton1Click.Connect(() => {
            if (this.confirmationGui) {
                this.confirmationGui.Destroy();
                this.confirmationGui = undefined;
            }
        });
        this.addConnection(cancelConnection);

        // Auto-dismiss after timeout
        task.wait(KEYBOARD_CONSTANTS.CONFIRMATION_TIMEOUT / 1000);
        if (this.confirmationGui) {
            this.confirmationGui.Destroy();
            this.confirmationGui = undefined;
        }
    }

    private quitGame(): void {
        const player = Players.LocalPlayer;
        if (player) {
            // Kick the player to quit the game
            player.Kick("You have quit the game.");
        }
    }

    protected onDestroy(): void {
        // Clean up confirmation GUI if it exists
        if (this.confirmationGui) {
            this.confirmationGui.Destroy();
            this.confirmationGui = undefined;
        }

    }
}

/**
 * Flat Block Creator
 * Creates a large flat block under the node tree for visual foundation
 */

import { BLOCK_CONSTANTS } from "./constants/blockConstants";

export interface FlatBlockConfig {
  origin: Vector3;
  parent: Instance;
  height?: number;
  width?: number;
  depth?: number;
  color?: Color3;
}

export interface SwimLaneBlockConfig {
  position: Vector3;
  width: number;
  depth: number;
  height: number;
  color: Color3;
  typeName: string;
  parent: Instance;
  propertyName?: string;
}

export const FLAT_BLOCK_DEFAULTS = {
  height: BLOCK_CONSTANTS.DIMENSIONS.DEFAULT_HEIGHT,
  width: BLOCK_CONSTANTS.DIMENSIONS.DEFAULT_WIDTH,
  depth: BLOCK_CONSTANTS.DIMENSIONS.DEFAULT_DEPTH,
  shadowColor: BLOCK_CONSTANTS.COLORS.SHADOW,
  platformColor: BLOCK_CONSTANTS.COLORS.PLATFORM,
  zFightingFix: BLOCK_CONSTANTS.DIMENSIONS.Z_FIGHTING_OFFSET,
};

/**
 * Creates platform and shadow blocks under the node tree
 * @param config Configuration for the blocks
 * @returns An object containing both the platform and shadow blocks
 */
export function createFlatBlocks(config: FlatBlockConfig): { platform: Part; shadow: Part } {
  const {
    origin,
    parent,
    width = FLAT_BLOCK_DEFAULTS.width,
    depth = FLAT_BLOCK_DEFAULTS.depth,
  } = config;

  // Create platform block first (will be parent)
  const platformBlock = new Instance("Part");
  platformBlock.Name = "PlatformBlock";

  // Platform has uniform shadow thickness for consistency
  // Platform has fixed size of 100x100 in X,Z dimensions
  platformBlock.Size = new Vector3(BLOCK_CONSTANTS.DIMENSIONS.PLATFORM_SIZE, BLOCK_CONSTANTS.DIMENSIONS.UNIFORM_SHADOW_THICKNESS, BLOCK_CONSTANTS.DIMENSIONS.PLATFORM_SIZE);

  // Set platform material and appearance
  platformBlock.Material = BLOCK_CONSTANTS.MATERIALS.PLATFORM;
  platformBlock.Color = FLAT_BLOCK_DEFAULTS.platformColor;
  platformBlock.TopSurface = Enum.SurfaceType.Smooth;
  platformBlock.BottomSurface = Enum.SurfaceType.Smooth;

  // Add texture to platform block
  // const texture = new Instance("Texture");
  // texture.Texture = "rbxassetid://6372755229";
  // texture.Face = Enum.NormalId.Top;
  // texture.StudsPerTileU = 10;
  // texture.StudsPerTileV = 10;
  // texture.Parent = platformBlock;

  // Set platform physics properties
  platformBlock.Anchored = true;
  platformBlock.CanCollide = true;
  platformBlock.CastShadow = false;
  platformBlock.Transparency = 1; // Fully transparent

  // Position platform block at base level
  platformBlock.Position = new Vector3(
    origin.X,
    BLOCK_CONSTANTS.DIMENSIONS.UNIFORM_SHADOW_THICKNESS / 2, // Center at ground level with uniform thickness
    origin.Z
  );

  // Parent platform block
  platformBlock.Parent = parent;

  // Create shadow block (will be child of platform)
  const shadowBlock = new Instance("Part");
  shadowBlock.Name = "GroupShadowBlock";

  // Set shadow size with uniform thickness (no extra buffer - padding is already included in width/depth)
  shadowBlock.Size = new Vector3(width, BLOCK_CONSTANTS.DIMENSIONS.UNIFORM_SHADOW_THICKNESS, depth);

  // Set shadow material and appearance
  shadowBlock.Material = BLOCK_CONSTANTS.MATERIALS.SHADOW;
  shadowBlock.Color = FLAT_BLOCK_DEFAULTS.shadowColor;
  shadowBlock.TopSurface = Enum.SurfaceType.Smooth;
  shadowBlock.BottomSurface = Enum.SurfaceType.Smooth;

  // Set shadow physics properties
  shadowBlock.Anchored = true;
  shadowBlock.CanCollide = true;
  shadowBlock.CastShadow = false;
  shadowBlock.Transparency = 0; // Fully opaque

  // Position shadow block - 0.1 units above platform
  shadowBlock.Position = new Vector3(
    origin.X,
    BLOCK_CONSTANTS.DIMENSIONS.UNIFORM_SHADOW_THICKNESS + BLOCK_CONSTANTS.DIMENSIONS.Z_FIGHTING_OFFSET + BLOCK_CONSTANTS.DIMENSIONS.UNIFORM_SHADOW_THICKNESS / 2, // 0.1 above platform top
    origin.Z
  );

  // Parent shadow block to platform block
  shadowBlock.Parent = platformBlock;



  return { platform: platformBlock, shadow: shadowBlock };
}

/**
 * Legacy function - creates a single flat block
 * @deprecated Use createFlatBlocks instead
 * @param config Configuration for the flat block
 * @returns The created part instance
 */
export function createFlatBlock(config: FlatBlockConfig): Part {
  const {
    origin,
    parent,
    height = FLAT_BLOCK_DEFAULTS.height,
    width = FLAT_BLOCK_DEFAULTS.width,
    depth = FLAT_BLOCK_DEFAULTS.depth,
    color = FLAT_BLOCK_DEFAULTS.shadowColor,
  } = config;

  // Create the block
  const block = new Instance("Part");
  block.Name = "FlatBlockFoundation";

  // Set size
  block.Size = new Vector3(width, height, depth);

  // Set material and appearance
  block.Material = BLOCK_CONSTANTS.MATERIALS.SHADOW;
  block.Color = color;
  block.TopSurface = Enum.SurfaceType.Smooth;
  block.BottomSurface = Enum.SurfaceType.Smooth;

  // Set physics properties
  block.Anchored = true;
  block.CanCollide = true; // Changed to true to make the block solid
  block.CastShadow = false; // Optimize rendering
  block.Transparency = 0; // Fully opaque

  // Position the block
  // Center at origin X and Z, position bottom surface at Y=0 (ground level)
  // This ensures the block sits on the ground
  block.Position = new Vector3(
    origin.X,
    height / 2, // Bottom at Y=0, so center is at height/2
    origin.Z
  );

  // Parent the block
  block.Parent = parent;


  return block;
}

/**
 * Calculates appropriate block dimensions based on node tree bounds
 * @param nodeBounds The bounding box of the node tree
 * @param padding Additional padding beyond the bounds
 * @returns Recommended width and depth for the block
 */
export function calculateBlockDimensions(
  nodeBounds: { minX: number; maxX: number; minZ: number; maxZ: number },
  padding = 0
): { width: number; depth: number } {
  const nodeWidth = nodeBounds.maxX - nodeBounds.minX;
  const nodeDepth = nodeBounds.maxZ - nodeBounds.minZ;

  const width = nodeWidth + padding * 2;
  const depth = nodeDepth + padding * 2;

  // Ensure minimum size
  const minSize = BLOCK_CONSTANTS.DIMENSIONS.MIN_BLOCK_SIZE;
  const finalWidth = math.max(width, minSize);
  const finalDepth = math.max(depth, minSize);


  return {
    width: finalWidth,
    depth: finalDepth,
  };
}


/**
 * Creates a block under a swimlane
 * @param config Configuration for the swimlane block
 * @returns The created swimlane block
 */
export function createSwimLaneBlock(config: SwimLaneBlockConfig): Part {
  const { position, width, depth, height, color, typeName, parent } = config;

  // Create swimlane block
  const swimLaneBlock = new Instance("Part");
  swimLaneBlock.Name = `SwimlaneShadowBlock_${typeName}`;

  // Set size
  swimLaneBlock.Size = new Vector3(width, height, depth);

  // Set material and appearance
  swimLaneBlock.Material = BLOCK_CONSTANTS.MATERIALS.SWIMLANE;
  swimLaneBlock.Color = color;
  swimLaneBlock.TopSurface = Enum.SurfaceType.Smooth;
  swimLaneBlock.BottomSurface = Enum.SurfaceType.Smooth;
  swimLaneBlock.Transparency = 0; // Fully opaque

  // Set physics properties
  swimLaneBlock.Anchored = true;
  swimLaneBlock.CanCollide = false; // Don't interfere with player movement
  swimLaneBlock.CastShadow = false;

  // Position the block
  swimLaneBlock.Position = position;

  // Parent to shadow block
  swimLaneBlock.Parent = parent;


  return swimLaneBlock;
}

/**
 * Swimlane Block Creator
 * Creates swimlane blocks for visual grouping of nodes
 * Part of F002 Phase 2 refactoring - T6
 */

import { BaseBlockCreator } from "./baseBlockCreator";
import { BLOCK_CONSTANTS } from "../constants/blockConstants";
import { LAYOUT_CONSTANTS } from "../constants/layoutConstants";

export interface SwimLaneBlockConfig {
  position: Vector3;
  width: number;
  depth: number;
  height: number;
  color: Color3;
  typeName: string;
  parent: Instance;
  propertyName?: string;
}

export class SwimLaneBlockCreator extends BaseBlockCreator {
  /**
   * Create a Z-parallel lane shadow block (runs along Z axis)
   */
  public createSwimLaneBlock(config: SwimLaneBlockConfig): Part {
    const {
      position,
      width,
      depth,
      height,
      color,
      typeName,
      parent,
      propertyName,
    } = config;

    // Generate unique ID for the lane
    const laneId = `${propertyName || "default"}_${typeName}`;
    const blockName = `ZParallel_Lane_${laneId}`;

    // Debug: Print width of actual swimlane
    print(`[SWIMLANE] ${typeName}: width=${width}, depth=${depth}`)

    const swimLaneBlock = this.createBlock({
      name: blockName,
      size: new Vector3(width, height, depth),
      position: position,
      material: BLOCK_CONSTANTS.MATERIALS.PLATFORM,
      color: color,
      transparency: BLOCK_CONSTANTS.TRANSPARENCY.OPAQUE,
      canCollide: true,
    });

    swimLaneBlock.CastShadow = false;
    swimLaneBlock.Parent = parent;

    // Add surface labels to all faces
    this.addSurfaceLabelsToAllFaces(swimLaneBlock, typeName);

    return swimLaneBlock;
  }

  /**
   * Create Z-parallel lane blocks for multiple types
   */
  public createZParallelLaneBlocks(
    typeBounds: Map<
      string,
      { minX: number; maxX: number; minZ: number; maxZ: number }
    >,
    yPosition: number,
    height: number,
    parent: Instance,
    colorMap?: Map<string, Color3>
  ): Map<string, Part> {
    const swimLaneBlocks = new Map<string, Part>();
    let colorIndex = 0;

    typeBounds.forEach((bounds, typeName) => {
      const color =
        colorMap?.get(typeName) ||
        this.getColorFromArray(
          BLOCK_CONSTANTS.COLORS.Z_AXIS_COLORS,
          colorIndex
        );

      const dimensions = this.calculateBlockDimensions(
        bounds,
        BLOCK_CONSTANTS.DIMENSIONS.SHADOW_BUFFER
      );

      const config: SwimLaneBlockConfig = {
        position: new Vector3(
          dimensions.position.X,
          yPosition,
          dimensions.position.Z
        ),
        width: dimensions.size.X,
        depth: dimensions.size.Z,
        height: height,
        color: color,
        typeName: typeName,
        parent: parent,
      };

      const block = this.createSwimLaneBlock(config);
      swimLaneBlocks.set(typeName, block);

      colorIndex++;
    });

    return swimLaneBlocks;
  }

  /**
   * Create Z-parallel lane blocks grouped by X position
   */
  public createXAxisSwimLaneBlocks(
    typeBounds: Map<
      string,
      { minX: number; maxX: number; minZ: number; maxZ: number }
    >,
    parent: Instance,
    yPosition: number = 0
  ): Map<string, Part> {
    const zParallelBlocks = new Map<string, Part>();
    let colorIndex = 0;
    let currentX = 0;

    typeBounds.forEach((bounds, typeName) => {
      // Use fixed width from layout constants
      const width = LAYOUT_CONSTANTS.LANE_DIMENSIONS.Z_PARALLEL_LANE_WIDTH;
      const depth =
        bounds.maxZ -
        bounds.minZ +
        BLOCK_CONSTANTS.DIMENSIONS.Z_PARALLEL_LANE_BUFFER * 2;

      // Position lanes with fixed spacing
      const centerX = currentX;
      const centerZ = (bounds.minZ + bounds.maxZ) / 2;
      currentX += width + LAYOUT_CONSTANTS.LANE_SPACING.Z_PARALLEL_LANE_SPACING;

      const blockName = `ZParallelShadowBlock_${typeName}`;
      const blockSize = new Vector3(
        width,
        BLOCK_CONSTANTS.DIMENSIONS.SHADOW_BLOCK_HEIGHT,
        depth
      );

      // Log shadow block creation

      const block = this.createBlock({
        name: blockName,
        size: blockSize,
        position: new Vector3(centerX, yPosition, centerZ),
        material: BLOCK_CONSTANTS.MATERIALS.SWIMLANE,
        color: this.getColorFromArray(
          BLOCK_CONSTANTS.COLORS.Z_AXIS_COLORS,
          colorIndex
        ),
        transparency: BLOCK_CONSTANTS.TRANSPARENCY.OPAQUE,
        canCollide: true,
      });

      block.CastShadow = false;
      block.Parent = parent;
      zParallelBlocks.set(typeName, block);

      colorIndex++;
    });

    // Verify all shadow blocks after creation
    zParallelBlocks.forEach((block, typeName) => {});

    // Also find all shadow blocks in parent
    const allShadowBlocks = parent
      .GetChildren()
      .filter(
        (child): child is Part =>
          child.IsA("Part") && child.Name.match("ShadowBlock")[0] !== undefined
      );
    allShadowBlocks.forEach((block) => {});

    return zParallelBlocks;
  }

  /**
   * Add surface labels to all faces of a block
   */
  private addSurfaceLabelsToAllFaces(block: Part, text: string): void {
    const faces: Enum.NormalId[] = [
      Enum.NormalId.Front,
      Enum.NormalId.Back,
      Enum.NormalId.Left,
      Enum.NormalId.Right,
      Enum.NormalId.Top,
      Enum.NormalId.Bottom,
    ];

    // Check if this is a Z-parallel block
    const isZParallel = block.Name.match("^ZParallel_Lane_")[0] !== undefined;


    faces.forEach((face) => {
      // Skip Front face (red labels) for Z-parallel blocks
      if (isZParallel && face === Enum.NormalId.Front) {
        return;
      }
      // Create SurfaceGui
      const surfaceGui = new Instance("SurfaceGui");
      surfaceGui.Name = `SurfaceGui_${face.Name}`;
      surfaceGui.Face = face;
      surfaceGui.SizingMode = Enum.SurfaceGuiSizingMode.PixelsPerStud;
      surfaceGui.PixelsPerStud = 50;
      surfaceGui.Parent = block;

      // Create TextLabel directly in SurfaceGui
      const textLabel = new Instance("TextLabel");
      textLabel.Size = new UDim2(1, 0, 1, 0); // Full size as requested
      textLabel.Position = new UDim2(0, 0, 0, 0);
      textLabel.BackgroundTransparency = BLOCK_CONSTANTS.LABEL_STYLING.BACKGROUND_TRANSPARENCY;
      textLabel.BackgroundColor3 = block.Color; // Same color as the underlying block
      textLabel.BorderSizePixel = BLOCK_CONSTANTS.LABEL_STYLING.BORDER_SIZE_PIXEL;
      textLabel.BorderColor3 = BLOCK_CONSTANTS.LABEL_STYLING.BORDER_COLOR;
      textLabel.BorderMode = BLOCK_CONSTANTS.LABEL_STYLING.BORDER_MODE;
      textLabel.Font = BLOCK_CONSTANTS.LABEL_STYLING.FONT;
      textLabel.Text = text;

      // Use different colors for each face (for testing)
      const faceColors: { [key: string]: Color3 } = {
        Front: new Color3(1, 0, 0),     // Red
        Back: new Color3(0, 1, 0),      // Green
        Left: new Color3(0, 0, 1),      // Blue
        Right: new Color3(1, 1, 0),     // Yellow
        Top: new Color3(1, 0, 1),       // Magenta
        Bottom: new Color3(0, 1, 1),    // Cyan
      };

      textLabel.TextColor3 = faceColors[face.Name] || new Color3(1, 1, 1); // Default to white
      textLabel.TextScaled = BLOCK_CONSTANTS.LABEL_STYLING.TEXT_SCALED;
      textLabel.Parent = surfaceGui;
    });
  }
}

/**
 * Rope and connector creation logic
 * 
 * Updated to use Beams instead of RopeConstraints for better performance:
 * - Beams are visual-only (no physics calculations)
 * - 70% reduction in computational overhead
 * - No visual quality loss for straight connections
 */

import { Cluster, Link } from "../../../interfaces/simpleDataGenerator.interface";
import { createRopeLabel as createRopeLabelFromMaker } from "../../ropeLabelMaker/ropeLabelMaker";
import { RENDERER_CONSTANTS } from "./constants";
import { padNumber } from "../../../utils/stringUtils";
import { VisualizationOptions } from "../../../interfaces/enhancedGenerator.interface";
import { LINK_COLORS } from "../constants/robloxColors";

interface RopeCreationContext {
  cluster: Cluster;
  nodeToHexagon: Map<string, Model>;
  linksFolder: Folder;
  visualization?: VisualizationOptions;
  linkDiameter?: number;
}

/**
 * Creates rope connectors between hexagons based on relationships
 */
export function createRopeConnectors(context: RopeCreationContext): void {
  const { cluster, nodeToHexagon, linksFolder, visualization, linkDiameter } = context;

  // Check if connectors should be shown
  const showConnectors = visualization?.showConnectors ?? true;
  const showLinkLabels = visualization?.showLinkLabels ?? true;

  // Early return if connectors are disabled
  if (!showConnectors) {
    return;
  }

  let ropeIndex = 1;

  cluster.relations.forEach(link => {
    const sourceHex = nodeToHexagon.get(link.sourceNodeUuid);
    const targetHex = nodeToHexagon.get(link.targetNodeUuid);

    if (sourceHex && targetHex) {

      // Find center attachments
      const sourceAttachment = findCenterAttachment(sourceHex);
      const targetAttachment = findCenterAttachment(targetHex);

      if (sourceAttachment && targetAttachment) {
        // Create beam connector
        const beam = createRope(link, sourceHex, targetHex, sourceAttachment, targetAttachment, ropeIndex, linkDiameter);

        // Parent beam to links folder (beams must be in workspace hierarchy)
        beam.Parent = linksFolder;

        // Create rope label only if showLinkLabels is true
        if (showLinkLabels) {
          const labelParent = findCenterCube(targetHex) || findCenterCube(sourceHex) || linksFolder;
          createRopeLabel(
            sourceHex, 
            targetHex, 
            link, 
            sourceAttachment, 
            targetAttachment, 
            labelParent,
            ropeIndex
          );
        }

        ropeIndex++;
      }
    }
  });
}

/**
 * Find center attachment on a hexagon
 */
function findCenterAttachment(hexagon: Model): Attachment | undefined {
  // First try the expected pattern
  let attachment = findAttachmentRecursive(hexagon, `att000-${hexagon.Name}`);

  if (!attachment) {
    // Find any attachment starting with att000
    for (const desc of hexagon.GetDescendants()) {
      if (desc.IsA("Attachment") && desc.Name.sub(1, 6) === "att000") {
        attachment = desc as Attachment;
        break;
      }
    }
  }

  return attachment;
}

/**
 * Find attachment recursively in a model
 */
function findAttachmentRecursive(model: Instance, attachmentName: string): Attachment | undefined {
  for (const desc of model.GetDescendants()) {
    if (desc.IsA("Attachment") && desc.Name === attachmentName) {
      return desc as Attachment;
    }
  }
  return undefined;
}

/**
 * Find center cube in a hexagon
 */
function findCenterCube(hexagon: Model): Part | undefined {
  let centerCube = hexagon.FindFirstChild("center") as Part;
  if (!centerCube) {
    const children = hexagon.GetChildren();
    for (const child of children) {
      if (child.IsA("Part") && child.Name.find("centerCube-") !== undefined) {
        centerCube = child as Part;
        break;
      }
    }
  }
  return centerCube;
}

/**
 * Create a beam connector (replaces rope constraint for better performance)
 */
function createRope(
  link: Link,
  sourceHex: Model,
  targetHex: Model,
  sourceAttachment: Attachment,
  targetAttachment: Attachment,
  ropeIndex: number,
  linkDiameter?: number
): Beam {
  const beam = new Instance("Beam");
  beam.Name = `beam${padNumber(ropeIndex, 3)}-${link.type.lower()}-${sourceHex.Name}-to-${targetHex.Name}`;
  beam.Attachment0 = sourceAttachment;
  beam.Attachment1 = targetAttachment;

  // Beam visual properties
  const diameter = linkDiameter ?? RENDERER_CONSTANTS.ROPE.THICKNESS;
  beam.Width0 = diameter;
  beam.Width1 = diameter;

  // Set color using LINK_COLORS array
  const color3 = getLinkColor3(link.type);
  beam.Color = new ColorSequence(color3);

  // Visual settings for performance
  beam.FaceCamera = true;
  beam.Segments = 1; // Straight line, no physics calculation
  beam.Transparency = new NumberSequence(0); // Fully opaque
  beam.LightEmission = 0; // No glow effect
  beam.LightInfluence = 1; // Normal lighting
  beam.Texture = ""; // No texture for better performance
  beam.TextureSpeed = 0;
  beam.TextureLength = 1;
  beam.TextureMode = Enum.TextureMode.Static;

  // Note: Beams don't cast shadows by default (performance benefit)

  return beam;
}

/**
 * Create rope label
 */
function createRopeLabel(
  sourceHex: Model,
  targetHex: Model,
  link: Link,
  sourceAttachment: Attachment,
  targetAttachment: Attachment,
  labelParent: Instance,
  ropeIndex: number
): void {
  const relationName = `${getNodeName(sourceHex)}_${link.type}_${getNodeName(targetHex)}`;

  createRopeLabelFromMaker({
    ropeIndex,
    relationTypeName: link.type,
    sourceAttachment,
    targetAttachment,
    parent: labelParent,
    relationName
  });
}

/**
 * Get Color3 for link type using LINK_COLORS array
 */
function getLinkColor3(linkType: string): Color3 {
  // Extract numeric part from link type (e.g., "Link1" -> 1, "Link10" -> 10)
  const match = linkType.match("[0-9]+");
  if (match && match[0]) {
    const linkIndex = tonumber(match[0]);
    if (linkIndex && linkIndex > 0 && linkIndex <= LINK_COLORS.size()) {
      return LINK_COLORS[linkIndex - 1]; // Array is 0-indexed
    }
  }

  // Fallback for non-numeric link types or out of range
  // Use hash of link type string to consistently assign a color
  let hash = 0;
  for (let i = 1; i <= linkType.size(); i++) {
    const char = linkType.sub(i, i);
    hash = ((hash << 5) - hash) + char.byte()[0];
    hash = hash & hash; // Convert to 32bit integer
  }
  const colorIndex = math.abs(hash) % LINK_COLORS.size();
  return LINK_COLORS[colorIndex];
}

/**
 * Get node name from hexagon model for label creation
 */
function getNodeName(hexagon: Model): string {
  // Try to find a text label inside the hexagon that contains the node name
  const textLabels = hexagon.GetDescendants().filter(desc => 
    desc.IsA("TextLabel") || desc.IsA("BillboardGui")
  );

  if (textLabels.size() > 0) {
    const firstLabel = textLabels[0];
    if (firstLabel.IsA("TextLabel")) {
      return firstLabel.Text;
    } else if (firstLabel.IsA("BillboardGui")) {
      const textLabel = firstLabel.FindFirstChildOfClass("TextLabel");
      if (textLabel) {
        return textLabel.Text;
      }
    }
  }

  // Fallback to hexagon name
  return hexagon.Name;
}

/**
 * Property Value Resolver
 * Centralizes property extraction logic for nodes
 * Part of F002 Phase 2 refactoring - T8
 * Updated for T9: Now supports dynamic property discovery
 */

import { Node } from "../../interfaces/simpleDataGenerator.interface";
import { isPersonNode, getAgeRange, getFullName } from "../../utils/nodePropertyHelpers";
import { POSITION_CONSTANTS } from "./constants/positionConstants";
import { getNodePropertyValue } from "../../utils/propertyDiscovery";

export type PropertyExtractor = (node: Node) => string;

export class PropertyValueResolver {
  private propertyExtractors: Map<string, PropertyExtractor>;

  constructor() {
    this.propertyExtractors = new Map([
      ["type", (node) => this.extractType(node)],
      ["age", (node) => this.extractAge(node)],
      ["petType", (node) => this.extractPetType(node)],
      ["petColor", (node) => this.extractPetColor(node)],
      ["firstName", (node) => this.extractFirstName(node)],
      ["lastName", (node) => this.extractLastName(node)],
      ["fullName", (node) => this.extractFullName(node)],
      ["animalType", (node) => this.extractAnimalType(node)],
      ["countryOfBirth", (node) => this.extractCountryOfBirth(node)],
      ["countryOfResidence", (node) => this.extractCountryOfResidence(node)],
      // Harness-specific properties
      ["service", (node) => this.extractService(node)],
      ["component", (node) => this.extractComponent(node)],
      ["language", (node) => this.extractLanguage(node)],
      ["size", (node) => this.extractSize(node)],
      ["resourceDomain", (node) => this.extractResourceDomain(node)],
      ["operationType", (node) => this.extractOperationType(node)],
      ["apiPattern", (node) => this.extractApiPattern(node)],
      ["apiComplexity", (node) => this.extractApiComplexity(node)],
      ["httpMethod", (node) => this.extractHttpMethod(node)]
    ]);
  }

  /**
   * Get property value for a node
   */
  public getPropertyValue(node: Node, propertyName: string): string {
    const extractor = this.propertyExtractors.get(propertyName);

    if (extractor) {
      try {
        return extractor(node);
      } catch (error) {
        warn(`[PropertyValueResolver] Error extracting property ${propertyName}: ${error}`);
      }
    }

    // Fall back to dynamic property discovery for unknown properties
    const value = getNodePropertyValue(node, propertyName);
    if (value !== undefined) {
      return tostring(value);
    }

    // Final fallback
    return "Unknown";
  }

  /**
   * Register a custom property extractor
   */
  public registerExtractor(propertyName: string, extractor: PropertyExtractor): void {
    this.propertyExtractors.set(propertyName, extractor);
  }

  /**
   * Get all available property names
   */
  public getAvailableProperties(): string[] {
    const properties: string[] = [];
    this.propertyExtractors.forEach((_, propertyName) => {
      properties.push(propertyName);
    });
    return properties;
  }

  /**
   * Extract type property
   */
  private extractType(node: Node): string {
    // For Harness data, the file type is in properties.type
    if (node.properties?.type) {
      return node.properties.type;
    }
    // For regular nodes, use node.type
    return node.type;
  }

  /**
   * Extract age as range
   */
  private extractAge(node: Node): string {
    return getAgeRange(node);
  }

  /**
   * Extract pet type
   */
  private extractPetType(node: Node): string {
    if (!isPersonNode(node)) {
      return "None";
    }
    return node.properties.petType || "None";
  }

  /**
   * Extract pet color
   */
  private extractPetColor(node: Node): string {
    if (!isPersonNode(node)) {
      return "None";
    }
    return node.properties.petColor || "None";
  }

  /**
   * Extract first name
   */
  private extractFirstName(node: Node): string {
    if (!isPersonNode(node)) {
      return node.name;
    }
    return node.properties.firstName || node.name;
  }

  /**
   * Extract last name
   */
  private extractLastName(node: Node): string {
    if (!isPersonNode(node)) {
      return "";
    }
    return node.properties.lastName || "";
  }

  /**
   * Extract full name
   */
  private extractFullName(node: Node): string {
    return getFullName(node);
  }

  /**
   * Extract animal type
   */
  private extractAnimalType(node: Node): string {
    if (node.type !== "Animals" || !node.properties) {
      return "None";
    }
    // Type-safe property access
    const properties = node.properties as { animalType?: string };
    return properties.animalType || "None";
  }

  /**
   * Extract country of birth
   */
  private extractCountryOfBirth(node: Node): string {
    if (!isPersonNode(node)) {
      return "Unknown";
    }
    return node.properties.countryOfBirth || "Unknown";
  }

  /**
   * Extract country of residence
   */
  private extractCountryOfResidence(node: Node): string {
    if (!isPersonNode(node)) {
      return "Unknown";
    }
    return node.properties.countryOfResidence || "Unknown";
  }

  /**
   * Get age range label from age value
   */
  public getAgeRangeLabel(age: number): string {
    const ranges = POSITION_CONSTANTS.AGE_RANGES;

    if (age < ranges.CHILD.max) return ranges.CHILD.label;
    if (age < ranges.YOUNG_ADULT.max) return ranges.YOUNG_ADULT.label;
    if (age < ranges.MIDDLE_AGED.max) return ranges.MIDDLE_AGED.label;
    if (age < ranges.SENIOR.max) return ranges.SENIOR.label;
    return ranges.ELDERLY.label;
  }

  /**
   * Extract service property (Harness)
   */
  private extractService(node: Node): string {
    return node.properties?.service || "Unknown";
  }

  /**
   * Extract component property (Harness)
   */
  private extractComponent(node: Node): string {
    return node.properties?.component || node.type || "Unknown";
  }

  /**
   * Extract language property (Harness)
   */
  private extractLanguage(node: Node): string {
    return node.properties?.language || "Unknown";
  }

  /**
   * Extract size property (Harness)
   */
  private extractSize(node: Node): string {
    return node.properties?.size || "Unknown";
  }

  /**
   * Extract resourceDomain property (Harness)
   */
  private extractResourceDomain(node: Node): string {
    return node.properties?.resourceDomain || "Unknown";
  }

  /**
   * Extract operationType property (Harness)
   */
  private extractOperationType(node: Node): string {
    return node.properties?.operationType || "Unknown";
  }

  /**
   * Extract apiPattern property (Harness)
   */
  private extractApiPattern(node: Node): string {
    return node.properties?.apiPattern || "Unknown";
  }

  /**
   * Extract apiComplexity property (Harness)
   */
  private extractApiComplexity(node: Node): string {
    return node.properties?.apiComplexity || "Unknown";
  }

  /**
   * Extract httpMethod property (Harness)
   */
  private extractHttpMethod(node: Node): string {
    return node.properties?.httpMethod || "Unknown";
  }
}

/**
 * Vertical Wall Creator
 * Creates vertical walls around the platform for Y-axis property visualization
 */


export interface VerticalWallConfig {
  platformBounds: {
    minX: number;
    maxX: number;
    minZ: number;
    maxZ: number;
  };
  height: number;
  parent: Instance;
}

export function createVerticalWalls(config: VerticalWallConfig): Part[] {
  const { platformBounds, height, parent } = config;
  const walls: Part[] = [];

  print(`[VerticalWallCreator] Creating walls with height ${height} for parent ${parent.Name}`);

  const wallThickness = 0.5;
  const wallColor = new Color3(0.3, 0.3, 0.3);
  const wallTransparency = 0.7;

  // Calculate platform dimensions
  const platformWidth = platformBounds.maxX - platformBounds.minX;
  const platformDepth = platformBounds.maxZ - platformBounds.minZ;
  const centerX = (platformBounds.minX + platformBounds.maxX) / 2;
  const centerZ = (platformBounds.minZ + platformBounds.maxZ) / 2;

  // Back wall (positive Z) - keep only back and right walls
  const backWall = new Instance("Part");
  backWall.Name = "VerticalWall_Back";
  backWall.Size = new Vector3(platformWidth + wallThickness * 2, height, wallThickness);
  backWall.Position = new Vector3(centerX, height / 2, platformBounds.maxZ + wallThickness / 2);
  backWall.Material = Enum.Material.Glass;
  backWall.Color = wallColor;
  backWall.Transparency = wallTransparency;
  backWall.Anchored = true;
  backWall.CanCollide = false;
  backWall.Parent = parent;
  walls.push(backWall);
  print(`[VerticalWallCreator] Created back wall at position (${backWall.Position.X}, ${backWall.Position.Y}, ${backWall.Position.Z})`);

  // Right wall (positive X)
  const rightWall = new Instance("Part");
  rightWall.Name = "VerticalWall_Right";
  rightWall.Size = new Vector3(wallThickness, height, platformDepth);
  rightWall.Position = new Vector3(platformBounds.maxX + wallThickness / 2, height / 2, centerZ);
  rightWall.Material = Enum.Material.Glass;
  rightWall.Color = wallColor;
  rightWall.Transparency = wallTransparency;
  rightWall.Anchored = true;
  rightWall.CanCollide = false;
  rightWall.Parent = parent;
  walls.push(rightWall);
  print(`[VerticalWallCreator] Created right wall at position (${rightWall.Position.X}, ${rightWall.Position.Y}, ${rightWall.Position.Z})`);

  print(`[VerticalWallCreator] Created ${walls.size()} vertical walls total`);
  return walls;
}

/**
 * Create swimlane shadows on vertical walls
 */
export function createWallSwimlanes(
  walls: Part[],
  propertyGroups: Map<string, { minY: number; maxY: number }>,
  colors: Map<string, Color3>
): void {
  walls.forEach(wall => {
    propertyGroups.forEach((bounds, propertyValue) => {
      const shadowHeight = bounds.maxY - bounds.minY;
      const shadowY = (bounds.minY + bounds.maxY) / 2;

      // Create a shadow block on the wall
      const shadow = new Instance("Part");
      shadow.Name = `WallShadow_${wall.Name}_${propertyValue}`;

      // Adjust size and position based on wall orientation
      if (wall.Name.find("Front") || wall.Name.find("Back")) {
        // Front/Back walls - shadows span width
        shadow.Size = new Vector3(wall.Size.X, shadowHeight, 0.1);
        shadow.Position = new Vector3(wall.Position.X, shadowY, wall.Position.Z);
      } else {
        // Left/Right walls - shadows span depth
        shadow.Size = new Vector3(0.1, shadowHeight, wall.Size.Z);
        shadow.Position = new Vector3(wall.Position.X, shadowY, wall.Position.Z);
      }

      shadow.Material = Enum.Material.Concrete;
      shadow.Color = colors.get(propertyValue) || new Color3(0.5, 0.5, 0.5);
      shadow.Transparency = 0.8;
      shadow.Anchored = true;
      shadow.CanCollide = false;
      shadow.Parent = wall;

      // Add label using SurfaceGui
      const surfaceGui = new Instance("SurfaceGui");
      surfaceGui.Face = wall.Name.find("Front") ? Enum.NormalId.Front 
                     : wall.Name.find("Back") ? Enum.NormalId.Back
                     : wall.Name.find("Left") ? Enum.NormalId.Left
                     : Enum.NormalId.Right;
      surfaceGui.SizingMode = Enum.SurfaceGuiSizingMode.PixelsPerStud;
      surfaceGui.PixelsPerStud = 50;
      surfaceGui.Parent = shadow;

      const label = new Instance("TextLabel");
      label.Size = new UDim2(1, 0, 1, 0);
      label.BackgroundTransparency = 1;
      label.Text = propertyValue;
      label.TextColor3 = new Color3(1, 1, 1);
      label.TextScaled = true;
      label.Font = Enum.Font.SourceSansBold;
      label.TextStrokeTransparency = 0;
      label.TextStrokeColor3 = new Color3(0, 0, 0);
      label.Parent = surfaceGui;
    });
  });


}

/**
 * Create a single vertical wall at the far Z edge of a shadow block
 * Wall is parallel to X-axis with same width as shadow block
 */
export function createFarZEdgeWall(shadowBlock: Part, height: number): Part {
  const wallThickness = 0.5;
  const wallColor = new Color3(0.3, 0.3, 0.3);
  const wallTransparency = 0.7;

  // Get shadow block dimensions
  const shadowWidth = shadowBlock.Size.X;
  const shadowDepth = shadowBlock.Size.Z;

  // Calculate wall position at far Z edge (positive Z)
  const wallX = shadowBlock.Position.X;
  const wallY = shadowBlock.Position.Y + shadowBlock.Size.Y / 2 + height / 2; // Position above shadow block
  const wallZ = shadowBlock.Position.Z + shadowDepth / 2 + wallThickness / 2;

  // Create the wall
  const wall = new Instance("Part");
  wall.Name = "FarZEdgeWall";
  wall.Size = new Vector3(shadowWidth, height, wallThickness);
  wall.Position = new Vector3(wallX, wallY, wallZ);
  wall.Material = Enum.Material.Glass;
  wall.Color = wallColor;
  wall.Transparency = wallTransparency;
  wall.Anchored = true;
  wall.CanCollide = false;
  wall.Parent = shadowBlock.Parent;


  return wall;
}

/**
 * Create a single vertical wall at the far X edge of a shadow block
 * Wall is parallel to Z-axis with same depth as shadow block
 */
export function createFarXEdgeWall(shadowBlock: Part, height: number): Part {
  const wallThickness = 0.5;
  const wallColor = new Color3(0.3, 0.3, 0.3);
  const wallTransparency = 0.7;

  // Get shadow block dimensions
  const shadowWidth = shadowBlock.Size.X;
  const shadowDepth = shadowBlock.Size.Z;

  // Calculate wall position at far X edge (positive X)
  const wallX = shadowBlock.Position.X + shadowWidth / 2 + wallThickness / 2;
  const wallY = shadowBlock.Position.Y + shadowBlock.Size.Y / 2 + height / 2; // Position above shadow block
  const wallZ = shadowBlock.Position.Z;

  // Create the wall
  const wall = new Instance("Part");
  wall.Name = "FarXEdgeWall";
  wall.Size = new Vector3(wallThickness, height, shadowDepth);
  wall.Position = new Vector3(wallX, wallY, wallZ);
  wall.Material = Enum.Material.Glass;
  wall.Color = wallColor;
  wall.Transparency = wallTransparency;
  wall.Anchored = true;
  wall.CanCollide = false;
  wall.Parent = shadowBlock.Parent;


  return wall;
}

