/**
 * Test Data Processor for Unified Data Renderer
 * 
 * Handles processing and conversion of test data into cluster format
 */

import {
  discoverNodeProperties,
  filterValidAxisProperties,
} from "../../../../../utils/propertyDiscovery";
import {
  Cluster,
  Group,
  Link,
  Node,
} from "../../../../../interfaces/simpleDataGenerator.interface";
import { EnhancedGeneratorConfig } from "../../../../../interfaces/enhancedGenerator.interface";
import { TEMP_HARNESS_LINKS } from "../../../../../data/tempHarnessLinks";
import { TEMP_HARNESS_TEST_DATA } from "../../../../../data/tempHarnessTestData";
import { ITestDataProcessor } from "./interfaces";

// Default maximum number of items to use from test data
const DEFAULT_MAX_DATA_ITEMS = 100;

export class TestDataProcessor implements ITestDataProcessor {
  private useTestData = true;

  /**
   * Generate cluster from harness test data
   */
  public generateClusterFromTestData(config?: EnhancedGeneratorConfig): Cluster {
    const maxItems = config?.maxDataItems || DEFAULT_MAX_DATA_ITEMS;
    print(`[TestDataProcessor] Using maxItems: ${maxItems} (config.maxDataItems: ${config?.maxDataItems || "undefined"})`);
    print(`[TestDataProcessor] Available test data items: ${TEMP_HARNESS_TEST_DATA.size()}`);

    // Convert Harness data to Node format - using only first maxItems items
    const harnessNodes: Node[] = [];
    let itemCount = 0;

    TEMP_HARNESS_TEST_DATA.forEach((file, index) => {
      if (itemCount >= maxItems) return; // Only process first maxItems items
      itemCount++;

      const node: Node = {
        uuid: `harness_node_${index}`,
        name: this.getFileName(file.path),
        type: file.component as any, // Use component as node type
        color: this.getServiceColor(file.service),
        position: { x: 0, y: 0, z: 0 }, // Will be calculated by swim lanes
        attachmentNames: [],
        properties: {
          service: file.service,
          component: file.component,
          language: file.language,
          size: file.size,
          type: file.type,
          resourceDomain: file.resourceDomain,
          operationType: file.operationType,
          apiPattern: file.apiPattern,
          apiComplexity: file.apiComplexity,
          httpMethod: file.httpMethod,
          path: file.path,
        },
      };
      harnessNodes.push(node);
    });

    // Use real harness links from the link detection analysis
    // Filter to only include links between nodes we actually have
    const nodeUuids = new Set(harnessNodes.map((node) => node.uuid));
    const validHarnessLinks = TEMP_HARNESS_LINKS.filter(
      (link) =>
        nodeUuids.has(link.sourceNodeUuid) && nodeUuids.has(link.targetNodeUuid)
    );

    print(`[TestDataProcessor] Found ${TEMP_HARNESS_LINKS.size()} total links, ${validHarnessLinks.size()} valid links for ${nodeUuids.size()} nodes`);

    // Convert to Link[] format (remove extra properties)
    const harnessLinks: Link[] = validHarnessLinks.map((link) => ({
      uuid: link.uuid,
      type: link.type,
      sourceNodeUuid: link.sourceNodeUuid,
      targetNodeUuid: link.targetNodeUuid,
      color: link.color,
    }));

    // Discover properties from the harness nodes
    const discoveredProps = discoverNodeProperties(harnessNodes);
    const validProps = filterValidAxisProperties(harnessNodes, discoveredProps);

    // Create a single group containing all harness nodes
    const mainGroup: Group = {
      id: "harness-group",
      name: "Harness Data Group",
      nodes: harnessNodes,
    };

    return {
      groups: [mainGroup],
      relations: harnessLinks,
      discoveredProperties: validProps,
    };
  }

  /**
   * Set whether to use test data mode
   */
  public setUseTestData(enabled: boolean): void {
    this.useTestData = enabled;
  }

  /**
   * Check if currently using test data
   */
  public isUsingTestData(): boolean {
    return this.useTestData;
  }

  /**
   * Extract filename from full path
   */
  private getFileName(path: string): string {
    if (false) {
      // This prevents lint errors while keeping the code available
      return "";
    }
    const parts = path.split("/");
    return parts[parts.size() - 1] || path;
  }

  /**
   * Get color based on service type
   */
  private getServiceColor(service: string): [number, number, number] {
    if (false) {
      // This prevents lint errors while keeping the code available
      return [0, 0, 0];
    }
    const serviceColors: { [key: string]: [number, number, number] } = {
      platform: [0.2, 0.4, 0.8],
      ci: [0.8, 0.4, 0.2],
      cd: [0.2, 0.8, 0.2],
      ce: [0.8, 0.2, 0.8],
      core: [0.8, 0.8, 0.2],
    };
    return serviceColors[service] || [0.5, 0.5, 0.5];
  }

  /**
   * Get test data statistics
   */
  public getTestDataStatistics(): TestDataStatistics {
    return {
      totalNodes: TEMP_HARNESS_TEST_DATA.size(),
      totalLinks: TEMP_HARNESS_LINKS.size(),
      services: this.getUniqueServices(),
      components: this.getUniqueComponents(),
      languages: this.getUniqueLanguages()
    };
  }

  /**
   * Get unique services from test data
   */
  private getUniqueServices(): string[] {
    const services = new Set<string>();
    TEMP_HARNESS_TEST_DATA.forEach((file) => {
      services.add(file.service);
    });
    const servicesArray: string[] = [];
    services.forEach((service) => servicesArray.push(service));
    return servicesArray;
  }

  /**
   * Get unique components from test data
   */
  private getUniqueComponents(): string[] {
    const components = new Set<string>();
    TEMP_HARNESS_TEST_DATA.forEach((file) => {
      components.add(file.component);
    });
    const componentsArray: string[] = [];
    components.forEach((component) => componentsArray.push(component));
    return componentsArray;
  }

  /**
   * Get unique languages from test data
   */
  private getUniqueLanguages(): string[] {
    const languages = new Set<string>();
    TEMP_HARNESS_TEST_DATA.forEach((file) => {
      languages.add(file.language);
    });
    const languagesArray: string[] = [];
    languages.forEach((language) => languagesArray.push(language));
    return languagesArray;
  }
}

/**
 * Test data statistics interface
 */
interface TestDataStatistics {
  totalNodes: number;
  totalLinks: number;
  services: string[];
  components: string[];
  languages: string[];
}

import { createTextBox } from "./TextBoxMaker";
import { LABEL_CONSTANTS } from "./renderers/constants/labelConstants";
import { BLOCK_CONSTANTS } from "./renderers/constants/blockConstants";

interface LabelGroupConfig {
  ropeIndex: number;
  sourceText: string;
  relationText: string;
  targetText: string;
  sourceAttachment: Attachment;
  targetAttachment: Attachment;
  parent: Instance;
  props?: { [key: string]: any };
}

function padNumber(num: number, length: number): string {
  const str = tostring(num);
  let result = str;
  while (result.size() < length) {
    result = "0" + result;
  }
  return result;
}

export function createLabelGroup({
  ropeIndex,
  sourceText,
  relationText,
  targetText,
  sourceAttachment,
  targetAttachment,
  parent,
  props = {},
}: LabelGroupConfig): Model {
  // Default properties for each label block
  const defaultProps = {
    size: new Vector3(1, 1, 3),
    brickColor: new BrickColor("Institutional white"),
    material: Enum.Material.SmoothPlastic,
    transparency: BLOCK_CONSTANTS.TRANSPARENCY.OPAQUE,
    spacing: 0.1,
  };

  // Merge default props with passed props
  const finalProps = {
    ...defaultProps,
    ...props,
  };

  // Calculate midpoint between attachments
  const midpoint = sourceAttachment.WorldPosition.add(
    targetAttachment.WorldPosition
  ).div(2);

  // Calculate direction vector for rope alignment
  const direction = targetAttachment.WorldPosition.sub(
    sourceAttachment.WorldPosition
  ).Unit;

  // Create a group to hold all three blocks
  const labelGroup = new Instance("Model");
  labelGroup.Name = `labelGroup${padNumber(
    ropeIndex,
    3
  )}-${sourceText}-${relationText}-${targetText}`;
  labelGroup.Parent = parent;

  // Calculate positions for the three blocks along the rope direction
  // Total length needed: 3 blocks + 2 gaps = 3 * blockLength + 2 * spacing
  const blockLength = finalProps.size.Z;
  const totalLength = 3 * blockLength + 2 * finalProps.spacing;
  const startOffset = -totalLength / 2;

  // Block positions along the rope direction
  const positions = [
    startOffset + blockLength / 2, // First block (source)
    startOffset + blockLength + finalProps.spacing + blockLength / 2, // Second block (relation)
    startOffset + 2 * blockLength + 2 * finalProps.spacing + blockLength / 2, // Third block (target)
  ];

  const texts = [sourceText, relationText, targetText];
  const blocks: Part[] = [];

  // Create three blocks
  for (let i = 0; i < 3; i++) {
    const cube = new Instance("Part");
    cube.Name = `block${i + 1}-${texts[i]}-${padNumber(ropeIndex, 3)}`;

    // Set properties
    cube.Size = finalProps.size;
    cube.BrickColor = finalProps.brickColor;
    cube.Material = finalProps.material;
    cube.Transparency = finalProps.transparency;
    cube.Shape = Enum.PartType.Block;
    cube.Anchored = false; // Unanchored so it can follow the rope
    cube.CanCollide = false;
    cube.CastShadow = false;

    // Position the block along the rope direction
    const blockPosition = midpoint.add(direction.mul(positions[i]));
    // Create CFrame that aligns the block's Z axis (long dimension) with the rope direction
    // CFrame.lookAt points -Z at target, but we want +Z along the rope, so we rotate 180 degrees
    const lookAtCFrame = CFrame.lookAt(blockPosition, blockPosition.add(direction));
    // Rotate 180 degrees around Y axis to align +Z with rope direction
    cube.CFrame = lookAtCFrame.mul(CFrame.Angles(0, math.pi, 0));

    cube.Parent = labelGroup;
    blocks.push(cube);

    // Add text boxes to all faces except the small ends (front/back)
    const faces = [
      Enum.NormalId.Left,
      Enum.NormalId.Right,
      Enum.NormalId.Top,
      Enum.NormalId.Bottom,
    ];

    faces.forEach((face) => {
      createTextBox({
        part: cube,
        face: face,
        text: texts[i],
      });
    });

    // Create attachment on the center block (relation block)
    if (i === 1) {
      const cubeAttachment = new Instance("Attachment");
      cubeAttachment.Name = `groupAtt${padNumber(ropeIndex, 3)}`;
      cubeAttachment.Position = new Vector3(0, 0, 0);
      cubeAttachment.Parent = cube;
    }
  }

  // Create attachment at rope midpoint (invisible part for positioning)
  const midpointPart = new Instance("Part");
  midpointPart.Name = `ropeAnchor${padNumber(ropeIndex, 3)}`;
  midpointPart.Size = new Vector3(LABEL_CONSTANTS.SIZES.PART_SIZE, LABEL_CONSTANTS.SIZES.PART_SIZE, LABEL_CONSTANTS.SIZES.PART_SIZE);
  midpointPart.Transparency = LABEL_CONSTANTS.TRANSPARENCY.PART; // Invisible
  midpointPart.Anchored = false; // Unanchored to follow rope physics
  midpointPart.CanCollide = false;
  midpointPart.Position = midpoint;
  midpointPart.CastShadow = false;
  midpointPart.Parent = parent;

  const ropeAttachment = new Instance("Attachment");
  ropeAttachment.Name = `ropeAtt${padNumber(ropeIndex, 3)}`;
  ropeAttachment.Position = new Vector3(0, 0, 0);
  ropeAttachment.Parent = midpointPart;

  // Create a RodConstraint to connect the midpoint part to the rope's midpoint
  // This will keep the labels positioned at the rope's midpoint
  const rodConstraint = new Instance("RodConstraint");
  rodConstraint.Name = `labelRod${padNumber(ropeIndex, 3)}`;
  rodConstraint.Attachment0 = ropeAttachment;
  rodConstraint.Attachment1 = sourceAttachment; // Connect to one of the rope's attachments
  rodConstraint.Length = sourceAttachment.WorldPosition.sub(midpoint).Magnitude;
  rodConstraint.Parent = midpointPart;

  // Create another rod to the target for stability
  const rodConstraint2 = new Instance("RodConstraint");
  rodConstraint2.Name = `labelRod2${padNumber(ropeIndex, 3)}`;
  rodConstraint2.Attachment0 = ropeAttachment;
  rodConstraint2.Attachment1 = targetAttachment;
  rodConstraint2.Length = targetAttachment.WorldPosition.sub(midpoint).Magnitude;
  rodConstraint2.Parent = midpointPart;

  // Weld each block to the midpoint part
  for (let i = 0; i < blocks.size(); i++) {
    const weld = new Instance("WeldConstraint");
    weld.Name = `labelWeld${i + 1}`;
    weld.Part0 = blocks[i];
    weld.Part1 = midpointPart;
    weld.Parent = blocks[i];
  }

  return labelGroup;
}

/**
 * Group Animation Test Service
 * 
 * Demonstrates animated movement of grouped hexagons in 3D space.
 * Creates two stacks of hexagons (red and blue) and provides a GUI button
 * that triggers an animation where red hexagons move to blue hexagons' position.
 */

import { TweenService, ReplicatedStorage } from "@rbxts/services";
import { makeHexagon } from "../../shared/modules/hexagonMaker";

// Hexagon configuration constants
const HEXAGON_CONFIG = {
  WIDTH: 8,
  HEIGHT: 0.5,
  SPACING: 4,
  COLORS: {
    RED: [1, 0, 0] as [number, number, number],
    BLUE: [0, 0, 1] as [number, number, number]
  }
};

// Animation configuration
const ANIMATION_CONFIG = {
  DURATION: 2,
  EASING_STYLE: Enum.EasingStyle.Quad,
  EASING_DIRECTION: Enum.EasingDirection.InOut
};

// Initial positions
const POSITIONS = {
  RED_STACK: new Vector3(-20, 10, 0),
  BLUE_STACK: new Vector3(20, 10, 0)
};

export class GroupAnimationTestService {
  private redHexagons: Model[] = [];
  private blueHexagons: Model[] = [];
  private remoteEvent?: RemoteEvent;
  private isAnimating = false;
  private testFolder?: Folder;

  /**
   * Runs the animation test
   */
  public runTest(parentFolder: Folder): void {

    // Cleanup any previous test
    this.cleanup();

    // Create test folder
    this.testFolder = new Instance("Folder");
    this.testFolder.Name = "AnimationTest";
    this.testFolder.Parent = parentFolder;

    // Create or get RemoteEvent
    this.remoteEvent = ReplicatedStorage.FindFirstChild("AnimationTestRemote") as RemoteEvent;
    if (!this.remoteEvent) {
      this.remoteEvent = new Instance("RemoteEvent");
      this.remoteEvent.Name = "AnimationTestRemote";
      this.remoteEvent.Parent = ReplicatedStorage;
    }

    // Set up remote event listener
    this.setupRemoteListener();

    // Create hexagon stacks
    this.redHexagons = this.createHexagonStack(
      HEXAGON_CONFIG.COLORS.RED,
      POSITIONS.RED_STACK,
      3,
      "red"
    );

    this.blueHexagons = this.createHexagonStack(
      HEXAGON_CONFIG.COLORS.BLUE,
      POSITIONS.BLUE_STACK,
      3,
      "blue"
    );

  }

  /**
   * Creates a vertical stack of hexagons
   */
  private createHexagonStack(
    color: [number, number, number], 
    basePosition: Vector3, 
    count: number,
    colorName: string
  ): Model[] {
    const hexagons: Model[] = [];

    for (let i = 0; i < count; i++) {
      const yOffset = i * (HEXAGON_CONFIG.HEIGHT + HEXAGON_CONFIG.SPACING);
      const position: [number, number, number] = [
        basePosition.X,
        basePosition.Y + yOffset,
        basePosition.Z
      ];

      const hexagon = makeHexagon({
        id: colorName === "red" ? 100 + i : 200 + i,
        position: new Vector3(position[0], position[1], position[2]),
        width: HEXAGON_CONFIG.WIDTH,
        height: HEXAGON_CONFIG.HEIGHT,
        barColor: new Color3(color[0], color[1], color[2]),
        labels: [`${colorName}-Front`, `${colorName}-Left`, `${colorName}-Right`],
        stackIndex: colorName === "red" ? 1 : 2,
        hexIndex: i + 1
      });

      hexagon.Name = `${colorName}Hexagon${i + 1}`;
      hexagon.Parent = this.testFolder;
      hexagons.push(hexagon);
    }

    return hexagons;
  }

  /**
   * Sets up remote event listener
   */
  private setupRemoteListener(): void {
    if (!this.remoteEvent) return;

    this.remoteEvent.OnServerEvent.Connect((player: Player, ...args: unknown[]) => {
      const eventType = args[0] as string;
      if (eventType === "triggerAnimation" && !this.isAnimating) {
        this.startAnimation();
      }
    });
  }

  /**
   * Starts the animation
   */
  private startAnimation(): void {
    if (this.isAnimating) return;


    // Notify all clients that animation started
    if (this.remoteEvent) {
      this.remoteEvent.FireAllClients("animationStarted");
    }

    // Calculate target positions (where blue hexagons are)
    const targetPositions = this.blueHexagons.map(hexagon => {
      const primaryPart = hexagon.FindFirstChildWhichIsA("Part");
      return primaryPart ? primaryPart.Position : new Vector3(0, 0, 0);
    });

    // Animate red hexagons to blue positions
    this.animateHexagons(this.redHexagons, targetPositions);
  }

  /**
   * Animates hexagons to target positions
   */
  private animateHexagons(hexagons: Model[], targetPositions: Vector3[]): void {
    this.isAnimating = true;

    const tweenInfo = new TweenInfo(
      ANIMATION_CONFIG.DURATION,
      ANIMATION_CONFIG.EASING_STYLE,
      ANIMATION_CONFIG.EASING_DIRECTION
    );

    // Create and play tweens for each hexagon's parts
    const allTweens: Tween[] = [];

    hexagons.forEach((hexagon, hexIndex) => {
      const targetPos = targetPositions[hexIndex];
      const parts = hexagon.GetDescendants().filter((obj): obj is Part => obj.IsA("Part"));

      parts.forEach(part => {
        // Calculate offset from the hexagon's primary part
        const primaryPart = hexagon.FindFirstChildWhichIsA("Part");
        if (primaryPart) {
          const offset = part.Position.sub(primaryPart.Position);
          const newPosition = targetPos.add(offset);

          const tween = TweenService.Create(
            part,
            tweenInfo,
            { Position: newPosition }
          );
          allTweens.push(tween);
          tween.Play();
        }
      });
    });

    // Notify clients when animation completes
    if (allTweens.size() > 0) {
      allTweens[0].Completed.Connect(() => {
        this.isAnimating = false;
        if (this.remoteEvent) {
          this.remoteEvent.FireAllClients("animationCompleted");
        }
      });
    }
  }


  /**
   * Cleans up previous test instances
   */
  private cleanup(): void {
    // Destroy test folder and all blocks
    if (this.testFolder) {
      this.testFolder.Destroy();
      this.testFolder = undefined;
    }

    // Clear arrays
    this.redHexagons = [];
    this.blueHexagons = [];
    this.isAnimating = false;
  }
}

/**
 * Endcap Block Creator
 * Creates endcap blocks for swimlane shadows
 * Part of T10: Add endcaps to swimlane shadows
 */

import { BLOCK_CONSTANTS } from "../constants/blockConstants";
import { BaseBlockCreator } from "./baseBlockCreator";

export interface EndcapConfig {
  swimlaneBlock: Part;
  swimlaneName: string;
  parent: Instance;
  gap?: number;
  isZAxis?: boolean;
  color?: Color3;
}

export class EndcapBlockCreator extends BaseBlockCreator {
  private readonly ENDCAP_WIDTH = 1;
  private readonly DEFAULT_GAP = 1;

  /**
   * Create endcaps for a swimlane and wrap everything in a model
   */
  public createSwimlaneWithEndcaps(config: EndcapConfig): Model {
    const {
      swimlaneBlock,
      swimlaneName,
      parent,
      gap = this.DEFAULT_GAP,
      isZAxis = false,
      color = swimlaneBlock.Color, // Use swimlane color by default
    } = config;

    const swimlanePos = swimlaneBlock.Position;
    const swimlaneSize = swimlaneBlock.Size;

    // Create a model to contain the swimlane and its endcaps
    const swimlaneModel = new Instance("Model");
    swimlaneModel.Name = `${swimlaneName}_Swimlane`;
    swimlaneModel.Parent = parent;

    // Parent the swimlane block to the model
    swimlaneBlock.Parent = swimlaneModel;

    // Calculate endcap dimensions
    const endcapWidth = this.ENDCAP_WIDTH;
    const endcapHeight = swimlaneSize.Y; // Match swimlane height

    let endcapSize: Vector3;
    let leftPos: Vector3;
    let rightPos: Vector3;

    if (isZAxis) {
      // Z-axis property swimlanes run in X direction, so endcaps at left and right
      endcapSize = new Vector3(endcapWidth, endcapHeight, swimlaneSize.Z);

      // Left endcap (negative X)
      leftPos = new Vector3(
        swimlanePos.X - swimlaneSize.X / 2 - gap - endcapWidth / 2,
        swimlanePos.Y,
        swimlanePos.Z
      );

      // Right endcap (positive X)
      rightPos = new Vector3(
        swimlanePos.X + swimlaneSize.X / 2 + gap + endcapWidth / 2,
        swimlanePos.Y,
        swimlanePos.Z
      );
    } else {
      // X-axis property swimlanes run in Z direction, so endcaps at front and back
      endcapSize = new Vector3(swimlaneSize.X, endcapHeight, endcapWidth);

      // Front endcap (negative Z)
      leftPos = new Vector3(
        swimlanePos.X,
        swimlanePos.Y,
        swimlanePos.Z - swimlaneSize.Z / 2 - gap - endcapWidth / 2
      );

      // Back endcap (positive Z)
      rightPos = new Vector3(
        swimlanePos.X,
        swimlanePos.Y,
        swimlanePos.Z + swimlaneSize.Z / 2 + gap + endcapWidth / 2
      );
    }

    // Create left/front endcap
    this.createEndcapBlock(
      `${swimlaneName}_${isZAxis ? "Left" : "Front"}Endcap`,
      endcapSize,
      leftPos,
      swimlaneName,
      swimlaneModel, // Parent to the model
      isZAxis,
      color
    );

    // Create right/back endcap
    this.createEndcapBlock(
      `${swimlaneName}_${isZAxis ? "Right" : "Back"}Endcap`,
      endcapSize,
      rightPos,
      swimlaneName,
      swimlaneModel, // Parent to the model
      isZAxis,
      color
    );

    return swimlaneModel;
  }

  /**
   * Create a single endcap block
   */
  private createEndcapBlock(
    name: string,
    size: Vector3,
    position: Vector3,
    label: string,
    parent: Instance,
    isZAxis: boolean,
    color: Color3
  ): Part {
    // Create the endcap block
    const endcap = this.createBlock({
      name: name,
      size: size,
      position: position,
      material: Enum.Material.Concrete,
      color: color, // Use swimlane color
      transparency: 0,
      canCollide: false,
    });

    // Add labels to all faces
    this.addLabelsToAllFaces(endcap, label, isZAxis);

    endcap.Parent = parent;
    return endcap;
  }

  /**
   * Add surface labels to all faces of the endcap
   */
  private addLabelsToAllFaces(
    block: Part,
    text: string,
    isZAxis: boolean
  ): void {
    const faces: Enum.NormalId[] = [
      Enum.NormalId.Front,
      Enum.NormalId.Back,
      Enum.NormalId.Left,
      Enum.NormalId.Right,
      Enum.NormalId.Top,
      Enum.NormalId.Bottom,
    ];

    faces.forEach((face) => {
      // Skip labels on short sides
      // For Z-axis endcaps: skip Front and Back faces (these are the narrow sides)
      // For X-axis endcaps: skip Left and Right faces (these are the narrow sides)
      if (
        isZAxis &&
        (face === Enum.NormalId.Front || face === Enum.NormalId.Back)
      ) {
        return;
      }
      if (
        !isZAxis &&
        (face === Enum.NormalId.Left || face === Enum.NormalId.Right)
      ) {
        return;
      }
      // Create SurfaceGui
      const surfaceGui = new Instance("SurfaceGui");
      surfaceGui.Name = `EndcapLabel_${face.Name}`;
      surfaceGui.Face = face;
      surfaceGui.SizingMode = Enum.SurfaceGuiSizingMode.PixelsPerStud;
      surfaceGui.PixelsPerStud = 50;
      surfaceGui.Parent = block;

      // Create Frame for background
      const frame = new Instance("Frame");
      frame.Size = new UDim2(1, 0, 1, 0);
      frame.BackgroundColor3 = new Color3(0.1, 0.1, 0.1);
      frame.BackgroundTransparency = 1; // Fully transparent background
      frame.BorderSizePixel = BLOCK_CONSTANTS.LABEL_STYLING.BORDER_SIZE_PIXEL;
      frame.BorderColor3 = BLOCK_CONSTANTS.LABEL_STYLING.BORDER_COLOR;
      frame.Parent = surfaceGui;

      // Create TextLabel
      const textLabel = new Instance("TextLabel");
      textLabel.Size = new UDim2(0.9, 0, 0.9, 0);
      textLabel.Position = new UDim2(0.05, 0, 0.05, 0);
      textLabel.BackgroundTransparency = 1;
      textLabel.Font = BLOCK_CONSTANTS.LABEL_STYLING.FONT;
      textLabel.Text = text;
      textLabel.TextColor3 = BLOCK_CONSTANTS.LABEL_STYLING.TEXT_COLOR;
      textLabel.TextScaled = false;
      textLabel.TextSize = 36; // Reduced by 50%

      // Rotate text on top face for X-axis swimlanes (person endcaps)
      if (face === Enum.NormalId.Top && !isZAxis) {
        textLabel.Rotation = 90;
      }

      textLabel.Parent = frame;
    });
  }
}

/**
 * Collapsible frame component for the configuration GUI
 * Allows the main GUI to be minimized/expanded
 */

import { GUI_CONSTANTS } from "../constants";
import { createUICorner } from "../utilities";
import { ComponentFactory } from "../componentFactory";
import { TweenService } from "@rbxts/services";

interface CollapsibleFrameOptions {
  parent: ScreenGui;
  size?: UDim2;
  collapsedSize?: UDim2;
  title?: string;
  position?: UDim2;
}

interface CollapsibleFrameResult {
  frame: Frame;
  contentFrame: Frame;
  toggleButton: TextButton;
  isCollapsed: boolean;
  toggle: () => void;
  setCollapsed: (collapsed: boolean) => void;
}

/**
 * Creates a collapsible main frame with header and toggle button
 */
export function createCollapsibleFrame(options: CollapsibleFrameOptions): CollapsibleFrameResult {
  const {
    parent,
    size = new UDim2(0, GUI_CONSTANTS.FRAME.ENHANCED_WIDTH, GUI_CONSTANTS.FRAME.ENHANCED_HEIGHT_SCALE, 0),
    collapsedSize = new UDim2(0, GUI_CONSTANTS.FRAME.ENHANCED_WIDTH, 0, 40),
    title = "Configuration GUI",
    position = GUI_CONSTANTS.FRAME.POSITION
  } = options;

  let isCollapsed = true; // Start collapsed

  // Create main container frame
  const mainFrame = new Instance("Frame");
  mainFrame.Name = GUI_CONSTANTS.NAMES.MAIN_FRAME;
  mainFrame.Size = collapsedSize; // Start with collapsed size
  mainFrame.Position = position;
  mainFrame.BackgroundColor3 = GUI_CONSTANTS.COLORS.BACKGROUND;
  mainFrame.BorderSizePixel = 0;
  mainFrame.ClipsDescendants = true;
  mainFrame.Parent = parent;

  // Add corner rounding
  createUICorner(mainFrame);

  // Create header frame
  const headerFrame = new Instance("Frame");
  headerFrame.Name = "HeaderFrame";
  headerFrame.Size = new UDim2(1, 0, 0, 40);
  headerFrame.Position = new UDim2(0, 0, 0, 0);
  headerFrame.BackgroundColor3 = new Color3(0.15, 0.15, 0.15);
  headerFrame.BorderSizePixel = 0;
  headerFrame.Parent = mainFrame;

  // Add corner rounding to header (only top corners)
  const headerCorner = createUICorner(headerFrame);
  headerCorner.CornerRadius = new UDim(0, 8);

  // Create title label
  const titleLabel = new Instance("TextLabel");
  titleLabel.Name = "TitleLabel";
  titleLabel.Size = new UDim2(1, -50, 1, 0);
  titleLabel.Position = new UDim2(0, 10, 0, 0);
  titleLabel.BackgroundTransparency = 1;
  titleLabel.Font = GUI_CONSTANTS.TYPOGRAPHY.TITLE_FONT;
  titleLabel.Text = title;
  titleLabel.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  titleLabel.TextScaled = true;
  titleLabel.TextXAlignment = Enum.TextXAlignment.Left;
  titleLabel.Parent = headerFrame;

  // Create toggle button
  const toggleButton = ComponentFactory.createButton({
    name: "ToggleButton",
    text: "+", // Plus sign for collapsed state
    position: new UDim2(1, -40, 0, 5),
    size: new UDim2(0, 30, 0, 30),
    parent: headerFrame,
    backgroundColor: new Color3(0.25, 0.25, 0.25)
  });

  // Style the toggle button
  toggleButton.Font = Enum.Font.SourceSansBold;
  toggleButton.TextSize = 24;

  // Create content frame (holds all GUI content)
  const contentFrame = new Instance("Frame");
  contentFrame.Name = "ContentFrame";
  contentFrame.Size = new UDim2(1, 0, 1, -40);
  contentFrame.Position = new UDim2(0, 0, 0, 40);
  contentFrame.BackgroundTransparency = 1;
  contentFrame.Visible = false; // Start hidden since we're collapsed
  contentFrame.Parent = mainFrame;

  // Create tween info for smooth animation
  const tweenInfo = new TweenInfo(
    0.3, // Duration
    Enum.EasingStyle.Quad,
    Enum.EasingDirection.InOut
  );

  // Toggle function
  const toggle = () => {
    isCollapsed = !isCollapsed;

    // Update button text
    toggleButton.Text = isCollapsed ? "+" : "";

    // Animate frame size
    const targetSize = isCollapsed ? collapsedSize : size;
    const tween = TweenService.Create(mainFrame, tweenInfo, {
      Size: targetSize
    });

    // Hide/show content during animation
    if (isCollapsed) {
      contentFrame.Visible = false;
    } else {
      // Delay showing content until animation is partially complete
      task.wait(0.1);
      contentFrame.Visible = true;
    }

    tween.Play();
  };

  // Set collapsed state without animation
  const setCollapsed = (collapsed: boolean) => {
    isCollapsed = collapsed;
    toggleButton.Text = isCollapsed ? "+" : "";
    mainFrame.Size = isCollapsed ? collapsedSize : size;
    contentFrame.Visible = !isCollapsed;
  };

  // Connect toggle button
  toggleButton.MouseButton1Click.Connect(toggle);

  // Allow dragging by header
  let dragging = false;
  let dragStart: Vector3 | undefined;
  let startPos: UDim2 | undefined;

  headerFrame.InputBegan.Connect((input) => {
    if (input.UserInputType === Enum.UserInputType.MouseButton1) {
      dragging = true;
      dragStart = input.Position;
      startPos = mainFrame.Position;
    }
  });

  headerFrame.InputChanged.Connect((input) => {
    if (dragging && input.UserInputType === Enum.UserInputType.MouseMovement && dragStart && startPos) {
      const delta = input.Position.sub(dragStart);
      mainFrame.Position = new UDim2(
        startPos.X.Scale,
        startPos.X.Offset + delta.X,
        startPos.Y.Scale,
        startPos.Y.Offset + delta.Y
      );
    }
  });

  headerFrame.InputEnded.Connect((input) => {
    if (input.UserInputType === Enum.UserInputType.MouseButton1) {
      dragging = false;
    }
  });

  return {
    frame: mainFrame,
    contentFrame,
    toggleButton,
    isCollapsed,
    toggle,
    setCollapsed
  };
}

import { ReplicatedStorage } from "@rbxts/services";
import { UnifiedDataRenderer } from "../../shared/modules/renderers/unifiedDataRenderer";
import { BaseService } from "../../shared/services/base/BaseService";
import { validateEnhancedGeneratorConfig, validateRemoteData } from "../../shared/utils/validation";

export class ConfigGUIServerService extends BaseService {
  private remoteEvent: RemoteEvent;
  private unifiedRenderer: UnifiedDataRenderer;
  private projectRootFolder: Folder;
  private origin: Vector3;

  constructor(projectRootFolder: Folder, origin?: Vector3) {
    super("ConfigGUIServerService");
    this.unifiedRenderer = new UnifiedDataRenderer();
    this.projectRootFolder = projectRootFolder;
    this.origin = origin || new Vector3(0, 0, 0);

    // Create or get RemoteEvent
    let remoteEvent = ReplicatedStorage.FindFirstChild("ConfigGUIRemote") as RemoteEvent;
    if (!remoteEvent) {
      remoteEvent = new Instance("RemoteEvent");
      remoteEvent.Name = "ConfigGUIRemote";
      remoteEvent.Parent = ReplicatedStorage;
      // Track the instance for cleanup
      this.addInstance(remoteEvent);
    }
    this.remoteEvent = remoteEvent;

    // Set up event listener
    this.setupEventListener();
  }

  /**
   * Sets up the remote event listener for config changes
   */
  private setupEventListener(): void {
    const eventConnection = this.remoteEvent.OnServerEvent.Connect((player: Player, ...args: unknown[]) => {
      const [eventType, data] = args;

      // First validate remote data size and structure
      const remoteValidation = validateRemoteData(data);
      if (!remoteValidation.isValid) {
        warn(`[ConfigGUIServerService] Invalid remote data from ${player.Name}: ${remoteValidation.error}`);
        this.remoteEvent.FireClient(player, "error", remoteValidation.error);
        return;
      }

      // We only handle enhanced mode now with the unified renderer
      if (eventType === "regenerateEnhanced" && typeIs(data, "table")) {

        // Use comprehensive validation
        const validationResult = validateEnhancedGeneratorConfig(data);

        if (validationResult.isValid && validationResult.sanitizedConfig) {
          // Use unified renderer with sanitized config
          const cluster = this.unifiedRenderer.renderEnhancedData(
            this.projectRootFolder, 
            validationResult.sanitizedConfig, 
            this.origin
          );

          // Send success response with discovered properties
          this.remoteEvent.FireClient(player, "regenerateSuccess", validationResult.sanitizedConfig);

          // Send discovered properties if available
          if (cluster && cluster.discoveredProperties) {
            this.remoteEvent.FireClient(player, "discoveredProperties", cluster.discoveredProperties);
          }
        } else {
          // Send detailed error response
          const errorMessage = validationResult.errors.join("; ");
          warn(`[ConfigGUIServerService] Validation errors: ${errorMessage}`);
          this.remoteEvent.FireClient(player, "regenerateError", errorMessage);
        }
      } else if (eventType === "clearGraph") {

        // Delete the GraphMaker folder
        const graphMakerFolder = this.projectRootFolder.FindFirstChild("GraphMaker");
        if (graphMakerFolder) {
          graphMakerFolder.Destroy();
        }

        // Delete the flat block foundation
        const flatBlock = this.projectRootFolder.FindFirstChild("FlatBlockFoundation");
        if (flatBlock) {
          flatBlock.Destroy();
        }

        // Send success response
        this.remoteEvent.FireClient(player, "clearSuccess");
      } else if (eventType === "updateEnhanced" && typeIs(data, "table")) {

        // Use comprehensive validation
        const validationResult = validateEnhancedGeneratorConfig(data);

        if (validationResult.isValid && validationResult.sanitizedConfig) {
          // Use unified renderer's update method with sanitized config
          const clusterOrVoid = this.unifiedRenderer.updateEnhancedData(
            this.projectRootFolder, 
            validationResult.sanitizedConfig, 
            this.origin
          );

          // Send success response
          this.remoteEvent.FireClient(player, "updateSuccess", validationResult.sanitizedConfig);

          // Send discovered properties if a new cluster was created
          if (clusterOrVoid && typeIs(clusterOrVoid, "table") && clusterOrVoid.discoveredProperties) {
            this.remoteEvent.FireClient(player, "discoveredProperties", clusterOrVoid.discoveredProperties);
          }
        } else {
          // Send detailed error response
          const errorMessage = validationResult.errors.join("; ");
          warn(`[ConfigGUIServerService] Update validation errors: ${errorMessage}`);
          this.remoteEvent.FireClient(player, "updateError", errorMessage);
        }
      }
    });

    // Add connection to be managed
    this.addConnection(eventConnection);
  }


  /**
   * Custom cleanup logic
   */
  protected onDestroy(): void {
    // Clean up the unified renderer if it has a destroy method
    if (this.unifiedRenderer && "destroy" in this.unifiedRenderer) {
      const renderer = this.unifiedRenderer as unknown as { destroy?: () => void };
      if (renderer.destroy) {
        renderer.destroy();
      }
    }

  }
}

import { BaseService } from "../../shared/services/base/BaseService";
import { ConfigGUIService } from "../services/configGui";
import { EnhancedGeneratorConfig } from "../../shared/interfaces/enhancedGenerator.interface";
import { ReplicatedStorage } from "@rbxts/services";
import { validateEnhancedGeneratorConfig } from "../../shared/utils/validation";

export class ConfigGUIController extends BaseService {
  private guiService?: ConfigGUIService;
  private remoteEvent?: RemoteEvent;

  /**
   * Initializes the configuration GUI controller
   */
  constructor() {
    super("ConfigGUIController");
  }

  public initialize(): void {
    // Wait for remote event
    const remoteEvent = ReplicatedStorage.WaitForChild(
      "ConfigGUIRemote"
    ) as RemoteEvent;
    this.remoteEvent = remoteEvent;

    // Set up event listeners
    this.setupEventListeners();

    // Wait for initial config from server before creating GUI
    // The GUI will be created when we receive the initial config
  }

  /**
   * Sets up remote event listeners
   */
  private setupEventListeners(): void {
    if (!this.remoteEvent) return;

    const eventConnection = this.remoteEvent.OnClientEvent.Connect(
      (eventType: string, data?: unknown) => {

        if (eventType === "initialConfig" && typeIs(data, "table")) {

          // Check if it's an enhanced config (has layers property)
          const configData = data as { layers?: unknown };
          if (configData.layers) {
            const enhancedConfig = data as EnhancedGeneratorConfig;

            if (!this.guiService) {
              // First time receiving config - create GUI with initial values


              this.guiService = new ConfigGUIService({
                onEnhancedConfigChange: (config) =>
                  this.onEnhancedConfigChange(config),
                onClearRequest: () => this.onClearRequest(),
                onUpdateRequest: (config) => this.onUpdateRequest(config),
                initialConfig: enhancedConfig,
              });
              this.guiService.createGUI();
            } else {
              // GUI already exists - just update it

              this.guiService.updateEnhancedConfig(enhancedConfig);
            }
          } else {
            // Simple mode no longer supported
          }
        } else if (eventType === "regenerateSuccess") {
        } else if (eventType === "regenerateError") {
          warn(" Regeneration failed:", data);
        } else if (eventType === "updateSuccess") {
        } else if (eventType === "updateError") {
          warn(" Update failed:", data);
        } else if (eventType === "triggerGeneration" && typeIs(data, "table")) {

          // Automatic generation triggered by server
          const enhancedConfig = data as EnhancedGeneratorConfig;
          if (this.guiService) {
            // Just trigger generation without updating GUI (it was already created with initialConfig)
            this.onEnhancedConfigChange(enhancedConfig);
          }
        } else if (eventType === "discoveredProperties" && typeIs(data, "table")) {

          const properties = data as string[];

          if (this.guiService) {

            this.guiService.updateDiscoveredProperties(properties);
          } else {

          }
        }
      }
    );

    // Add connection to be managed
    this.addConnection(eventConnection);
  }

  /**
   * Handles enhanced configuration changes from the GUI
   */
  private onEnhancedConfigChange(config: EnhancedGeneratorConfig): void {
    if (this.remoteEvent) {
      // Validate configuration before sending
      const validationResult = validateEnhancedGeneratorConfig(config);

      if (validationResult.isValid && validationResult.sanitizedConfig) {
        this.remoteEvent.FireServer("regenerateEnhanced", validationResult.sanitizedConfig);
      } else {
        // Show validation errors in GUI
        const errorMessage = validationResult.errors.join("\n");
        warn(` Configuration validation failed:\n${errorMessage}`);

        if (this.guiService) {
          this.guiService.showError(`Validation failed: ${validationResult.errors[0]}`);
        }
      }
    }
  }

  /**
   * Handles clear request from the GUI
   */
  private onClearRequest(): void {
    if (this.remoteEvent) {
      this.remoteEvent.FireServer("clearGraph");
    }
  }

  /**
   * Handles update request from the GUI
   */
  private onUpdateRequest(config: EnhancedGeneratorConfig): void {
    if (this.remoteEvent) {
      // Validate configuration before sending
      const validationResult = validateEnhancedGeneratorConfig(config);

      if (validationResult.isValid && validationResult.sanitizedConfig) {
        this.remoteEvent.FireServer("updateEnhanced", validationResult.sanitizedConfig);
      } else {
        // Show validation errors in GUI
        const errorMessage = validationResult.errors.join("\n");
        warn(` Update validation failed:\n${errorMessage}`);

        if (this.guiService) {
          this.guiService.showError(`Validation failed: ${validationResult.errors[0]}`);
        }
      }
    }
  }

  /**
   * Custom cleanup logic
   */
  protected onDestroy(): void {
    if (this.guiService) {
      this.guiService.destroy();
      this.guiService = undefined;
    }

  }
}

/**
 * Node Generator for Unified Data Renderer
 * 
 * Handles creation and configuration of individual nodes
 */

import {
  ANIMAL_TYPES,
  COLOR_PALETTES,
  COUNTRIES_OF_BIRTH,
  COUNTRIES_OF_RESIDENCE,
  DEFAULT_ATTACHMENTS,
  FIRST_NAMES,
  LAST_NAMES,
  NODE_TYPE_NAMES,
  PET_COLORS,
  PET_TYPES,
} from "../../constants";
import {
  EnhancedGeneratorConfig,
  LayerConfig,
} from "../../../../../interfaces/enhancedGenerator.interface";
import { Node } from "../../../../../interfaces/simpleDataGenerator.interface";
import { INodeGenerator } from "./interfaces";

export class NodeGenerator implements INodeGenerator {

  /**
   * Generate nodes for a single layer
   */
  public generateLayerNodes(
    layer: LayerConfig,
    config: EnhancedGeneratorConfig
  ): Node[] {
    const layerNodes: Node[] = [];

    for (let i = 0; i < layer.numNodes; i++) {
      const node = this.createNode(i, layer.layerNumber, config);
      layerNodes.push(node);
    }

    return layerNodes;
  }

  /**
   * Create a single node
   */
  public createNode(
    index: number,
    layerNumber: number,
    config: EnhancedGeneratorConfig
  ): Node {
    // Cycle through node types based on config
    const nodeTypeIndex = index % config.numNodeTypes;
    const nodeTypeName =
      NODE_TYPE_NAMES[math.min(nodeTypeIndex, NODE_TYPE_NAMES.size() - 1)];
    const color =
      COLOR_PALETTES.NODE_COLORS[
        nodeTypeIndex % COLOR_PALETTES.NODE_COLORS.size()
      ];

    const node: Node = {
      uuid: `node_${layerNumber}_${index}`,
      name: `${nodeTypeName} ${layerNumber}-${index + 1}`,
      type: nodeTypeName as any, // Now supports "man", "woman", "child"
      color,
      position: { x: 0, y: 0, z: 0 }, // Will be calculated by swim lanes
      attachmentNames: DEFAULT_ATTACHMENTS,
    };

    // Add level property for swim lane algorithm
    const nodeWithLevel = node as Node & { level: number };
    nodeWithLevel.level = layerNumber;

    // Add type-specific properties
    this.addTypeSpecificProperties(node, nodeTypeName, config);

    return node;
  }

  /**
   * Add type-specific properties to node
   */
  private addTypeSpecificProperties(
    node: Node,
    nodeTypeName: string,
    config: EnhancedGeneratorConfig
  ): void {
    if (nodeTypeName === "man" || nodeTypeName === "woman") {
      this.addPersonProperties(node, config);
    } else if (nodeTypeName === "child") {
      this.addChildProperties(node, config);
    } else if (nodeTypeName === "grandparent") {
      this.addGrandparentProperties(node, config);
    } else if (nodeTypeName === "Animals") {
      this.addAnimalProperties(node);
    }
  }

  /**
   * Add properties for adult persons (man/woman)
   */
  private addPersonProperties(node: Node, config: EnhancedGeneratorConfig): void {
    const numPetTypes = config.numPetTypes || 5;
    const maxPetTypeIndex = math.min(numPetTypes - 1, PET_TYPES.size() - 1);

    node.properties = {
      age: math.random(18, 80),
      petType: PET_TYPES[math.random(0, maxPetTypeIndex)],
      petColor: PET_COLORS[math.random(0, PET_COLORS.size() - 1)],
      firstName: FIRST_NAMES[math.random(0, FIRST_NAMES.size() - 1)],
      lastName: LAST_NAMES[math.random(0, LAST_NAMES.size() - 1)],
      countryOfBirth:
        COUNTRIES_OF_BIRTH[math.random(0, COUNTRIES_OF_BIRTH.size() - 1)],
      countryOfResidence:
        COUNTRIES_OF_RESIDENCE[
          math.random(0, COUNTRIES_OF_RESIDENCE.size() - 1)
        ],
    };
  }

  /**
   * Add properties for children
   */
  private addChildProperties(node: Node, config: EnhancedGeneratorConfig): void {
    const numPetTypes = config.numPetTypes || 5;
    const maxPetTypeIndex = math.min(numPetTypes - 1, PET_TYPES.size() - 1);

    node.properties = {
      age: math.random(5, 17),
      petType: PET_TYPES[math.random(0, maxPetTypeIndex)],
      petColor: PET_COLORS[math.random(0, PET_COLORS.size() - 1)],
      firstName: FIRST_NAMES[math.random(0, FIRST_NAMES.size() - 1)],
      lastName: LAST_NAMES[math.random(0, LAST_NAMES.size() - 1)],
      countryOfBirth:
        COUNTRIES_OF_BIRTH[math.random(0, COUNTRIES_OF_BIRTH.size() - 1)],
      countryOfResidence:
        COUNTRIES_OF_RESIDENCE[
          math.random(0, COUNTRIES_OF_RESIDENCE.size() - 1)
        ],
    };
  }

  /**
   * Add properties for grandparents
   */
  private addGrandparentProperties(node: Node, config: EnhancedGeneratorConfig): void {
    const numPetTypes = config.numPetTypes || 5;
    const maxPetTypeIndex = math.min(numPetTypes - 1, PET_TYPES.size() - 1);

    node.properties = {
      age: math.random(65, 95),
      petType: PET_TYPES[math.random(0, maxPetTypeIndex)],
      petColor: PET_COLORS[math.random(0, PET_COLORS.size() - 1)],
      firstName: FIRST_NAMES[math.random(0, FIRST_NAMES.size() - 1)],
      lastName: LAST_NAMES[math.random(0, LAST_NAMES.size() - 1)],
      countryOfBirth:
        COUNTRIES_OF_BIRTH[math.random(0, COUNTRIES_OF_BIRTH.size() - 1)],
      countryOfResidence:
        COUNTRIES_OF_RESIDENCE[
          math.random(0, COUNTRIES_OF_RESIDENCE.size() - 1)
        ],
    };
  }

  /**
   * Add properties for animals
   */
  private addAnimalProperties(node: Node): void {
    node.properties = {
      animalType: ANIMAL_TYPES[math.random(0, ANIMAL_TYPES.size() - 1)],
    };
  }
}

/**
 * Event Handlers for Configuration GUI
 * 
 * Centralizes all event handling logic for the configuration GUI,
 * providing clean separation of concerns and easier testing.
 */

import { EnhancedGeneratorConfig } from "./interfaces";
import { GUIStateManager } from "./stateManager";
import { updateStatus } from "./components/status";
import { validateEnhancedConfig } from "./validationHandlers";

interface EventHandlerOptions {
  stateManager: GUIStateManager;
  onEnhancedConfigChange?: (config: EnhancedGeneratorConfig) => void;
  onClearRequest?: () => void;
  onUpdateRequest?: (config: EnhancedGeneratorConfig) => void;
}

export class GUIEventHandlers {
  private stateManager: GUIStateManager;
  private onEnhancedConfigChange?: (config: EnhancedGeneratorConfig) => void;
  private onClearRequest?: () => void;
  private onUpdateRequest?: (config: EnhancedGeneratorConfig) => void;

  constructor(options: EventHandlerOptions) {
    this.stateManager = options.stateManager;
    this.onEnhancedConfigChange = options.onEnhancedConfigChange;
    this.onClearRequest = options.onClearRequest;
    this.onUpdateRequest = options.onUpdateRequest;
  }

  /**
   * Handles spacing field changes
   */
  public handleSpacingChange<K extends keyof import("../../../shared/interfaces/enhancedGenerator.interface").SpacingConfig>(
    field: K,
    value: import("../../../shared/interfaces/enhancedGenerator.interface").SpacingConfig[K]
  ): void {
    this.stateManager.updateSpacing(field, value);
    this.updateStatusMessage(`${field} updated to ${value}`);
  }


  /**
   * Handles layer updates
   */
  public handleLayerUpdate(layers: EnhancedGeneratorConfig["layers"]): void {
    this.stateManager.setLayers(layers);
    this.updateStatusMessage(`${layers.size()} layers configured`);
  }

  /**
   * Handles visualization option changes
   */
  public handleVisualizationChange<K extends keyof import("../../../shared/interfaces/enhancedGenerator.interface").VisualizationOptions>(
    field: K,
    value: import("../../../shared/interfaces/enhancedGenerator.interface").VisualizationOptions[K]
  ): void {
    this.stateManager.updateVisualization(field, value);
    this.updateStatusMessage(`Visualization option "${field}" changed`);

    // Trigger immediate update without regeneration
    if (this.onEnhancedConfigChange) {
      const config = this.stateManager.getEnhancedConfig();
      this.onEnhancedConfigChange(config);
    }
  }

  /**
   * Handles regenerate button click
   */
  public handleRegenerateClick(): void {
    const config = this.stateManager.getEnhancedConfig();

    // Validate configuration
    const validationResult = validateEnhancedConfig(config);
    if (!validationResult.isValid) {
      this.updateStatusMessage(validationResult.error || "Invalid configuration", true);
      return;
    }

    if (config.layers.size() === 0) {
      this.updateStatusMessage("No layers configured!", true);
      return;
    }

    if (this.onEnhancedConfigChange) {
      this.updateStatusMessage("Regenerating with new configuration...");
      this.onEnhancedConfigChange(config);
      this.updateStatusMessage("Regeneration complete");
    }
  }

  /**
   * Handles update button click
   */
  public handleUpdateClick(): void {
    const config = this.stateManager.getEnhancedConfig();

    // Validate configuration
    const validationResult = validateEnhancedConfig(config);
    if (!validationResult.isValid) {
      this.updateStatusMessage(validationResult.error || "Invalid configuration", true);
      return;
    }

    if (config.layers.size() === 0) {
      this.updateStatusMessage("No layers configured!", true);
      return;
    }

    if (this.onUpdateRequest) {
      this.updateStatusMessage("Updating existing graph...");
      this.onUpdateRequest(config);
      this.updateStatusMessage("Update complete");
    }
  }

  /**
   * Handles clear button click
   */
  public handleClearClick(recreateGUI: () => void): void {
    // Send clear request to server to delete GraphMaker folder
    if (this.onClearRequest) {
      this.onClearRequest();
    }

    // Reset configuration
    this.stateManager.resetConfig();

    // Destroy current GUI elements
    const state = this.stateManager.getState();
    if (state.configFrame) {
      state.configFrame.Destroy();
    }

    // Recreate the GUI with reset state
    recreateGUI();
    this.updateStatusMessage("Configuration and graph cleared");
  }

  /**
   * Updates the status message
   */
  private updateStatusMessage(message: string, isError = false): void {
    const state = this.stateManager.getState();
    if (state.statusLabel) {
      updateStatus(state.statusLabel, message, isError);
    }
  }

  /**
   * Handles export config button click
   */
  public handleExportConfigClick(): void {
    // Export configuration data
    this.stateManager.getEnhancedConfig();

    this.updateStatusMessage("Configuration exported to console");
  }

  /**
   * Generates an array of type names
   */
  public generateTypeArray(prefix: string, count: number): string[] {
    const types: string[] = [];
    for (let i = 0; i < count; i++) {
      types.push(`${prefix} ${string.char(65 + i)}`); // A, B, C, etc.
    }
    return types;
  }
}

/**
 * Property Manager for Unified Data Renderer
 * 
 * Handles property discovery and validation for generated nodes
 */

import {
  discoverNodeProperties,
  filterValidAxisProperties,
} from "../../../../../utils/propertyDiscovery";
import { Node } from "../../../../../interfaces/simpleDataGenerator.interface";
import { IPropertyManager } from "./interfaces";

export class PropertyManager implements IPropertyManager {

  /**
   * Discover and validate properties from generated nodes
   */
  public discoverAndValidateProperties(nodes: Node[]): any[] {
    // Use the existing property discovery utilities
    const discoveredProps = discoverNodeProperties(nodes);
    const validProps = filterValidAxisProperties(nodes, discoveredProps);

    return validProps;
  }

  /**
   * Analyze property distribution across nodes
   */
  public analyzePropertyDistribution(nodes: Node[]): PropertyAnalysis {
    const analysis: PropertyAnalysis = {
      totalNodes: nodes.size(),
      propertyCoverage: new Map(),
      propertyTypes: new Map(),
      uniqueValues: new Map()
    };

    // Analyze each node's properties
    nodes.forEach((node) => {
      if (node.properties) {
        for (const [propName, value] of pairs(node.properties)) {
          // Track coverage
          const currentCount = analysis.propertyCoverage.get(propName) || 0;
          analysis.propertyCoverage.set(propName, currentCount + 1);

          // Track types
          const valueType = typeOf(value);
          analysis.propertyTypes.set(propName, valueType);

          // Track unique values
          if (!analysis.uniqueValues.has(propName)) {
            analysis.uniqueValues.set(propName, new Set());
          }
          analysis.uniqueValues.get(propName)!.add(value);
        }
      }
    });

    return analysis;
  }

  /**
   * Validate that properties are suitable for axis mapping
   */
  public validateAxisProperties(nodes: Node[], propertyName: string): PropertyValidation {
    let foundCount = 0;
    let nullCount = 0;
    let typeSet = new Set<string>();
    const sampleValues: defined[] = [];

    nodes.forEach((node) => {
      const properties = node.properties;
      let found = false;
      if (properties) {
        for (const [propName, propValue] of pairs(properties)) {
          if (propName === propertyName && propValue !== undefined) {
            foundCount++;
            typeSet.add(typeOf(propValue));
            if (sampleValues.size() < 5) {
              sampleValues.push(propValue);
            }
            found = true;
            break;
          }
        }
      }
      if (!found) {
        nullCount++;
      }
    });

    const isValid = foundCount > 1 && typeSet.size() === 1; // Need variety and consistent typing
    const coverage = (foundCount / nodes.size()) * 100;

    const typeSetArray: string[] = [];
    typeSet.forEach((typeValue) => typeSetArray.push(typeValue));

    return {
      isValid,
      coverage,
      uniqueValueCount: foundCount, // Simplified for roblox-ts compatibility
      dataType: typeSet.size() === 1 ? typeSetArray[0] : 'mixed',
      nullCount,
      sampleValues
    };
  }

  /**
   * Get summary of all properties across nodes
   */
  public getPropertySummary(nodes: Node[]): PropertySummary {
    const analysis = this.analyzePropertyDistribution(nodes);
    const summary: PropertySummary = {
      totalProperties: analysis.propertyCoverage.size(),
      averageCoverage: 0,
      properties: []
    };

    let totalCoverage = 0;
    analysis.propertyCoverage.forEach((count, propName) => {
      const coverage = (count / analysis.totalNodes) * 100;
      totalCoverage += coverage;

      const validation = this.validateAxisProperties(nodes, propName);

      const uniqueValuesSet = analysis.uniqueValues.get(propName);
      const uniqueValuesCount = uniqueValuesSet ? uniqueValuesSet.size() : 0;

      summary.properties.push({
        name: propName,
        type: analysis.propertyTypes.get(propName) || 'unknown',
        coverage,
        uniqueValues: uniqueValuesCount,
        isValidForAxis: validation.isValid
      });
    });

    summary.averageCoverage = totalCoverage / analysis.propertyCoverage.size();

    // Sort by coverage descending
    table.sort(summary.properties, (a, b) => b.coverage > a.coverage);

    return summary;
  }
}

/**
 * Property analysis result interface
 */
interface PropertyAnalysis {
  totalNodes: number;
  propertyCoverage: Map<string, number>;
  propertyTypes: Map<string, string>;
  uniqueValues: Map<string, Set<defined>>;
}

/**
 * Property validation result interface
 */
interface PropertyValidation {
  isValid: boolean;
  coverage: number;
  uniqueValueCount: number;
  dataType: string;
  nullCount: number;
  sampleValues: defined[];
}

/**
 * Property summary interface
 */
interface PropertySummary {
  totalProperties: number;
  averageCoverage: number;
  properties: PropertyInfo[];
}

/**
 * Individual property information
 */
interface PropertyInfo {
  name: string;
  type: string;
  coverage: number;
  uniqueValues: number;
  isValidForAxis: boolean;
}

/**
 * Position Mapper for calculating node positions
 * Part of F002 Phase 2 refactoring - T5
 */

import { Node } from "../../../../interfaces/simpleDataGenerator.interface";
import { SpacingConfig } from "../interfaces";
import { POSITION_CONSTANTS } from "../../constants/positionConstants";
import { resolvePropertyValue } from "../../../../utils/nodePropertyHelpers";

export class PositionMapper {
  /**
   * Calculate X positions for each type column
   */
  public calculateTypeColumnPositions(
    sortedTypes: string[],
    nodesByTypeAndLayer: Map<string, Node[]>,
    numLayers: number,
    spacing: SpacingConfig,
    nodeOrganizer: { findMaxNodesInLayer: (nodes: Map<string, Node[]>, type: string, layers: number) => number }
  ): Map<string, number> {
    let typeXOffset = 0;
    const typeXPositions = new Map<string, number>();

    sortedTypes.forEach((nodeType, typeIndex) => {
      typeXPositions.set(nodeType, typeXOffset);

      // Find max nodes of this type in any layer
      const maxNodesInLayer = nodeOrganizer.findMaxNodesInLayer(
        nodesByTypeAndLayer, 
        nodeType, 
        numLayers
      );

      // Move to next type column
      typeXOffset += maxNodesInLayer * spacing.nodeSpacing;
      if (typeIndex < sortedTypes.size() - 1) {
        typeXOffset += spacing.swimlaneSpacing; // Gap between swim lanes
      }
    });

    return typeXPositions;
  }

  /**
   * Create position mapping for a property (Z-axis positioning)
   */
  public createPropertyPositionMap(
    nodesByTypeAndLayer: Map<string, Node[]>, 
    propertyName: string
  ): Map<string, number> {
    const uniqueValues = new Set<string>();

    // Collect all unique values for the property
    nodesByTypeAndLayer.forEach(nodes => {
      nodes.forEach(node => {
        const value = resolvePropertyValue(node, propertyName);
        uniqueValues.add(value);
      });
    });

    // Sort values and assign positions
    const sortedValues = [...uniqueValues].sort();
    const positionMap = new Map<string, number>();

    sortedValues.forEach((value, index) => {
      // Position values using constant spacing
      const position = (index - sortedValues.size() / 2) * POSITION_CONSTANTS.Z_DIMENSION_GROUP_SPACING;
      positionMap.set(value, position);
    });

    return positionMap;
  }

  /**
   * Calculate centering offset for nodes within a swimlane
   */
  public calculateCenteringOffset(
    maxNodesForType: number,
    actualNodeCount: number,
    spacing: SpacingConfig
  ): number {
    const laneWidth = maxNodesForType * spacing.nodeSpacing;
    const nodesWidth = actualNodeCount * spacing.nodeSpacing;
    return (laneWidth - nodesWidth) / POSITION_CONSTANTS.SWIMLANE.CENTERING_DIVISOR;
  }

  /**
   * Calculate layer Y position (inverted so layer 1 is at top)
   */
  public calculateLayerY(layer: number, numLayers: number, spacing: SpacingConfig): number {
    return POSITION_CONSTANTS.BASE_Y + (numLayers - layer) * spacing.layerSpacing;
  }

  /**
   * Apply offsets to cluster positions
   */
  public applyOffsetsToCluster(
    nodes: Node[], 
    offsetX: number, 
    offsetY: number, 
    offsetZ: number
  ): void {
    nodes.forEach(node => {
      if (!node.position) {
        warn(`[PositionMapper] Cannot apply offset to node ${node.name} - no position`);
        return;
      }

      node.position.x += offsetX;
      node.position.y += offsetY;
      node.position.z += offsetZ;
    });
  }

  /**
   * Calculate offsets to center cluster at origin
   */
  public calculateCenteringOffsets(
    bounds: { minX: number; maxX: number; minY: number },
    origin: Vector3,
    yOffset: number = 0
  ): { x: number; y: number; z: number } {
    const centerX = (bounds.minX + bounds.maxX) / 2;
    const offsetX = origin.X - centerX;
    const offsetY = origin.Y - bounds.minY + yOffset;
    const offsetZ = origin.Z;

    return { x: offsetX, y: offsetY, z: offsetZ };
  }

  /**
   * Ensure minimum ground clearance
   */
  public ensureGroundClearance(offsetY: number, minY: number): number {
    const minFinalY = minY + offsetY;
    const groundClearanceAdjustment = minFinalY < POSITION_CONSTANTS.MIN_GROUND_CLEARANCE 
      ? POSITION_CONSTANTS.MIN_GROUND_CLEARANCE - minFinalY 
      : 0;

    if (groundClearanceAdjustment > 0) {
    }

    return offsetY + groundClearanceAdjustment;
  }

  /**
   * Generate type number for node labeling
   */
  public generateTypeNumber(nodeType: string, counter: number): string {
    const paddedNumber = counter < 10 ? `0${counter}` : `${counter}`;
    return `${nodeType.lower()}${paddedNumber}`;
  }
}

/**
 * BaseService - Foundation class for all services with connection management
 * 
 * Provides automatic cleanup of RBXScriptConnections to prevent memory leaks.
 * All services should extend this class and use addConnection() for event subscriptions.
 */

import { IService } from "../../interfaces/IService";

export abstract class BaseService implements IService {
  /** Array to store all active connections for cleanup */
  protected connections: RBXScriptConnection[] = [];

  /** Array to store all created instances for cleanup */
  protected instances: Instance[] = [];

  /** Flag to check if service has been destroyed */
  protected isDestroyed = false;

  /** Service name for logging */
  protected serviceName: string;

  constructor(serviceName?: string) {
    this.serviceName = serviceName || "BaseService";
  }

  /**
   * Adds a connection to be managed by the service
   * The connection will be automatically disconnected when destroy() is called
   * 
   * @param connection - The RBXScriptConnection to manage
   * @returns The same connection for chaining
   */
  protected addConnection(connection: RBXScriptConnection): RBXScriptConnection {
    if (this.isDestroyed) {
      warn(`[${this.serviceName}] Attempted to add connection after service was destroyed`);
      connection.Disconnect();
      return connection;
    }

    this.connections.push(connection);
    return connection;
  }

  /**
   * Adds multiple connections to be managed by the service
   * 
   * @param connections - Array of connections to manage
   */
  protected addConnections(...connections: RBXScriptConnection[]): void {
    connections.forEach(conn => this.addConnection(conn));
  }

  /**
   * Adds an instance to be managed by the service
   * The instance will be automatically destroyed when destroy() is called
   * 
   * @param instance - The Instance to manage
   * @returns The same instance for chaining
   */
  protected addInstance<T extends Instance>(instance: T): T {
    if (this.isDestroyed) {
      warn(`[${this.serviceName}] Attempted to add instance after service was destroyed`);
      instance.Destroy();
      return instance;
    }

    this.instances.push(instance);
    return instance;
  }

  /**
   * Removes a connection from management (useful if disconnecting early)
   * 
   * @param connection - The connection to remove
   */
  protected removeConnection(connection: RBXScriptConnection): void {
    const index = this.connections.indexOf(connection);
    if (index >= 0) {
      const newConnections: RBXScriptConnection[] = [];
      for (let i = 0; i < this.connections.size(); i++) {
        if (i !== index) {
          newConnections.push(this.connections[i]);
        }
      }
      this.connections = newConnections;
    }
  }

  /**
   * Disconnects all managed connections
   */
  protected disconnectAll(): void {
    this.connections.forEach(conn => {
      try {
        conn.Disconnect();
      } catch (e) {
        warn(`[${this.serviceName}] Error disconnecting connection:`, e);
      }
    });
    this.connections = [];
  }

  /**
   * Destroys all managed instances
   */
  protected destroyAllInstances(): void {
    this.instances.forEach(instance => {
      try {
        if (instance && instance.Parent) {
          instance.Destroy();
        }
      } catch (e) {
        warn(`[${this.serviceName}] Error destroying instance:`, e);
      }
    });
    this.instances = [];
  }

  /**
   * Gets the number of active connections
   */
  protected getConnectionCount(): number {
    return this.connections.size();
  }

  /**
   * Gets the number of managed instances
   */
  protected getInstanceCount(): number {
    return this.instances.size();
  }

  /**
   * Called before destroy to allow subclasses to perform cleanup
   * Override this method in subclasses for custom cleanup logic
   */
  protected onDestroy(): void {
    // Override in subclasses
  }

  /**
   * Destroys the service and cleans up all connections and instances
   * This method should be called when the service is no longer needed
   */
  public destroy(): void {
    if (this.isDestroyed) {
      warn(`[${this.serviceName}] Service already destroyed`);
      return;
    }

    // Call subclass cleanup first
    this.onDestroy();

    // Disconnect all connections
    this.disconnectAll();

    // Destroy all instances
    this.destroyAllInstances();

    // Mark as destroyed
    this.isDestroyed = true;

  }

  /**
   * Checks if the service has been destroyed
   */
  public isServiceDestroyed(): boolean {
    return this.isDestroyed;
  }
}

