const CHECKBOX_FIELDS: CheckboxField[] = [
  { label: "Show nodes", field: "showNodes", default: true },
  { label: "Show link labels", field: "showLinkLabels", default: false },
  { label: "Show connectors", field: "showConnectors", default: true },
  { label: "Create same-layer links", field: "allowSameLevelLinks", default: false }
];

export function createVisualizationControls({
  parent,
  visualization,
  onVisualizationChange
}: VisualizationControlsProps): Frame {
  // Create container - now positioned above the buttons due to narrower width
  const container = new Instance("Frame");
  container.Name = "VisualizationControls";
  container.Size = new UDim2(1, 0, 1, 0); // Use full size of parent
  // Position above the buttons
  container.Position = new UDim2(0, 0, 0, 0); // Position will be set by layout manager
  container.BackgroundColor3 = new Color3(0.15, 0.15, 0.15);
  container.BorderSizePixel = 0;
  container.Parent = parent;

  const containerCorner = new Instance("UICorner");
  containerCorner.CornerRadius = new UDim(0, 4);
  containerCorner.Parent = container;

  const rowHeight = 30;
  const checkboxSize = 20;
  const labelOffset = 30;
  const startY = 20;
  const spacing_between = 10;
  const columnsPerRow = 2;
  const columnWidth = (1 / columnsPerRow);

  // Add title
  const title = new Instance("TextLabel");
  title.Name = "Title";
  title.Size = new UDim2(1, -20, 0, 20);
  title.Position = new UDim2(0, 10, 0, 0);
  title.BackgroundTransparency = 1;
  title.Text = "Visualization Options";
  title.TextColor3 = new Color3(0.9, 0.9, 0.9);
  title.TextScaled = false;
  title.TextSize = 14;
  title.Font = Enum.Font.SourceSansBold;
  title.TextXAlignment = Enum.TextXAlignment.Left;
  title.Parent = container;

  CHECKBOX_FIELDS.forEach((fieldDef, index) => {
    const row = math.floor(index / columnsPerRow);
    const col = index % columnsPerRow;
    const xPos = col * columnWidth;
    const yPos = startY + (row * (rowHeight + spacing_between));

    // Create checkbox frame
    const checkboxFrame = new Instance("Frame");
    checkboxFrame.Name = `${fieldDef.field}Frame`;
    checkboxFrame.Size = new UDim2(columnWidth, -10, 0, rowHeight);
    checkboxFrame.Position = new UDim2(xPos, 10, 0, yPos);
    checkboxFrame.BackgroundTransparency = 1;
    checkboxFrame.Parent = container;

    // Create checkbox button
    const checkbox = new Instance("TextButton");
    checkbox.Name = `${fieldDef.field}Checkbox`;
    checkbox.Size = new UDim2(0, checkboxSize, 0, checkboxSize);
    checkbox.Position = new UDim2(0, 0, 0.5, -checkboxSize/2);
    checkbox.BackgroundColor3 = new Color3(0.25, 0.25, 0.25);
    checkbox.BorderSizePixel = 0;
    checkbox.Text = "";
    checkbox.Parent = checkboxFrame;

    const corner = new Instance("UICorner");
    corner.CornerRadius = new UDim(0, 4);
    corner.Parent = checkbox;

    // Create checkmark
    const checkmark = new Instance("TextLabel");
    checkmark.Name = "Checkmark";
    checkmark.Size = new UDim2(1, 0, 1, 0);
    checkmark.BackgroundTransparency = 1;
    checkmark.Font = Enum.Font.SourceSansBold;
    checkmark.Text = "";
    checkmark.TextColor3 = GUI_CONSTANTS.COLORS.SUCCESS;
    checkmark.TextScaled = true;
    checkmark.Visible = visualization[fieldDef.field] as boolean;
    checkmark.Parent = checkbox;

    // Create label
    const label = new Instance("TextLabel");
    label.Size = new UDim2(1, -labelOffset, 1, 0);
    label.Position = new UDim2(0, labelOffset, 0, 0);
    label.BackgroundTransparency = 1;
    label.Font = GUI_CONSTANTS.TYPOGRAPHY.LABEL_FONT;
    label.Text = fieldDef.label;
    label.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
    label.TextSize = 14;
    label.TextXAlignment = Enum.TextXAlignment.Left;
    label.Parent = checkboxFrame;

    // Handle checkbox click
    checkbox.MouseButton1Click.Connect(() => {
      const newValue = !(visualization[fieldDef.field] as boolean);
      (visualization as any)[fieldDef.field] = newValue;
      checkmark.Visible = newValue;
      onVisualizationChange(fieldDef.field, newValue);
    });
  });

  return container;
}

import { UserInputService } from "@rbxts/services";
import { UI_CONSTANTS } from "../constants";
import type { DropdownProps } from "../types";

/**
 * Creates a dropdown functionality for a button
 */
export function createDropdown({
  button,
  currentValue,
  onChange,
  parent,
  properties
}: DropdownProps): void {
  let isOpen = false;
  let optionsFrame: Frame | undefined;

  button.MouseButton1Click.Connect(() => {
    if (!isOpen) {
      optionsFrame = createOptionsFrame(button, parent, properties, (value) => {
        // Don't update button text here - let the onChange handler do it
        onChange(value);
        closeDropdown();
      });
      isOpen = true;
    } else {
      closeDropdown();
    }
  });

  // Close when clicking elsewhere
  UserInputService.InputBegan.Connect((input) => {
    if (input.UserInputType === Enum.UserInputType.MouseButton1 && isOpen && optionsFrame) {
      wait(0.1);
      closeDropdown();
    }
  });

  function closeDropdown() {
    if (optionsFrame) {
      optionsFrame.Destroy();
      optionsFrame = undefined;
    }
    isOpen = false;
  }
}

/**
 * Creates the options frame for the dropdown
 */
function createOptionsFrame(
  button: TextButton,
  parent: Frame,
  properties: string[],
  onSelect: (value: string) => void
): Frame {
  const optionsFrame = new Instance("Frame");
  optionsFrame.Name = button.Name + "Options";

  // Position dropdown below the button
  const buttonPosition = button.Position;
  const buttonSize = button.Size;
  optionsFrame.Position = new UDim2(
    buttonPosition.X.Scale,
    buttonPosition.X.Offset,
    buttonPosition.Y.Scale,
    buttonPosition.Y.Offset + buttonSize.Y.Offset + UI_CONSTANTS.SPACING.DROPDOWN_OFFSET
  );

  const maxVisibleItems = UI_CONSTANTS.DROPDOWN.MAX_VISIBLE_ITEMS;
  const visibleHeight = math.min(properties.size() * UI_CONSTANTS.DROPDOWN.ITEM_HEIGHT, 
    maxVisibleItems * UI_CONSTANTS.DROPDOWN.ITEM_HEIGHT);

  optionsFrame.Size = new UDim2(0, button.Size.X.Offset, 0, visibleHeight);
  optionsFrame.BackgroundColor3 = UI_CONSTANTS.DROPDOWN.BACKGROUND_COLOR;
  optionsFrame.BorderSizePixel = 0;
  optionsFrame.ZIndex = 10;
  optionsFrame.Parent = parent;

  const optionsCorner = new Instance("UICorner");
  optionsCorner.CornerRadius = new UDim(0, UI_CONSTANTS.BUTTON.CORNER_RADIUS);
  optionsCorner.Parent = optionsFrame;

  // Create scrolling frame if needed
  const scrollFrame = createScrollFrame(optionsFrame, properties.size());

  // Create option buttons
  properties.forEach((property, index) => {
    createOptionButton(scrollFrame, property, index, onSelect);
  });

  return optionsFrame;
}

/**
 * Creates the scrolling frame for dropdown options
 */
function createScrollFrame(parent: Frame, itemCount: number): ScrollingFrame {
  const scrollFrame = new Instance("ScrollingFrame");
  scrollFrame.Size = new UDim2(1, 0, 1, 0);
  scrollFrame.Position = new UDim2(0, 0, 0, 0);
  scrollFrame.BackgroundTransparency = 1;
  scrollFrame.BorderSizePixel = 0;
  scrollFrame.ScrollBarThickness = UI_CONSTANTS.DROPDOWN.SCROLL_BAR_THICKNESS;
  scrollFrame.CanvasSize = new UDim2(0, 0, 0, itemCount * UI_CONSTANTS.DROPDOWN.ITEM_HEIGHT);
  scrollFrame.Parent = parent;

  return scrollFrame;
}

/**
 * Creates an option button in the dropdown
 */
function createOptionButton(
  parent: ScrollingFrame,
  property: string,
  index: number,
  onSelect: (value: string) => void
): TextButton {
  const optionButton = new Instance("TextButton");
  optionButton.Name = `Option_${property}`;
  optionButton.Text = property;
  optionButton.Position = new UDim2(0, 0, 0, index * UI_CONSTANTS.DROPDOWN.ITEM_HEIGHT);
  optionButton.Size = new UDim2(1, -4, 0, UI_CONSTANTS.DROPDOWN.ITEM_HEIGHT);
  optionButton.BackgroundTransparency = 1;
  optionButton.TextColor3 = UI_CONSTANTS.TEXT.BUTTON_COLOR;
  optionButton.Font = Enum.Font.SourceSans;
  optionButton.TextSize = UI_CONSTANTS.TEXT.LABEL_SIZE;
  optionButton.BorderSizePixel = 0;
  optionButton.ZIndex = 100;
  optionButton.Parent = parent;

  // Hover effect
  optionButton.MouseEnter.Connect(() => {
    optionButton.BackgroundTransparency = 0;
    optionButton.BackgroundColor3 = UI_CONSTANTS.BUTTON.HOVER_COLOR;
  });

  optionButton.MouseLeave.Connect(() => {
    optionButton.BackgroundTransparency = 1;
  });

  // Selection
  optionButton.MouseButton1Click.Connect(() => {
    onSelect(property);
  });

  return optionButton;
}

