/**
 * Animation Test GUI Controller
 * Handles the client-side GUI for the animation test
 */

import { Players, ReplicatedStorage } from "@rbxts/services";

// GUI configuration
const GUI_CONFIG = {
  BUTTON: {
    SIZE: new UDim2(0, 200, 0, 50),
    POSITION: new UDim2(1, -220, 0, 20), // Upper right with 20px margin
    TEXT: "Move Red Hexes to Blue",
    COLORS: {
      DEFAULT: new Color3(0.2, 0.6, 0.2),
      HOVER: new Color3(0.3, 0.7, 0.3),
      DISABLED: new Color3(0.5, 0.5, 0.5)
    }
  }
};

export class AnimationTestGUIController {
  private gui?: ScreenGui;
  private button?: TextButton;
  private remoteEvent?: RemoteEvent;
  private isAnimating = false;

  /**
   * Initializes the animation test GUI
   */
  public initialize(): void {

    // Wait for remote event
    const remoteEvent = ReplicatedStorage.WaitForChild("AnimationTestRemote", 5) as RemoteEvent | undefined;
    if (!remoteEvent) {
      warn("AnimationTestRemote not found - animation test may not be enabled");
      return;
    }

    this.remoteEvent = remoteEvent;

    // Set up event listeners
    this.setupEventListeners();

    // Create GUI
    this.createGUI();
  }

  /**
   * Sets up remote event listeners
   */
  private setupEventListeners(): void {
    if (!this.remoteEvent) return;

    this.remoteEvent.OnClientEvent.Connect((eventType: string) => {
      if (eventType === "animationStarted") {
        this.isAnimating = true;
        this.updateButtonState();
      } else if (eventType === "animationCompleted") {
        this.isAnimating = false;
        this.updateButtonState();
      }
    });
  }

  /**
   * Creates the GUI with move button
   */
  private createGUI(): void {
    const player = Players.LocalPlayer;
    const playerGui = player.WaitForChild("PlayerGui") as PlayerGui;

    // Create ScreenGui
    this.gui = new Instance("ScreenGui");
    this.gui.Name = "AnimationTestGUI";
    this.gui.ResetOnSpawn = false;

    // Create transparent frame container
    const frame = new Instance("Frame");
    frame.Size = new UDim2(1, 0, 1, 0);
    frame.BackgroundTransparency = 1;
    frame.Parent = this.gui;

    // Create button
    this.button = new Instance("TextButton");
    this.button.Size = GUI_CONFIG.BUTTON.SIZE;
    this.button.Position = GUI_CONFIG.BUTTON.POSITION;
    this.button.Text = GUI_CONFIG.BUTTON.TEXT;
    this.button.Font = Enum.Font.SourceSansBold;
    this.button.TextScaled = true;
    this.button.TextColor3 = new Color3(1, 1, 1);
    this.button.BackgroundColor3 = GUI_CONFIG.BUTTON.COLORS.DEFAULT;
    this.button.BorderSizePixel = 0;
    this.button.Parent = frame;

    // Add corner rounding
    const uiCorner = new Instance("UICorner");
    uiCorner.CornerRadius = new UDim(0, 8);
    uiCorner.Parent = this.button;

    // Add hover effects
    this.button.MouseEnter.Connect(() => {
      if (!this.isAnimating && this.button) {
        this.button.BackgroundColor3 = GUI_CONFIG.BUTTON.COLORS.HOVER;
      }
    });

    this.button.MouseLeave.Connect(() => {
      if (!this.isAnimating && this.button) {
        this.button.BackgroundColor3 = GUI_CONFIG.BUTTON.COLORS.DEFAULT;
      }
    });

    // Connect click event
    this.button.MouseButton1Click.Connect(() => this.onButtonClick());

    // Parent to PlayerGui
    this.gui.Parent = playerGui;

  }

  /**
   * Handles button click
   */
  private onButtonClick(): void {
    if (this.isAnimating || !this.remoteEvent) return;

    // Send request to server
    this.remoteEvent.FireServer("triggerAnimation");
  }

  /**
   * Updates button state based on animation status
   */
  private updateButtonState(): void {
    if (!this.button) return;

    if (this.isAnimating) {
      this.button.BackgroundColor3 = GUI_CONFIG.BUTTON.COLORS.DISABLED;
      this.button.Text = "Animating...";
    } else {
      this.button.BackgroundColor3 = GUI_CONFIG.BUTTON.COLORS.DEFAULT;
      this.button.Text = GUI_CONFIG.BUTTON.TEXT;
    }
  }

  /**
   * Cleans up the GUI
   */
  public destroy(): void {
    if (this.gui) {
      this.gui.Destroy();
      this.gui = undefined;
      this.button = undefined;
    }
  }
}

import { EnhancedGeneratorConfig } from "../../shared/interfaces/enhancedGenerator.interface";

export interface GraphInitConfig {
  initialPosition: { x: number; y: number; z: number };
  defaultConfig: EnhancedGeneratorConfig;
}

export class GraphInitializerService {
  private configGUIServer?: unknown;

  private readonly DEFAULT_CONFIG: EnhancedGeneratorConfig = {
    numNodeTypes: 4,
    numLinkTypes: 3,
    layers: [
      {
        layerNumber: 1,
        numNodes: 4, // Test with more nodes in first layer
        connectionsPerNode: 0, // Test with no intra-layer connections
      },
      {
        layerNumber: 2,
        numNodes: 8,
        connectionsPerNode: 1,
      },
      {
        layerNumber: 3,
        numNodes: 12,
        connectionsPerNode: 0, // Test sparse connectivity
      },
      {
        layerNumber: 4,
        numNodes: 16,
        connectionsPerNode: 2,
      },
      {
        layerNumber: 5,
        numNodes: 20,
        connectionsPerNode: 0, // Test last layer with no connections
      },
    ],
    spacing: {
      nodeHeight: 0.5,
      nodeRadius: 0.5,
      layerSpacing: 5,
      // layerSpacing: 0.5,
      nodeSpacing: 1,
      swimlaneSpacing: 1,
      linkDiameter: 0.1,
    },
    visualization: {
      showNodes: true,
      showLinkLabels: false,
      showConnectors: true,
      allowSameLevelLinks: true, // Enable same-level links for testing
    },
    // Default axis mapping for Harness data - will be updated when properties are discovered
    axisMapping: {
      xAxis: "httpMethod",
      zAxis: "apiPattern",
      yAxis: "component",
    },
    visualMapping: {
      backgroundColor: "httpMethod", // Default to httpMethod for Harness data
      borderColor: "none",
    },
    yAxisConfig: {
      useLayer: true,
      property: undefined,
    },
    numPetTypes: 5,
    maxDataItems: 1000, // Maximum number of items to use from test data
  };

  private readonly DEFAULT_POSITION = { x: 0, y: 0, z: 0 };

  constructor() {}

  public setConfigGUIServer(configGUIServer: unknown): void {
    this.configGUIServer = configGUIServer;
  }

  public initializeGraph(customConfig?: Partial<GraphInitConfig>): void {
    // Merge custom config with defaults
    const mergedDefaultConfig = {
      ...this.DEFAULT_CONFIG,
      ...customConfig?.defaultConfig,
    };

    const config: GraphInitConfig = {
      initialPosition: customConfig?.initialPosition || this.DEFAULT_POSITION,
      defaultConfig: mergedDefaultConfig,
    };

    wait(2);
    this.triggerGraphGeneration(config);
  }

  public initializeGraphWithMaxItems(maxDataItems: number): void {
    print(
      `[GraphInitializer] Initializing graph with max ${maxDataItems} items`
    );

    const configWithMaxItems = {
      ...this.DEFAULT_CONFIG,
      maxDataItems,
    };

    const config: GraphInitConfig = {
      initialPosition: this.DEFAULT_POSITION,
      defaultConfig: configWithMaxItems,
    };

    wait(2);
    this.triggerGraphGeneration(config);
  }

  private triggerGraphGeneration(config: GraphInitConfig): void {
    if (!this.configGUIServer) {
      warn(
        " ConfigGUIServerService not set, cannot trigger graph generation"
      );
      return;
    }

    const players = game.GetService("Players").GetPlayers();

    if (players.size() > 0) {
      players.forEach((player) => {
        this.sendInitialConfig(player, config);
      });
    }

    game.GetService("Players").PlayerAdded.Connect((player) => {
      wait(2);
      this.sendInitialConfig(player, config);
    });
  }

  private sendInitialConfig(player: Player, config: GraphInitConfig): void {
    const remoteEvent = game
      .GetService("ReplicatedStorage")
      .FindFirstChild("ConfigGUIRemote") as RemoteEvent;
    if (!remoteEvent) {
      warn(" ConfigGUIRemote not found, cannot send initial configuration");
      return;
    }

    remoteEvent.FireClient(player, "initialConfig", config.defaultConfig);

    wait(1);
    remoteEvent.FireClient(player, "triggerGeneration", config.defaultConfig);
  }
}

/**
 * Node Organizer for clustering and grouping nodes
 * Part of F002 Phase 2 refactoring - T5
 */

import { Cluster, Node } from "../../../../interfaces/simpleDataGenerator.interface";
import { resolvePropertyValue } from "../../../../utils/nodePropertyHelpers";

export interface OrganizedNodes {
  nodesByLayer: Map<number, Node[]>;
  nodesByTypeAndLayer: Map<string, Node[]>;
  typeCounters: Map<string, number>;
}

export class NodeOrganizer {
  /**
   * Organize nodes by layer and property value
   */
  public organizeByLayerAndProperty(cluster: Cluster, propertyName: string): OrganizedNodes {
    const nodesByLayer = new Map<number, Node[]>();
    const typeCounters = new Map<string, number>();
    const nodesByTypeAndLayer = new Map<string, Node[]>();

    // First organize by layer
    this.organizeByLayer(cluster, nodesByLayer);

    // Then organize by property and layer
    this.organizeByPropertyAndLayer(nodesByLayer, propertyName, nodesByTypeAndLayer, typeCounters);

    return { nodesByLayer, nodesByTypeAndLayer, typeCounters };
  }

  /**
   * Sort property values by their node count
   */
  public sortByCount(typeCounters: Map<string, number>): string[] {
    const sortedTypes: string[] = [];
    typeCounters.forEach((_, nodeType) => {
      sortedTypes.push(nodeType);
    });

    // Use table.sort for stable sorting
    table.sort(sortedTypes, (a, b) => {
      const countA = typeCounters.get(a) || 0;
      const countB = typeCounters.get(b) || 0;
      return countB > countA;
    });

    return sortedTypes;
  }

  /**
   * Sort nodes within a group
   */
  public sortNodesAlphabetically(nodes: Node[]): void {
    table.sort(nodes, (a, b) => a.name < b.name);
  }

  /**
   * Get unique property values from nodes
   */
  public getUniquePropertyValues(nodes: Map<string, Node[]>, propertyName: string): Set<string> {
    const uniqueValues = new Set<string>();

    nodes.forEach(nodeList => {
      nodeList.forEach(node => {
        const value = resolvePropertyValue(node, propertyName);
        uniqueValues.add(value);
      });
    });

    return uniqueValues;
  }

  /**
   * Find max nodes for a type across all layers
   */
  public findMaxNodesInLayer(
    nodesByTypeAndLayer: Map<string, Node[]>, 
    nodeType: string, 
    numLayers: number
  ): number {
    let maxNodesInLayer = 0;

    for (let layer = 1; layer <= numLayers; layer++) {
      const key = `${nodeType}-${layer}`;
      const nodes = nodesByTypeAndLayer.get(key) || [];
      maxNodesInLayer = math.max(maxNodesInLayer, nodes.size());
    }

    return maxNodesInLayer;
  }

  /**
   * Organize nodes by layer
   */
  private organizeByLayer(cluster: Cluster, nodesByLayer: Map<number, Node[]>): void {
    cluster.groups.forEach(group => {
      group.nodes.forEach((node) => {
        const nodeWithLevel = node as Node & { level?: number };
        const layer = nodeWithLevel.level || 1;

        if (!nodesByLayer.has(layer)) {
          nodesByLayer.set(layer, []);
        }
        nodesByLayer.get(layer)!.push(node);
      });
    });
  }

  /**
   * Organize nodes by property value and layer
   */
  private organizeByPropertyAndLayer(
    nodesByLayer: Map<number, Node[]>,
    propertyName: string,
    nodesByTypeAndLayer: Map<string, Node[]>,
    typeCounters: Map<string, number>
  ): void {
    nodesByLayer.forEach((nodes, layer) => {
      nodes.forEach(node => {
        const propertyValue = resolvePropertyValue(node, propertyName);
        const key = `${propertyValue}-${layer}`;

        if (!nodesByTypeAndLayer.has(key)) {
          nodesByTypeAndLayer.set(key, []);
        }
        nodesByTypeAndLayer.get(key)!.push(node);

        // Track total count per property value
        typeCounters.set(propertyValue, (typeCounters.get(propertyValue) || 0) + 1);
      });
    });
  }
}

/**
 * Bounds Calculator for cluster and node positioning
 * Part of F002 Phase 2 refactoring - T5
 */

import { Cluster, Node } from "../../../../interfaces/simpleDataGenerator.interface";
import { POSITION_CONSTANTS } from "../../constants/positionConstants";

export interface Bounds {
  minX: number;
  maxX: number;
  minY: number;
  minZ: number;
  maxZ: number;
}

export class BoundsCalculator {
  /**
   * Calculate bounds for a cluster of nodes
   */
  public calculateClusterBounds(cluster: Cluster): Bounds {
    if (!this.validateCluster(cluster)) {
      return this.getEmptyBounds();
    }

    let minX = POSITION_CONSTANTS.BOUNDS.INITIAL_MIN;
    let maxX = POSITION_CONSTANTS.BOUNDS.INITIAL_MAX;
    let minY = POSITION_CONSTANTS.BOUNDS.INITIAL_MIN;
    let minZ = POSITION_CONSTANTS.BOUNDS.INITIAL_MIN;
    let maxZ = POSITION_CONSTANTS.BOUNDS.INITIAL_MAX;

    cluster.groups[0].nodes.forEach(node => {
      if (!node.position) {
        warn(`[BoundsCalculator] Node ${node.name} has no position`);
        return;
      }

      minX = math.min(minX, node.position.x);
      maxX = math.max(maxX, node.position.x);
      minY = math.min(minY, node.position.y);
      minZ = math.min(minZ, node.position.z);
      maxZ = math.max(maxZ, node.position.z);
    });

    // Handle case where no valid positions were found
    if (minX === POSITION_CONSTANTS.BOUNDS.INITIAL_MIN) {
      return this.getEmptyBounds();
    }

    const bounds = { minX, maxX, minY, minZ, maxZ };
    this.logBounds(bounds, cluster.groups[0].nodes.size());

    return bounds;
  }

  /**
   * Calculate bounds for a set of nodes
   */
  public calculateNodeSetBounds(nodes: Node[]): Bounds {
    if (nodes.size() === 0) {
      return this.getEmptyBounds();
    }

    let minX = POSITION_CONSTANTS.BOUNDS.INITIAL_MIN;
    let maxX = POSITION_CONSTANTS.BOUNDS.INITIAL_MAX;
    let minZ = POSITION_CONSTANTS.BOUNDS.INITIAL_MIN;
    let maxZ = POSITION_CONSTANTS.BOUNDS.INITIAL_MAX;

    nodes.forEach(node => {
      if (!node.position) return;

      minX = math.min(minX, node.position.x);
      maxX = math.max(maxX, node.position.x);
      minZ = math.min(minZ, node.position.z);
      maxZ = math.max(maxZ, node.position.z);
    });

    // Handle case where no valid positions were found
    if (minX === POSITION_CONSTANTS.BOUNDS.INITIAL_MIN) {
      return this.getEmptyBounds();
    }

    return { 
      minX, 
      maxX, 
      minY: 0, // Node sets don't track Y bounds
      minZ, 
      maxZ 
    };
  }

  /**
   * Calculate center point of bounds
   */
  public calculateCenter(bounds: Bounds): Vector3 {
    return new Vector3(
      (bounds.minX + bounds.maxX) / 2,
      bounds.minY,
      (bounds.minZ + bounds.maxZ) / 2
    );
  }

  /**
   * Calculate dimensions from bounds
   */
  public calculateDimensions(bounds: Bounds): Vector3 {
    return new Vector3(
      bounds.maxX - bounds.minX,
      0, // Height not calculated from bounds
      bounds.maxZ - bounds.minZ
    );
  }

  /**
   * Get empty bounds object
   */
  private getEmptyBounds(): Bounds {
    return {
      minX: 0,
      maxX: 0,
      minY: 0,
      minZ: 0,
      maxZ: 0
    };
  }

  /**
   * Validate cluster structure
   */
  private validateCluster(cluster: Cluster): boolean {
    if (!cluster) {
      warn("[BoundsCalculator] Invalid cluster: cluster is null or undefined");
      return false;
    }

    if (!cluster.groups || cluster.groups.size() === 0) {
      warn("[BoundsCalculator] Invalid cluster: no groups found");
      return false;
    }

    if (!cluster.groups[0].nodes) {
      warn("[BoundsCalculator] Invalid cluster: first group has no nodes array");
      return false;
    }

    return true;
  }

  /**
   * Log bounds information
   */
  private logBounds(bounds: Bounds, nodeCount: number): void {
  }
}

