import { GUI_CONSTANTS } from "../constants";
import type { SpacingConfig } from "../../../../shared/interfaces/enhancedGenerator.interface";

interface SpacingControlsProps {
  parent: Frame;
  spacing: SpacingConfig;
  onSpacingChange: (field: keyof SpacingConfig, value: number) => void;
}

interface SpacingField {
  label: string;
  field: keyof SpacingConfig;
  min: number;
  max: number;
  default: number;
}

const SPACING_FIELDS: SpacingField[] = [
  { label: "Node Height:", field: "nodeHeight", min: 0.1, max: 100, default: GUI_CONSTANTS.SPACING_DEFAULTS.NODE_HEIGHT },
  { label: "Node Radius:", field: "nodeRadius", min: 0.1, max: 20, default: GUI_CONSTANTS.SPACING_DEFAULTS.NODE_RADIUS },
  { label: "Layer Spacing:", field: "layerSpacing", min: 0.1, max: 200, default: GUI_CONSTANTS.SPACING_DEFAULTS.LAYER_SPACING },
  { label: "Node Spacing:", field: "nodeSpacing", min: 0.1, max: 100, default: GUI_CONSTANTS.SPACING_DEFAULTS.NODE_SPACING },
  { label: "Swimlane Spacing:", field: "swimlaneSpacing", min: 0.1, max: 100, default: GUI_CONSTANTS.SPACING_DEFAULTS.SWIMLANE_SPACING },
  { label: "Link Diameter:", field: "linkDiameter", min: 0.1, max: 10, default: GUI_CONSTANTS.SPACING_DEFAULTS.LINK_DIAMETER },
  { label: "Origin Y Offset:", field: "originYOffset", min: -100, max: 100, default: 0 }
];

export function createSpacingControls({
  parent,
  spacing,
  onSpacingChange
}: SpacingControlsProps): void {
  const rowHeight = 25;
  const labelWidth = 100;
  const inputWidth = 50;
  const startY = 35;
  const spacing_between = 5;
  const columnGap = 20;

  // Calculate column positions
  const col1X = 10;
  const col2X = col1X + labelWidth + inputWidth + columnGap;

  // Determine rows per column
  const itemsPerColumn = math.ceil(SPACING_FIELDS.size() / 2);

  SPACING_FIELDS.forEach((fieldDef, index) => {
    // Determine column and row
    const column = index < itemsPerColumn ? 0 : 1;
    const rowIndex = column === 0 ? index : index - itemsPerColumn;
    const yPos = startY + (rowIndex * (rowHeight + spacing_between));
    const xPos = column === 0 ? col1X : col2X;

    // Create label
    const label = new Instance("TextLabel");
    label.Size = new UDim2(0, labelWidth, 0, rowHeight);
    label.Position = new UDim2(0, xPos, 0, yPos);
    label.BackgroundTransparency = 1;
    label.Font = GUI_CONSTANTS.TYPOGRAPHY.LABEL_FONT;
    label.Text = fieldDef.label;
    label.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
    label.TextScaled = true;
    label.TextXAlignment = Enum.TextXAlignment.Left;
    label.Parent = parent;

    // Create input box
    const input = new Instance("TextBox");
    input.Name = `${fieldDef.field}Input`;
    input.Size = new UDim2(0, inputWidth, 0, rowHeight);
    input.Position = new UDim2(0, xPos + labelWidth + 5, 0, yPos);
    input.BackgroundColor3 = new Color3(0.25, 0.25, 0.25);
    input.BorderSizePixel = 0;
    input.Font = GUI_CONSTANTS.TYPOGRAPHY.INPUT_FONT;
    input.Text = tostring(spacing[fieldDef.field] ?? fieldDef.default);
    input.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
    input.TextScaled = true;
    input.Parent = parent;

    const corner = new Instance("UICorner");
    corner.CornerRadius = new UDim(0, 4);
    corner.Parent = input;

    // Input validation
    input.FocusLost.Connect(() => {
      const value = tonumber(input.Text);
      if (value && value >= fieldDef.min && value <= fieldDef.max) {
        // Keep decimal precision for all fields now that minimum is 0.1
        onSpacingChange(fieldDef.field, value);
        input.Text = tostring(value);
      } else {
        // Revert to current value
        input.Text = tostring(spacing[fieldDef.field] ?? fieldDef.default);
      }
    });
  });
}

/**
 * String validation utilities for input sanitization and security
 */

interface StringValidationOptions {
  maxLength?: number;
  minLength?: number;
  allowEmpty?: boolean;
  blockSpecialChars?: boolean;
  customPattern?: string;
  trimWhitespace?: boolean;
}

export interface ValidationResult {
  isValid: boolean;
  error?: string;
  sanitized?: string;
}

// Default limits based on common Roblox constraints
export const STRING_LIMITS = {
  NODE_NAME: 50,
  LABEL_TEXT: 100,
  DESCRIPTION: 200,
  SHORT_TEXT: 25,
  MAX_SAFE_LENGTH: 1000,
} as const;

// Pattern for detecting potentially malicious characters
// Note: In Roblox Lua, we use string patterns instead of regex



/**
 * General string validation with configurable options
 */
export function validateString(
  input: string | undefined,
  options: StringValidationOptions = {}
): ValidationResult {
  // Handle undefined
  if (input === undefined) {
    if (options.allowEmpty) {
      return { isValid: true, sanitized: "" };
    }
    return { isValid: false, error: "Input is required" };
  }

  // Ensure input is a string
  if (!typeIs(input, "string")) {
    return { isValid: false, error: "Input must be a string" };
  }

  let sanitized = input;

  // Trim whitespace if requested
  if (options.trimWhitespace) {
    // Use Roblox string matching to trim
    sanitized = string.match(sanitized, "^%s*(.-)%s*$")[0] as string || sanitized;
  }

  // Check empty string
  if (sanitized.size() === 0 && !options.allowEmpty) {
    return { isValid: false, error: "Input cannot be empty" };
  }

  // Check minimum length
  if (options.minLength !== undefined && sanitized.size() < options.minLength) {
    return {
      isValid: false,
      error: `Input must be at least ${options.minLength} characters`,
    };
  }

  // Check maximum length
  const maxLength = options.maxLength || STRING_LIMITS.MAX_SAFE_LENGTH;
  if (sanitized.size() > maxLength) {
    return {
      isValid: false,
      error: `Input exceeds maximum length of ${maxLength} characters`,
    };
  }

  // Check for injection attempts
  if (string.match(sanitized, "[<>\"'&\\]")[0] !== undefined) {
    if (options.blockSpecialChars) {
      // Remove dangerous characters
      sanitized = string.gsub(sanitized, "[<>\"'&\\]", "")[0];
      // Recheck length after sanitization
      if (sanitized.size() === 0 && !options.allowEmpty) {
        return {
          isValid: false,
          error: "Input contains only invalid characters",
        };
      }
    } else {
      return {
        isValid: false,
        error: "Input contains potentially dangerous characters",
      };
    }
  }

  // Apply custom pattern if provided
  if (options.customPattern) {
    if (!string.match(sanitized, options.customPattern)[0]) {
      return {
        isValid: false,
        error: "Input does not match required pattern",
      };
    }
  }

  // Remove all special characters if requested
  if (options.blockSpecialChars) {
    sanitized = string.gsub(sanitized, "[^a-zA-Z0-9\s\-_.,!?]", " ")[0];
    // Clean up multiple spaces
    sanitized = string.gsub(sanitized, "%s+", " ")[0];
    // Trim again
    sanitized = string.match(sanitized, "^%s*(.-)%s*$")[0] as string || sanitized;
  }

  return {
    isValid: true,
    sanitized,
  };
}

/**
 * Sanitizes a string by removing dangerous characters
 */
export function sanitizeString(input: string, maxLength?: number): string {
  const result = validateString(input, {
    maxLength: maxLength || STRING_LIMITS.MAX_SAFE_LENGTH,
    blockSpecialChars: true,
    trimWhitespace: true,
    allowEmpty: true,
  });

  return result.sanitized || "";
}

import { UI_CONSTANTS, getAvailableProperties, getPropertyValueCount } from "../constants";
import { createButton, createLabel, createSectionLabel } from "../utils/layoutManager";

import { createDropdown } from "./dropdown";

interface AxisMappingSectionProps {
  parent: Frame;
  xAxisValue: string;
  zAxisValue: string;
  yAxisValue: string;
  onXAxisChange: (value: string) => void;
  onZAxisChange: (value: string) => void;
  onYAxisChange: (value: string) => void;
}

/**
 * Creates the axis mapping section of the controls
 */
export function createAxisMappingSection({
  parent,
  xAxisValue,
  zAxisValue,
  yAxisValue,
  onXAxisChange,
  onZAxisChange,
  onYAxisChange
}: AxisMappingSectionProps): void {
  const availableProps = getAvailableProperties();

  // Format properties with counts
  const propsWithCounts = availableProps.map(prop => {
    const count = getPropertyValueCount(prop);
    return count > 0 ? `${prop} [${count}]` : prop;
  });



  // Section label
  createSectionLabel(parent, "Spatial Grouping", 5);

  // X position grouping row
  createLabel(
    parent,
    "X group:",
    new UDim2(0, UI_CONSTANTS.SPACING.SECTION_MARGIN, 0, 25),
    50
  );

  // Helper to format button text with count
  const formatButtonText = (prop: string): string => {
    const count = getPropertyValueCount(prop);
    return count > 0 ? `${prop} [${count}]` : prop;
  };

  const xAxisButton = createButton(
    parent,
    "XAxisButton",
    formatButtonText(xAxisValue),
    new UDim2(0, 65, 0, 25),
    UI_CONSTANTS.SPACING.DROPDOWN_WIDTH
  );

  // Z position grouping row
  createLabel(
    parent,
    "Z group:",
    new UDim2(0, UI_CONSTANTS.SPACING.SECTION_MARGIN, 0, 50),
    50
  );

  const zAxisButton = createButton(
    parent,
    "ZAxisButton",
    formatButtonText(zAxisValue),
    new UDim2(0, 65, 0, 50),
    UI_CONSTANTS.SPACING.DROPDOWN_WIDTH
  );

  // Y position grouping row
  createLabel(
    parent,
    "Y group:",
    new UDim2(0, UI_CONSTANTS.SPACING.SECTION_MARGIN, 0, 75),
    50
  );

  const yAxisButton = createButton(
    parent,
    "YAxisButton",
    formatButtonText(yAxisValue),
    new UDim2(0, 65, 0, 75),
    UI_CONSTANTS.SPACING.DROPDOWN_WIDTH
  );

  // Helper to extract property name from display string
  const extractPropertyName = (displayValue: string): string => {
    // Remove the count suffix if present (e.g., "service [15]" -> "service")
    const match = displayValue.match("^([^%[]+)%s*%[?");
    if (match && match[1]) {
      const result = match[1];
      return typeIs(result, "string") ? result : tostring(result);
    }
    return displayValue;
  };

  // Create dropdown functionality
  createDropdown({
    button: xAxisButton,
    currentValue: xAxisValue,
    onChange: (value) => {
      const propName = extractPropertyName(value);
      xAxisButton.Text = formatButtonText(propName);
      onXAxisChange(propName);
    },
    parent: parent,
    properties: propsWithCounts
  });

  createDropdown({
    button: zAxisButton,
    currentValue: zAxisValue,
    onChange: (value) => {
      const propName = extractPropertyName(value);
      zAxisButton.Text = formatButtonText(propName);
      onZAxisChange(propName);
    },
    parent: parent,
    properties: propsWithCounts
  });

  createDropdown({
    button: yAxisButton,
    currentValue: yAxisValue,
    onChange: (value) => {
      const propName = extractPropertyName(value);
      yAxisButton.Text = formatButtonText(propName);
      onYAxisChange(propName);
    },
    parent: parent,
    properties: propsWithCounts
  });
}

