/**
 * Type guard functions
 */
export function isNode(obj: unknown): obj is Node {
  if (!obj || typeOf(obj) !== "table") return false;
  const data = obj as { uuid?: unknown; position?: { x?: unknown } };
  return typeIs(data.uuid, "string") && 
         data.position !== undefined &&
         typeIs(data.position.x, "number");
}

export function isLink(obj: unknown): obj is Link {
  if (!obj || typeOf(obj) !== "table") return false;
  const data = obj as { uuid?: unknown; sourceNodeUuid?: unknown; targetNodeUuid?: unknown };
  return typeIs(data.uuid, "string") && 
         typeIs(data.sourceNodeUuid, "string") && 
         typeIs(data.targetNodeUuid, "string");
}

export function isCluster(obj: unknown): obj is Cluster {
  if (!obj || typeOf(obj) !== "table") return false;
  const data = obj as { groups?: unknown; relations?: unknown };
  return typeIs(data.groups, "table") && 
         typeIs(data.relations, "table");
}

/**
 * Standardized interfaces for label block maker
 * Following the IMaker pattern
 */

import { ILabelMakerConfig } from "../../interfaces/IMaker";

/**
 * Label configuration for each face
 */
export interface IFaceLabelConfig {
  text: string;
  textColor?: Color3;
  backgroundColor?: Color3;
  borderColor?: Color3;
}

/**
 * Standardized configuration for label block creation
 */
export interface ILabelBlockMakerConfig extends ILabelMakerConfig {
  /** Size of the block (cube) */
  size?: number;

  /** Rotation of the block */
  rotation?: Vector3;

  /** Labels for each face */
  labels?: {
    front?: IFaceLabelConfig;
    back?: IFaceLabelConfig;
    left?: IFaceLabelConfig;
    right?: IFaceLabelConfig;
    top?: IFaceLabelConfig;
    bottom?: IFaceLabelConfig;
  };

  /** Block color */
  color?: Color3;

  /** Block material */
  material?: Enum.Material;

  /** Block transparency */
  transparency?: number;

  /** Whether the block is anchored */
  anchored?: boolean;

  /** Whether the block casts shadows */
  castShadow?: boolean;

  /** Override text box properties */
  textBoxOverrides?: {
    textSize?: number;
    font?: Enum.Font;
    borderSizePixel?: number;
  };
}

/**
 * Legacy label block configuration (for backward compatibility during migration)
 */
export interface LabelBlockConfigLegacy {
  id: string | number;
  position?: { x: number; y: number; z: number };
  rotation?: { x: number; y: number; z: number };
  props?: {
    Size?: number;
    Color?: [number, number, number];
    Material?: string;
    Anchored?: boolean;
    Transparency?: number;
    CastShadow?: boolean;
    [key: string]: any;
  };
  labels?: {
    [face: string]: {
      text: string;
      textColor?: Color3;
      backgroundColor?: Color3;
      borderColor?: Color3;
    };
  };
  textBoxOverrides?: {
    textSize?: number;
    font?: Enum.Font;
    borderSizePixel?: number;
  };
  parent?: Instance;
}

/**
 * Converts legacy config to standardized config
 */
export function convertLegacyLabelBlockConfig(legacy: LabelBlockConfigLegacy): ILabelBlockMakerConfig {
  const config: ILabelBlockMakerConfig = {
    id: legacy.id,
    parent: legacy.parent,
    text: "", // Will be set from labels
    textBoxOverrides: legacy.textBoxOverrides,
  };

  // Convert position
  if (legacy.position) {
    config.position = new Vector3(
      legacy.position.x,
      legacy.position.y,
      legacy.position.z
    );
  }

  // Convert rotation
  if (legacy.rotation) {
    config.rotation = new Vector3(
      legacy.rotation.x,
      legacy.rotation.y,
      legacy.rotation.z
    );
  }

  // Convert props
  if (legacy.props) {
    if (legacy.props.Size !== undefined) {
      config.size = legacy.props.Size;
    }
    if (legacy.props.Color) {
      config.color = new Color3(
        legacy.props.Color[0],
        legacy.props.Color[1],
        legacy.props.Color[2]
      );
    }
    if (legacy.props.Material) {
      config.material = legacy.props.Material as unknown as Enum.Material;
    }
    if (legacy.props.Transparency !== undefined) {
      config.transparency = legacy.props.Transparency;
    }
    if (legacy.props.Anchored !== undefined) {
      config.anchored = legacy.props.Anchored;
    }
    if (legacy.props.CastShadow !== undefined) {
      config.castShadow = legacy.props.CastShadow;
    }
  }

  // Convert labels
  if (legacy.labels) {
    config.labels = legacy.labels as any; // Labels are already in the right format
  }

  return config;
}

import { GUI_CONSTANTS } from "../constants";

interface DropdownProps {
  parent: Frame;
  position: UDim2;
  size: UDim2;
  items: string[];
  defaultValue: string;
  onChange: (value: string) => void;
}

export function createDropdown({
  parent,
  position,
  size,
  items,
  defaultValue,
  onChange
}: DropdownProps): TextButton {
  // Create dropdown button
  const dropdown = new Instance("TextButton");
  dropdown.Name = "Dropdown";
  dropdown.Size = size;
  dropdown.Position = position;
  dropdown.BackgroundColor3 = GUI_CONSTANTS.COLORS.BUTTON.DEFAULT;
  dropdown.BorderSizePixel = 0;
  dropdown.Font = GUI_CONSTANTS.TYPOGRAPHY.INPUT_FONT;
  dropdown.Text = defaultValue;
  dropdown.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  dropdown.TextScaled = true;
  dropdown.Parent = parent;

  // Add corner radius
  const corner = new Instance("UICorner");
  corner.CornerRadius = new UDim(0, 4);
  corner.Parent = dropdown;

  // Create dropdown list (initially hidden)
  const listFrame = new Instance("Frame");
  listFrame.Name = "DropdownList";
  listFrame.Size = new UDim2(1, 0, 0, math.min(items.size() * 25, 200));
  listFrame.Position = new UDim2(0, 0, 1, 2);
  listFrame.BackgroundColor3 = GUI_CONSTANTS.COLORS.BACKGROUND;
  listFrame.BorderSizePixel = 0;
  listFrame.Visible = false;
  listFrame.ZIndex = 10;
  listFrame.Parent = dropdown;

  const listCorner = new Instance("UICorner");
  listCorner.CornerRadius = new UDim(0, 4);
  listCorner.Parent = listFrame;

  // Create scrolling frame
  const scrollFrame = new Instance("ScrollingFrame");
  scrollFrame.Size = new UDim2(1, -4, 1, -4);
  scrollFrame.Position = new UDim2(0, 2, 0, 2);
  scrollFrame.BackgroundTransparency = 1;
  scrollFrame.BorderSizePixel = 0;
  scrollFrame.ScrollBarThickness = 4;
  scrollFrame.CanvasSize = new UDim2(0, 0, 0, items.size() * 25);
  scrollFrame.Parent = listFrame;

  // Create list layout
  const listLayout = new Instance("UIListLayout");
  listLayout.SortOrder = Enum.SortOrder.LayoutOrder;
  listLayout.Parent = scrollFrame;

  // Create items
  items.forEach((item, index) => {
    const itemButton = new Instance("TextButton");
    itemButton.Name = `Item_${index}`;
    itemButton.Size = new UDim2(1, 0, 0, 25);
    itemButton.BackgroundColor3 = GUI_CONSTANTS.COLORS.BACKGROUND;
    itemButton.BorderSizePixel = 0;
    itemButton.Font = GUI_CONSTANTS.TYPOGRAPHY.INPUT_FONT;
    itemButton.Text = item;
    itemButton.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
    itemButton.TextScaled = true;
    itemButton.Parent = scrollFrame;

    // Hover effect
    itemButton.MouseEnter.Connect(() => {
      itemButton.BackgroundColor3 = GUI_CONSTANTS.COLORS.BUTTON.HOVER;
    });

    itemButton.MouseLeave.Connect(() => {
      itemButton.BackgroundColor3 = GUI_CONSTANTS.COLORS.BACKGROUND;
    });

    // Click handler
    itemButton.MouseButton1Click.Connect(() => {
      dropdown.Text = item;
      listFrame.Visible = false;
      onChange(item);
    });
  });

  // Toggle dropdown on click
  let isOpen = false;
  dropdown.MouseButton1Click.Connect(() => {
    isOpen = !isOpen;
    listFrame.Visible = isOpen;
  });

  // Close dropdown when clicking elsewhere
  const userInputService = game.GetService("UserInputService");
  userInputService.InputBegan.Connect((input) => {
    if (input.UserInputType === Enum.UserInputType.MouseButton1 && isOpen) {
      wait(0.1); // Small delay to prevent immediate closing
      isOpen = false;
      listFrame.Visible = false;
    }
  });

  return dropdown;
}

/**
 * Validation Handlers for Configuration GUI
 * 
 * Provides comprehensive validation for all configuration inputs
 * to ensure data integrity and prevent invalid states.
 */

import { EnhancedGeneratorConfig, LayerConfig } from "./interfaces";
import { GUI_CONSTANTS } from "./constants";

export interface ValidationResult {
  isValid: boolean;
  error?: string;
}

/**
 * Validates the entire enhanced configuration
 */
export function validateEnhancedConfig(config: EnhancedGeneratorConfig): ValidationResult {
  // Validate node types
  if (config.numNodeTypes < 1 || config.numNodeTypes > 10) {
    return {
      isValid: false,
      error: "Node types must be between 1 and 10"
    };
  }

  // Validate link types
  if (config.numLinkTypes < 1 || config.numLinkTypes > 10) {
    return {
      isValid: false,
      error: "Link types must be between 1 and 10"
    };
  }

  // Validate layers
  if (config.layers.size() > GUI_CONSTANTS.VALIDATION.MAX_LAYERS) {
    return {
      isValid: false,
      error: `Cannot exceed ${GUI_CONSTANTS.VALIDATION.MAX_LAYERS} layers`
    };
  }

  // Validate each layer
  for (let i = 0; i < config.layers.size(); i++) {
    const layer = config.layers[i];
    const layerResult = validateLayerConfig(layer, i + 1);
    if (!layerResult.isValid) {
      return layerResult;
    }
  }

  // Validate spacing
  if (config.spacing) {
    const spacingResult = validateSpacingConfig(config.spacing);
    if (!spacingResult.isValid) {
      return spacingResult;
    }
  }

  return { isValid: true };
}

/**
 * Validates a single layer configuration
 */
export function validateLayerConfig(layer: LayerConfig, layerNumber: number): ValidationResult {
  // Validate number of nodes
  if (layer.numNodes < 1 || layer.numNodes > GUI_CONSTANTS.VALIDATION.MAX_NODES_PER_LAYER) {
    return {
      isValid: false,
      error: `Layer ${layerNumber}: Number of nodes must be between 1 and ${GUI_CONSTANTS.VALIDATION.MAX_NODES_PER_LAYER}`
    };
  }

  // Validate connections per node
  if (layer.connectionsPerNode < 0 || layer.connectionsPerNode > GUI_CONSTANTS.VALIDATION.MAX_CONNECTIONS_PER_NODE) {
    return {
      isValid: false,
      error: `Layer ${layerNumber}: Connections per node must be between 0 and ${GUI_CONSTANTS.VALIDATION.MAX_CONNECTIONS_PER_NODE}`
    };
  }

  // Node type and link type are optional - they will be auto-generated if not specified
  // No validation needed for these fields

  return { isValid: true };
}

/**
 * Validates spacing configuration
 */
export function validateSpacingConfig(spacing: import("../../../shared/interfaces/enhancedGenerator.interface").SpacingConfig): ValidationResult {
  const fields = [
    { name: "nodeHeight", value: spacing.nodeHeight, min: 0.1, max: 100 },
    { name: "nodeRadius", value: spacing.nodeRadius, min: 0.1, max: 50 },
    { name: "layerSpacing", value: spacing.layerSpacing, min: 0.1, max: 200 },
    { name: "nodeSpacing", value: spacing.nodeSpacing, min: 0.1, max: 100 },
    { name: "swimlaneSpacing", value: spacing.swimlaneSpacing, min: 0.1, max: 200 },
    { name: "linkDiameter", value: spacing.linkDiameter, min: 0.1, max: 10 }
  ];

  for (const field of fields) {
    if (field.value < field.min || field.value > field.max) {
      return {
        isValid: false,
        error: `${field.name} must be between ${field.min} and ${field.max}`
      };
    }
  }

  return { isValid: true };
}

/**
 * Wall Manager
 * Handles creation of vertical walls for Y-axis visualization
 */

import { Cluster, Node } from "../../../../interfaces/simpleDataGenerator.interface";
import { EnhancedGeneratorConfig } from "../../../../interfaces/enhancedGenerator.interface";
import { createVerticalWalls } from "../../verticalWallCreator";
import { PropertyValueResolver } from "../../propertyValueResolver";
import {
  YPropertyBounds,
  WallManagerResult
} from "../types";

export class WallManager {
  private propertyResolver: PropertyValueResolver;

  constructor() {
    this.propertyResolver = new PropertyValueResolver();
  }

  /**
   * Creates vertical walls if Y-axis property is configured
   */
  public createWalls(
    cluster: Cluster,
    config: EnhancedGeneratorConfig,
    targetOrigin: Vector3,
    shadowWidth: number,
    shadowDepth: number,
    platform: Part
  ): WallManagerResult | undefined {
    print(`[WallManager] createWalls called with yAxis: ${config.axisMapping?.yAxis || "undefined"}`);

    if (!config.axisMapping?.yAxis || config.axisMapping.yAxis === "none") {
      print("[WallManager] No Y-axis configured, skipping wall creation");
      return undefined;
    }

    print("[WallManager] Creating vertical walls...");

    const wallHeight = this.calculateWallHeight(cluster);

    const walls = createVerticalWalls({
      platformBounds: {
        minX: targetOrigin.X - shadowWidth / 2,
        maxX: targetOrigin.X + shadowWidth / 2,
        minZ: targetOrigin.Z - shadowDepth / 2,
        maxZ: targetOrigin.Z + shadowDepth / 2,
      },
      height: wallHeight,
      parent: platform
    });

    // Add swimlane shadows on walls
    this.createYAxisWallSwimlanes(cluster, walls, config);

    return { walls, wallHeight };
  }

  /**
   * Calculate wall height based on cluster bounds
   */
  private calculateWallHeight(cluster: Cluster): number {
    let maxY = 0;
    cluster.groups[0].nodes.forEach((node: Node) => {
      maxY = math.max(maxY, node.position.y);
    });
    return maxY + 10; // Add some padding
  }

  /**
   * Create Y-axis swimlane shadows on walls
   */
  private createYAxisWallSwimlanes(
    cluster: Cluster,
    _walls: Part[],
    config: EnhancedGeneratorConfig
  ): void {
    if (!config.axisMapping?.yAxis || config.axisMapping.yAxis === "none") return;

    const yAxisProperty = config.axisMapping.yAxis;
    const propertyGroups = new Map<string, YPropertyBounds>();
    const propertyColors = new Map<string, Color3>();

    // Group nodes by Y-axis property and find bounds
    cluster.groups[0].nodes.forEach((node: Node) => {
      const propertyValue = this.propertyResolver.getPropertyValue(
        node,
        yAxisProperty
      );

      if (!propertyGroups.has(propertyValue)) {
        propertyGroups.set(propertyValue, {
          minY: math.huge,
          maxY: -math.huge,
        });
        // Use node color for the property
        propertyColors.set(
          propertyValue,
          new Color3(node.color[0], node.color[1], node.color[2])
        );
      }

      const bounds = propertyGroups.get(propertyValue)!;
      bounds.minY = math.min(bounds.minY, node.position.y);
      bounds.maxY = math.max(bounds.maxY, node.position.y);
    });

    // Create swimlane shadows on walls - DISABLED: remove child walls inside walls
    // createWallSwimlanes(walls, propertyGroups, propertyColors);
  }
}

