 */
export function createLabel(parent: Frame, text: string, position: UDim2, width: number): TextLabel {
  const label = new Instance("TextLabel");
  label.Name = `${text.gsub(" ", "")[0]}Label`;
  label.Text = text;
  label.Position = position;
  label.Size = new UDim2(0, width, 0, UI_CONSTANTS.BUTTON.HEIGHT);
  label.BackgroundTransparency = 1;
  label.TextColor3 = UI_CONSTANTS.TEXT.LABEL_COLOR;
  label.Font = Enum.Font.SourceSans;
  label.TextSize = UI_CONSTANTS.TEXT.LABEL_SIZE;
  label.TextXAlignment = Enum.TextXAlignment.Left;
  label.Parent = parent;

  return label;
}

/**
 * Creates a styled button
 */
export function createButton(parent: Frame, name: string, text: string, position: UDim2, width: number): TextButton {
  const button = new Instance("TextButton");
  button.Name = name;
  button.Text = text;
  button.Position = position;
  button.Size = new UDim2(0, width, 0, UI_CONSTANTS.BUTTON.HEIGHT);
  button.BackgroundColor3 = UI_CONSTANTS.BUTTON.BACKGROUND_COLOR;
  button.BorderSizePixel = 0;
  button.TextColor3 = UI_CONSTANTS.TEXT.BUTTON_COLOR;
  button.Font = Enum.Font.SourceSans;
  button.TextSize = UI_CONSTANTS.TEXT.LABEL_SIZE;
  button.Parent = parent;

  applyCornerRadius(button, UI_CONSTANTS.BUTTON.CORNER_RADIUS);

  return button;
}

/**
 * Applies corner radius to a GUI object
 */
export function applyCornerRadius(object: GuiObject, radius: number | UDim): void {
  const corner = new Instance("UICorner");
  if (typeIs(radius, "UDim")) {
    corner.CornerRadius = radius;
  } else {
    corner.CornerRadius = new UDim(0, radius);
  }
  corner.Parent = object;
}

/**
 * Creates the main container frame
 */
export function createMainFrame(gui: ScreenGui): Frame {
  const mainFrame = new Instance("Frame");
  mainFrame.Name = "AxisControlsFrame";
  mainFrame.Size = new UDim2(0, UI_CONSTANTS.FRAME.WIDTH, 0, UI_CONSTANTS.FRAME.HEIGHT);
  mainFrame.Position = new UDim2(1, -UI_CONSTANTS.FRAME.WIDTH - 10, 0.5, -UI_CONSTANTS.FRAME.HEIGHT / 2);
  mainFrame.BackgroundColor3 = UI_CONSTANTS.FRAME.BACKGROUND_COLOR;
  mainFrame.BorderSizePixel = 0;
  mainFrame.Parent = gui;

  applyCornerRadius(mainFrame, UI_CONSTANTS.FRAME.CORNER_RADIUS);

  return mainFrame;
}

/**
 * Sets button enabled state
 */
export function setButtonEnabled(button: TextButton, enabled: boolean): void {
  button.Active = enabled;
  button.BackgroundColor3 = enabled 
    ? UI_CONSTANTS.BUTTON.BACKGROUND_COLOR 
    : UI_CONSTANTS.BUTTON.DISABLED_BACKGROUND_COLOR;
  button.TextColor3 = enabled 
    ? UI_CONSTANTS.TEXT.BUTTON_COLOR 
    : UI_CONSTANTS.TEXT.DISABLED_COLOR;
}

/**
 * Standardized interfaces for bar maker
 * Following the IMaker pattern
 */

import { IVisualMakerConfig } from "../../interfaces/IMaker";

/**
 * Standardized configuration for bar creation
 */
export interface IBarMakerConfig extends IVisualMakerConfig {
  /** Rotation of the bar */
  rotation?: Vector3;

  /** Label text for the bar */
  label?: string;

  /** Stack index for hierarchical organization */
  stackIndex?: number;

  /** Hexagon index within the stack */
  hexIndex?: number;

  /** Bar index within the hexagon */
  barIndex?: number;

  /** Background color for the label */
  backgroundColor?: Color3;

  /** Border color for the label */
  borderColor?: Color3;

  /** Text color for the label */
  textColor?: Color3;

  /** Whether the bar is anchored */
  anchored?: boolean;

  /** Surface types */
  topSurface?: Enum.SurfaceType;
  bottomSurface?: Enum.SurfaceType;
}

/**
 * Legacy bar configuration (for backward compatibility during migration)
 */
export interface BarConfigLegacy {
  id: string | number;
  position?: { x: number; y: number; z: number };
  rotation?: { x: number; y: number; z: number };
  props?: {
    Size?: [number, number, number];
    Color?: [number, number, number];
    Material?: string;
    Anchored?: boolean;
    TopSurface?: string;
    BottomSurface?: string;
    Transparency?: number;
    BackgroundColor?: Color3;
    BorderColor?: Color3;
    [key: string]: any;
  };
  label?: string;
  stackIndex?: number;
  hexIndex?: number;
  barIndex?: number;
}

/**
 * Converts legacy config to standardized config
 */
export function convertLegacyBarConfig(legacy: BarConfigLegacy): IBarMakerConfig {
  const config: IBarMakerConfig = {
    id: legacy.id,
    label: legacy.label,
    stackIndex: legacy.stackIndex,
    hexIndex: legacy.hexIndex,
    barIndex: legacy.barIndex,
  };

  // Convert position
  if (legacy.position) {
    config.position = new Vector3(
      legacy.position.x,
      legacy.position.y,
      legacy.position.z
    );
  }

  // Convert rotation
  if (legacy.rotation) {
    config.rotation = new Vector3(
      legacy.rotation.x,
      legacy.rotation.y,
      legacy.rotation.z
    );
  }

  // Convert props
  if (legacy.props) {
    if (legacy.props.Size) {
      config.size = new Vector3(
        legacy.props.Size[0],
        legacy.props.Size[1],
        legacy.props.Size[2]
      );
    }
    if (legacy.props.Color) {
      config.color = new Color3(
        legacy.props.Color[0],
        legacy.props.Color[1],
        legacy.props.Color[2]
      );
    }
    if (legacy.props.Material) {
      config.material = legacy.props.Material as unknown as Enum.Material;
    }
    if (legacy.props.Transparency !== undefined) {
      config.transparency = legacy.props.Transparency;
    }
    if (legacy.props.Anchored !== undefined) {
      config.anchored = legacy.props.Anchored;
    }
    if (legacy.props.TopSurface) {
      config.topSurface = legacy.props.TopSurface as unknown as Enum.SurfaceType;
    }
    if (legacy.props.BottomSurface) {
      config.bottomSurface = legacy.props.BottomSurface as unknown as Enum.SurfaceType;
    }
    config.backgroundColor = legacy.props.BackgroundColor;
    config.borderColor = legacy.props.BorderColor;
  }

  return config;
}

import { DEFAULTS, getAvailableProperties } from "./constants";
import type { AxisMappingControlsProps, CompactAxisControlsProps } from "./types";
import { getOrCreateScreenGui } from "./utils/screenGuiManager";
import { createMainFrame } from "./utils/layoutManager";
import { createAxisMappingSection } from "./components/axisMappingSection";
import { createVisualCustomizationSection } from "./components/visualCustomizationSection";

/**
 * Creates axis mapping controls UI
 * Main entry point that maintains backward compatibility
 */
export function createAxisMappingControls({
  parent,
  axisMapping,
  visualMapping,
  useLayerForYAxis = DEFAULTS.USE_LAYER_FOR_Y_AXIS,
  yAxisProperty,
  onAxisMappingChange,
  onVisualMappingChange,
  onYAxisModeChange,
  onYAxisPropertyChange
}: AxisMappingControlsProps): void {
  // Provide default axis mapping if not provided
  const mapping = axisMapping || {
    xAxis: DEFAULTS.X_AXIS,
    zAxis: DEFAULTS.Z_AXIS
  };

  // Provide default visual mapping if not provided
  const visMapping = visualMapping || {
    backgroundColor: DEFAULTS.BACKGROUND_COLOR,
    borderColor: DEFAULTS.BORDER_COLOR
  };

  // Get or create the shared ScreenGui
  const gui = getOrCreateScreenGui();

  // Get the 3rd available property for Y axis default
  const availableProps = getAvailableProperties();
  const yAxisDefaultValue = availableProps.size() >= 3 ? availableProps[2] : "none";

  // Create a single compact frame for all controls
  createCompactAxisControls({
    gui: gui,
    xAxisValue: mapping.xAxis,
    zAxisValue: mapping.zAxis,
    yAxisValue: mapping.yAxis || yAxisDefaultValue,
    backgroundColorValue: visMapping.backgroundColor || DEFAULTS.BACKGROUND_COLOR,
    borderColorValue: visMapping.borderColor || DEFAULTS.BORDER_COLOR,
    onXAxisChange: (value) => onAxisMappingChange("xAxis", value),
    onZAxisChange: (value) => onAxisMappingChange("zAxis", value),
    onYAxisChange: (value) => onAxisMappingChange("yAxis", value),
    onBackgroundColorChange: (value) => onVisualMappingChange("backgroundColor", value),
    onBorderColorChange: (value) => onVisualMappingChange("borderColor", value)
  });
}

/**
 * Creates the compact axis controls frame with all sections
 * Internal function that orchestrates the UI creation
 */
function createCompactAxisControls({
  gui,
  xAxisValue,
  zAxisValue,
  yAxisValue,
  backgroundColorValue,
  borderColorValue,
  onXAxisChange,
  onZAxisChange,
  onYAxisChange,
  onBackgroundColorChange,
  onBorderColorChange
}: CompactAxisControlsProps): void {
  // Create main container frame
  const mainFrame = createMainFrame(gui);

  // Create axis mapping section
  createAxisMappingSection({
    parent: mainFrame,
    xAxisValue: xAxisValue,
    zAxisValue: zAxisValue,
    yAxisValue: yAxisValue,
    onXAxisChange: onXAxisChange,
    onZAxisChange: onZAxisChange,
    onYAxisChange: onYAxisChange
  });

  // Create visual customization section
  createVisualCustomizationSection({
    parent: mainFrame,
    backgroundColorValue: backgroundColorValue,
    borderColorValue: borderColorValue,
    onBackgroundColorChange: onBackgroundColorChange,
    onBorderColorChange: onBorderColorChange
  });
}

/**
 * Standardized bar maker following IMaker pattern
 */

import { IBarMakerConfig } from "./standardizedInterfaces";
import { generateAttachmentName, generateBarName, makeAttachment } from "./utilities";
import { createTextLabel } from "../TextLabelMaker";

/**
 * Default values for bar properties
 */
const BAR_DEFAULTS = {
  size: new Vector3(0.5, 0.5, 4),
  color: new Color3(0.5, 0.5, 0.5),
  material: Enum.Material.SmoothPlastic,
  transparency: 0,
  anchored: true,
  topSurface: Enum.SurfaceType.Smooth,
  bottomSurface: Enum.SurfaceType.Smooth,
  rotation: new Vector3(0, -30, 0),
};

/**
 * Creates a bar part with standardized configuration
 * @param config - Standardized bar configuration
 * @returns The created bar part
 */
export function makeBar(config: IBarMakerConfig): Part {
  // Extract configuration with defaults
  const {
    position = new Vector3(0, 0, 0),
    rotation = BAR_DEFAULTS.rotation,
    size = BAR_DEFAULTS.size,
    color = BAR_DEFAULTS.color,
    material = BAR_DEFAULTS.material,
    transparency = BAR_DEFAULTS.transparency,
    anchored = BAR_DEFAULTS.anchored,
    topSurface = BAR_DEFAULTS.topSurface,
    bottomSurface = BAR_DEFAULTS.bottomSurface,
    label = "Bar",
    stackIndex = 1,
    hexIndex = 1,
    barIndex = 1,
    backgroundColor,
    borderColor,
    textColor,
    parent,
    castShadow = false,
  } = config;

  const barLength = typeIs(size, "Vector3") ? size.Z : size[2];
  const frontFaceOffset = barLength / 2;
  const backFaceOffset = -barLength / 2;

  const barName = generateBarName(stackIndex, hexIndex, barIndex);
  const bar = new Instance("Part");
  bar.Name = barName;
  bar.Size = typeIs(size, "Vector3") ? size : new Vector3(size[0], size[1], size[2]);
  bar.Position = position;
  bar.Orientation = rotation;
  bar.Anchored = anchored;
  bar.Color = typeIs(color, "Color3") ? color : new Color3(color[0], color[1], color[2]);
  bar.Material = material;
  bar.TopSurface = topSurface;
  bar.BottomSurface = bottomSurface;
  bar.Transparency = transparency;
  bar.CastShadow = castShadow;

  // Create attachments
  const frontAttachment = makeAttachment(
    "FrontAttachment",
    frontFaceOffset,
    generateAttachmentName((barIndex - 1) * 2 + 1, hexIndex, stackIndex)
  );
  const backAttachment = makeAttachment(
    "BackAttachment",
    backFaceOffset,
    generateAttachmentName((barIndex - 1) * 2 + 2, hexIndex, stackIndex)
  );

  frontAttachment.Parent = bar;
  backAttachment.Parent = bar;

  // Create labels
  createTextLabel({
    part: bar,
    face: Enum.NormalId.Front,
    text: label,
    backgroundColor: backgroundColor || (typeIs(color, "Color3") ? color : new Color3(color[0], color[1], color[2])),
    borderColor: borderColor,
    textColor: textColor || borderColor,
  });

  createTextLabel({
    part: bar,
    face: Enum.NormalId.Back,
    text: label,
    backgroundColor: backgroundColor || (typeIs(color, "Color3") ? color : new Color3(color[0], color[1], color[2])),
    borderColor: borderColor,
    textColor: textColor || borderColor,
  });

  // Set parent if provided
  if (parent) {
    bar.Parent = parent;
  }

  return bar;
}

/**
 * Property Discovery Utility
 * Dynamically discovers properties from data objects for axis mapping
 * Part of T9: Make Axis Filters Dynamic
 */

import { Node } from "../interfaces/simpleDataGenerator.interface";

/**
 * System properties that should be excluded from axis mapping
 */
const SYSTEM_PROPERTIES = new Set([
  "uuid",
  "name", 
  "type",
  "color",
  "position",
  "attachmentNames",
  "properties", // This is the container, we want what's inside
  "level" // Internal property used by layout algorithm
]);

/**
 * Discovers all unique properties from an array of nodes
 * @param nodes - Array of nodes to analyze
 * @returns Array of discovered property names suitable for axis mapping
 */
export function discoverNodeProperties(nodes: Node[]): string[] {
  const propertySet = new Set<string>();

  nodes.forEach(node => {
    // Check if node has properties object
    if (node.properties && typeIs(node.properties, "table")) {
      // Extract all property keys
      for (const [key, _] of pairs(node.properties)) {
        if (typeIs(key, "string") && !SYSTEM_PROPERTIES.has(key)) {
          propertySet.add(key);
        }
      }
    }
  });

  // Convert set to sorted array for consistent ordering
  const properties: string[] = [];
  propertySet.forEach(prop => properties.push(prop));
  properties.sort();

  return properties;
}


/**
 * Gets property value from a node, handling nested properties
 * @param node - Node to get property from
 * @param propertyName - Name of property to retrieve
 * @returns Property value or undefined
 */
export function getNodePropertyValue(node: Node, propertyName: string): unknown {
  // Check nested properties first
  if (node.properties) {
    const props = node.properties as Record<string, unknown>;
    const value = props[propertyName];
    if (value !== undefined) {
      return value;
    }
  }

  // Check top-level properties (but skip system properties)
  if (!SYSTEM_PROPERTIES.has(propertyName)) {
    const topLevel = node as unknown as Record<string, unknown>;
    const value = topLevel[propertyName];
    if (value !== undefined) {
      return value;
    }
  }

  return undefined;
}

/**
 * Validates that discovered properties have enough variety for axis mapping
 * @param nodes - Array of nodes
 * @param propertyName - Property to validate
 * @returns True if property has at least 2 different values
 */
function isValidAxisProperty(nodes: Node[], propertyName: string): boolean {
  const uniqueValues = new Set<string>();

  nodes.forEach(node => {
    const value = getNodePropertyValue(node, propertyName);
    if (value !== undefined) {
      uniqueValues.add(tostring(value));
    }
  });

  return uniqueValues.size() >= 2;
}

/**
 * Filters discovered properties to only include those suitable for axis mapping
 * @param nodes - Array of nodes
 * @param properties - Array of property names to filter
 * @returns Filtered array of properties with enough variety
 */
export function filterValidAxisProperties(nodes: Node[], properties: string[]): string[] {
  return properties.filter(prop => isValidAxisProperty(nodes, prop));
}

/**
 * Standardized hexagon maker following IMaker pattern
 */

import { makeBar } from "../barMaker";
import { HEXAGON_CONSTANTS } from "./constants";
import {
  generateHexagonName,
  createCenterCube,
  createCenterAttachment,
  calculateBarDimensions
} from "./utilities";
import { createTextLabel } from "../TextLabelMaker";
import { IHexagonMakerConfig } from "./standardizedInterfaces";

/**
 * Creates a hexagon model with standardized configuration
 * @param config - Standardized hexagon configuration
 * @returns The created hexagon model
 */
export function makeHexagon(config: IHexagonMakerConfig): Model {
  // Extract configuration with defaults
  const {
    id = 1,
    position = new Vector3(0, 0, 0),
    width = HEXAGON_CONSTANTS.DEFAULT_WIDTH,
    height = HEXAGON_CONSTANTS.DEFAULT_HEIGHT,
    labels = HEXAGON_CONSTANTS.DEFAULT_LABELS,
    stackIndex = 1,
    hexIndex = 1,
    guid,
    barColor = new Color3(
      HEXAGON_CONSTANTS.DEFAULT_BAR_COLOR[0],
      HEXAGON_CONSTANTS.DEFAULT_BAR_COLOR[1],
      HEXAGON_CONSTANTS.DEFAULT_BAR_COLOR[2]
    ),
    barMaterial = Enum.Material.SmoothPlastic,
    barTransparency = 0,
    parent,
  } = config;

  // Calculate bar dimensions
  const dimensions = calculateBarDimensions(width, height);

  // Create the hexagon model
  const hexModel = new Instance("Model");
  const hexagonName = generateHexagonName(stackIndex, hexIndex);
  hexModel.Name = hexagonName;

  // Set GUID as attribute if provided
  if (guid) {
    hexModel.SetAttribute("guid", guid);
  }

  // Create center cube and attachment
  const centerCube = createCenterCube(
    [position.X, position.Y, position.Z], // Convert back to array for compatibility
    hexIndex,
    stackIndex
  );
  centerCube.Parent = hexModel;

  // Set the center cube as the primary part for positioning
  hexModel.PrimaryPart = centerCube;

  createCenterAttachment(centerCube, hexIndex, stackIndex);

  // Create 3 bars rotated 60 degrees apart
  for (let i = 0; i < HEXAGON_CONSTANTS.NUM_BARS; i++) {
    const rotation = i * HEXAGON_CONSTANTS.ROTATION_ANGLE; // 0, 60, 120

    // Slightly raise the first bar to avoid z-fighting with the top label
    const yOffset = i === 0 ? 0.001 : 0;

    const bar = makeBar({
      id: `${id}_bar${i + 1}`,
      position: new Vector3(position.X, position.Y + yOffset, position.Z),
      rotation: new Vector3(0, rotation, 0),
      size: new Vector3(dimensions.barLength, dimensions.barHeight, dimensions.barWidth),
      color: barColor,
      material: barMaterial,
      transparency: barTransparency,
      label: labels[i] || `Bar${i + 1}`,
      stackIndex: stackIndex,
      hexIndex: hexIndex,
      barIndex: i + 1,
    });

    bar.Parent = hexModel;

    // Add top label on the first bar only
    if (i === 0 && labels.size() > 0) {
      createTextLabel({
        part: bar,
        face: Enum.NormalId.Top,
        text: labels[0], // Use the first label (node name)
      });
    }
  }

  // Set parent if provided
  if (parent) {
    hexModel.Parent = parent;
  }

  return hexModel;
}

/**
 * Y-Parallel Shadow Manager
 * Handles creation of Y-axis parallel shadow planes
 */

import { Cluster } from "../../../../interfaces/simpleDataGenerator.interface";
import { EnhancedGeneratorConfig } from "../../../../interfaces/enhancedGenerator.interface";
import { YParallelShadowCreator } from "../../blocks/yParallelShadowCreator";
import { LAYOUT_CONSTANTS } from "../../constants/layoutConstants";
import { LaneBounds } from "../types";

export class YParallelShadowManager {
  private yParallelShadowCreator: YParallelShadowCreator;

  constructor() {
    this.yParallelShadowCreator = new YParallelShadowCreator();
  }

  /**
   * Creates Y-parallel shadow blocks based on Y-axis property
   */
  public createYParallelShadows(
    cluster: Cluster,
    parentFolder: Folder,
    config: EnhancedGeneratorConfig,
    allLaneBounds: LaneBounds
  ): Map<string, Part> | undefined {
    if (!config.axisMapping?.yAxis || config.axisMapping.yAxis === "none") {
      return undefined;
    }

    // Get the cluster folder
    const graphMakerFolder = parentFolder.FindFirstChild("GraphMaker") as Folder;
    if (!graphMakerFolder) {
      warn("[YParallelShadowManager] GraphMaker folder not found for Y-parallel shadows");
      return undefined;
    }

    const clusterFolder = graphMakerFolder.FindFirstChild("UnifiedDataCluster") as Folder;
    if (!clusterFolder) {
      warn("[YParallelShadowManager] UnifiedDataCluster folder not found for Y-parallel shadows");
      return undefined;
    }

    // Create models to wrap Y shadows
    const yShadowsModelRight = new Instance("Model");
    yShadowsModelRight.Name = "YParallelShadowsRight";
    yShadowsModelRight.Parent = clusterFolder;

    const yShadowsModelBack = new Instance("Model");
    yShadowsModelBack.Name = "YParallelShadowsBack";
    yShadowsModelBack.Parent = clusterFolder;

    // Calculate shadow dimensions (same as group shadow)
    const shadowWidth = allLaneBounds.width + LAYOUT_CONSTANTS.SHADOW_PADDING.X_PADDING * 2;
    const shadowDepth = allLaneBounds.depth + LAYOUT_CONSTANTS.SHADOW_PADDING.Z_PADDING * 2;

    // Create Y-parallel shadows for right side (original)
    const yParallelShadowsRight = this.yParallelShadowCreator.createYParallelShadows({
      nodes: cluster.groups[0].nodes,
      yAxisProperty: config.axisMapping.yAxis,
      parent: yShadowsModelRight,
      shadowWidth: shadowWidth,
      shadowDepth: shadowDepth,
      side: "right"
    });

    // Create Y-parallel shadows for back side (duplicate)
    const yParallelShadowsBack = this.yParallelShadowCreator.createYParallelShadows({
      nodes: cluster.groups[0].nodes,
      yAxisProperty: config.axisMapping.yAxis,
      parent: yShadowsModelBack,
      shadowWidth: shadowWidth,
      shadowDepth: shadowDepth,
      side: "back"
    });

    print(`[YParallelShadowManager] Created ${yParallelShadowsRight.size()} right and ${yParallelShadowsBack.size()} back Y-parallel shadow blocks`);

    // Return the right shadows for compatibility
    return yParallelShadowsRight;
  }
}

interface TextLabelConfig {
  part: Part;
  face: Enum.NormalId;
  text: string;
  parent?: Instance;
  backgroundColor?: Color3;
  borderColor?: Color3;
  textColor?: Color3;
}

export function createTextLabel({
  part,
  face,
  text,
  parent,
  backgroundColor,
  borderColor,
  textColor
}: TextLabelConfig): TextLabel {
  // Create the SurfaceGui
  const surfaceGui = new Instance("SurfaceGui");
  surfaceGui.Name = `SurfaceGui_${face.Name}`;
  surfaceGui.Face = face;
  surfaceGui.SizingMode = Enum.SurfaceGuiSizingMode.PixelsPerStud;
  surfaceGui.PixelsPerStud = 50;
  surfaceGui.Parent = part;

  // Use provided background color or default to part color
  const bgColor = backgroundColor || part.Color;

  // Create the TextLabel (not TextBox) with barMaker styling
  const textLabel = new Instance("TextLabel");
  textLabel.Name = "TextLabel";
  textLabel.Text = text;
  textLabel.TextScaled = true; // Scale text to fit within bounds
  textLabel.Font = Enum.Font.SourceSans;
  textLabel.Size = new UDim2(1, 0, 1, 0);
  textLabel.BackgroundColor3 = bgColor;
  textLabel.TextColor3 = textColor || new Color3(0, 0, 0);
  textLabel.BorderSizePixel = 0; // No border on hex labels
  textLabel.BorderColor3 = borderColor || new Color3(0, 0, 0);
  textLabel.BorderMode = Enum.BorderMode.Inset; // Keep border inside the TextLabel bounds
  textLabel.TextWrapped = true; // Allow wrapping in addition to scaling
  textLabel.Parent = surfaceGui;

  return textLabel;
}

export function createTextLabelWithCustomStyling({
  part,
  face,
  text,
  textSize = 24,
  backgroundColor,
  textColor = new Color3(0, 0, 0),
  font = Enum.Font.SourceSans,
  borderSizePixel = 2, // Reduced default from 10 to 2
  borderColor,
  textWrapped = true,
}: TextLabelConfig & {
  textSize?: number;
  backgroundColor?: Color3;
  textColor?: Color3;
  font?: Enum.Font;
  borderSizePixel?: number;
  borderColor?: Color3;
  textWrapped?: boolean;
}): TextLabel {
  // Create the SurfaceGui
  const surfaceGui = new Instance("SurfaceGui");
  surfaceGui.Name = `SurfaceGui_${face.Name}`;
  surfaceGui.Face = face;
  surfaceGui.SizingMode = Enum.SurfaceGuiSizingMode.PixelsPerStud;
  surfaceGui.PixelsPerStud = 50;
  surfaceGui.Parent = part;

  // Use part color as default background if not specified
  const defaultBackgroundColor =
    backgroundColor ||
    Color3.fromRGB(part.Color.R * 255, part.Color.G * 255, part.Color.B * 255);

  // Create the TextLabel with custom styling
  const textLabel = new Instance("TextLabel");
  textLabel.Name = "TextLabel";
  textLabel.Text = text;
  textLabel.TextSize = textSize;
  textLabel.Font = font;
  textLabel.Size = new UDim2(1, 0, 1, 0);
  textLabel.BackgroundColor3 = defaultBackgroundColor;
  textLabel.TextColor3 = textColor;
  textLabel.BorderSizePixel = borderSizePixel;
  if (borderColor) {
    textLabel.BorderColor3 = borderColor;
  }
  textLabel.BorderMode = Enum.BorderMode.Inset; // Keep border inside bounds
  textLabel.TextWrapped = textWrapped;
  textLabel.Parent = surfaceGui;

  return textLabel;
}

interface TextBoxConfig {
  part: Part;
  face: Enum.NormalId;
  text: string;
  parent?: Instance;
  backgroundColor?: Color3;
  borderColor?: Color3;
  textColor?: Color3;
}

export function createTextBox({
  part,
  face,
  text,
  parent,
  backgroundColor,
  borderColor,
  textColor
}: TextBoxConfig): TextBox {
  // Create the SurfaceGui
  const surfaceGui = new Instance("SurfaceGui");
  surfaceGui.Name = `SurfaceGui_${face.Name}`;
  surfaceGui.Face = face;
  surfaceGui.SizingMode = Enum.SurfaceGuiSizingMode.PixelsPerStud;
  surfaceGui.PixelsPerStud = 50;
  surfaceGui.Parent = part;

  // Use provided background color or default to part color
  const bgColor = backgroundColor || part.Color;

  // Create the TextBox with barMaker styling
  const textBox = new Instance("TextBox");
  textBox.Name = "TextBox";
  textBox.Text = text;
  textBox.TextScaled = true; // Scale text to fit within bounds
  textBox.Font = Enum.Font.SourceSans;
  textBox.Size = new UDim2(1, 0, 1, 0);
  textBox.BackgroundColor3 = bgColor;
  textBox.TextColor3 = textColor || new Color3(0, 0, 0);
  textBox.BorderSizePixel = 2; // Reduced from 10 to prevent overflow
  textBox.BorderColor3 = borderColor || new Color3(0, 0, 0);
  textBox.BorderMode = Enum.BorderMode.Inset; // Keep border inside the TextBox bounds
  textBox.TextWrapped = true; // Allow wrapping in addition to scaling
  textBox.Parent = surfaceGui;

  return textBox;
}

export function createTextBoxWithCustomStyling({
  part,
  face,
  text,
  textSize = 24,
  backgroundColor,
  textColor = new Color3(0, 0, 0),
  font = Enum.Font.SourceSans,
  borderSizePixel = 2, // Reduced default from 10 to 2
  borderColor,
  textWrapped = true,
}: TextBoxConfig & {
  textSize?: number;
  backgroundColor?: Color3;
  textColor?: Color3;
  font?: Enum.Font;
  borderSizePixel?: number;
  borderColor?: Color3;
  textWrapped?: boolean;
}): TextBox {
  // Create the SurfaceGui
  const surfaceGui = new Instance("SurfaceGui");
  surfaceGui.Name = `SurfaceGui_${face.Name}`;
  surfaceGui.Face = face;
  surfaceGui.SizingMode = Enum.SurfaceGuiSizingMode.PixelsPerStud;
  surfaceGui.PixelsPerStud = 50;
  surfaceGui.Parent = part;

  // Use part color as default background if not specified
  const defaultBackgroundColor =
    backgroundColor ||
    Color3.fromRGB(part.Color.R * 255, part.Color.G * 255, part.Color.B * 255);

  // Create the TextBox with custom styling
  const textBox = new Instance("TextBox");
  textBox.Name = "TextBox";
  textBox.Text = text;
  textBox.TextSize = textSize;
  textBox.Font = font;
  textBox.Size = new UDim2(1, 0, 1, 0);
  textBox.BackgroundColor3 = defaultBackgroundColor;
  textBox.TextColor3 = textColor;
  textBox.BorderSizePixel = borderSizePixel;
  if (borderColor) {
    textBox.BorderColor3 = borderColor;
  }
  textBox.BorderMode = Enum.BorderMode.Inset; // Keep border inside bounds
  textBox.TextWrapped = textWrapped;
  textBox.Parent = surfaceGui;

  return textBox;
}

