/**
 * IMaker - Interface for all maker functions/classes
 * 
 * Defines the contract that all makers should follow for consistent API
 */

/**
 * Base configuration for all makers
 */
export interface IMakerConfig {
  /** Optional unique identifier */
  id?: string | number;

  /** Parent instance to attach the created object to */
  parent?: Instance;

  /** Position in 3D space */
  position?: Vector3;

  /** Optional name for the created instance */
  name?: string;
}

/**
 * Standard result from a maker function
 */
export interface IMakerResult<T extends Instance = Instance> {
  /** The primary instance created */
  instance: T;

  /** Any child instances that should be tracked */
  children?: Instance[];

  /** Cleanup function to destroy all created instances */
  cleanup?: () => void;
}

/**
 * Interface for maker functions
 * Makers are functions that create Roblox instances
 */
export interface IMakerFunction<TConfig extends IMakerConfig = IMakerConfig, TResult extends Instance = Instance> {
  (config: TConfig): TResult;
}

/**
 * Interface for maker classes
 * More complex makers may be implemented as classes
 */
export interface IMakerClass<TConfig extends IMakerConfig = IMakerConfig, TResult extends Instance = Instance> {
  /**
   * Creates an instance based on the configuration
   */
  make(config: TConfig): TResult;

  /**
   * Optional method to validate configuration before creating
   */
  validate?(config: TConfig): boolean;

  /**
   * Optional method to get default configuration
   */
  getDefaults?(): Partial<TConfig>;
}

/**
 * Extended maker configuration with visual properties
 */
export interface IVisualMakerConfig extends IMakerConfig {
  /** Size of the object */
  size?: Vector3 | number[];

  /** Color of the object */
  color?: Color3 | number[];

  /** Material of the object */
  material?: Enum.Material;

  /** Transparency (0-1) */
  transparency?: number;

  /** Whether the object casts shadows */
  castShadow?: boolean;
}

/**
 * Configuration for label makers
 */
export interface ILabelMakerConfig extends IMakerConfig {
  /** Text content */
  text: string;

  /** Font to use */
  font?: Enum.Font;

  /** Text color */
  textColor?: Color3;

  /** Text size */
  textSize?: number;

  /** Background color */
  backgroundColor?: Color3;

  /** Background transparency */
  backgroundTransparency?: number;
}

/**
 * Configuration for connector/rope makers
 */
export interface IConnectorMakerConfig extends IMakerConfig {
  /** Start position or attachment */
  startPoint: Vector3 | Attachment;

  /** End position or attachment */
  endPoint: Vector3 | Attachment;

  /** Thickness of the connector */
  thickness?: number;

  /** Color of the connector */
  color?: Color3;

  /** Material of the connector */
  material?: Enum.Material;
}

/**
 * Block Dimension Calculator
 * Calculates dimensions for various block types
 * Part of F002 Phase 2 refactoring - T6
 */

import { BLOCK_CONSTANTS } from "../constants/blockConstants";

export interface NodeBounds {
  minX: number;
  maxX: number;
  minZ: number;
  maxZ: number;
}

export interface BlockDimensions {
  width: number;
  depth: number;
}

export class BlockDimensionCalculator {
  /**
   * Calculate block dimensions based on node bounds
   */
  public calculateBlockDimensions(
    nodeBounds: NodeBounds,
    padding: number = 0
  ): BlockDimensions {
    const nodeWidth = nodeBounds.maxX - nodeBounds.minX;
    const nodeDepth = nodeBounds.maxZ - nodeBounds.minZ;

    const width = nodeWidth + padding * 2;
    const depth = nodeDepth + padding * 2;

    // Ensure minimum size
    const finalWidth = math.max(width, BLOCK_CONSTANTS.DIMENSIONS.MIN_BLOCK_SIZE);
    const finalDepth = math.max(depth, BLOCK_CONSTANTS.DIMENSIONS.MIN_BLOCK_SIZE);

    return {
      width: finalWidth,
      depth: finalDepth,
    };
  }

  /**
   * Calculate swimlane dimensions with buffer
   */
  public calculateSwimlaneeDimensions(
    bounds: NodeBounds,
    buffer: number = BLOCK_CONSTANTS.DIMENSIONS.SHADOW_BUFFER
  ): { size: Vector3; position: Vector3 } {
    const width = bounds.maxX - bounds.minX + buffer * 2;
    const depth = bounds.maxZ - bounds.minZ + buffer * 2;
    const centerX = (bounds.minX + bounds.maxX) / 2;
    const centerZ = (bounds.minZ + bounds.maxZ) / 2;

    return {
      size: new Vector3(width, BLOCK_CONSTANTS.DIMENSIONS.SHADOW_BLOCK_HEIGHT, depth),
      position: new Vector3(centerX, 0, centerZ) // Y will be set by caller
    };
  }

  /**
   * Calculate platform dimensions with bounds
   */
  public calculatePlatformDimensions(
    clusterBounds: NodeBounds,
    extraPadding: number = 50
  ): BlockDimensions {
    return this.calculateBlockDimensions(clusterBounds, extraPadding);
  }

  /**
   * Calculate shadow block dimensions
   */
  public calculateShadowDimensions(
    clusterBounds: NodeBounds,
    shadowBuffer: number = BLOCK_CONSTANTS.DIMENSIONS.SHADOW_BUFFER
  ): BlockDimensions {
    return this.calculateBlockDimensions(clusterBounds, shadowBuffer);
  }

  /**
   * Calculate minimum bounding box for a set of bounds
   */
  public calculateCombinedBounds(boundsList: NodeBounds[]): NodeBounds {
    if (boundsList.size() === 0) {
      return { minX: 0, maxX: 0, minZ: 0, maxZ: 0 };
    }

    let minX = math.huge;
    let maxX = -math.huge;
    let minZ = math.huge;
    let maxZ = -math.huge;

    boundsList.forEach(bounds => {
      minX = math.min(minX, bounds.minX);
      maxX = math.max(maxX, bounds.maxX);
      minZ = math.min(minZ, bounds.minZ);
      maxZ = math.max(maxZ, bounds.maxZ);
    });

    return { minX, maxX, minZ, maxZ };
  }

}

import { UI_CONSTANTS } from "../constants";
import type { RadioButtonProps } from "../types";

/**
 * Creates a radio button control
 */
export function createRadioButton({
  parent,
  position,
  text,
  selected,
  onSelect
}: RadioButtonProps): Frame {
  const container = new Instance("Frame");
  container.Name = "RadioButtonContainer";
  container.Position = position;
  container.Size = new UDim2(1, 0, 0, UI_CONSTANTS.SPACING.RADIO_SIZE);
  container.BackgroundTransparency = 1;
  container.Parent = parent;

  // Radio button circle
  const radioButton = new Instance("TextButton");
  radioButton.Name = "RadioButton";
  radioButton.Position = new UDim2(0, 0, 0, 0);
  radioButton.Size = new UDim2(0, UI_CONSTANTS.SPACING.RADIO_SIZE, 0, UI_CONSTANTS.SPACING.RADIO_SIZE);
  radioButton.BackgroundColor3 = UI_CONSTANTS.BUTTON.BACKGROUND_COLOR;
  radioButton.BorderSizePixel = 0;
  radioButton.Text = "";
  radioButton.Parent = container;

  const radioCorner = new Instance("UICorner");
  radioCorner.CornerRadius = new UDim(0.5, 0);
  radioCorner.Parent = radioButton;

  // Inner circle (visible when selected)
  const innerCircle = new Instance("Frame");
  innerCircle.Name = "InnerCircle";
  innerCircle.Position = new UDim2(0.5, 0, 0.5, 0);
  innerCircle.AnchorPoint = new Vector2(0.5, 0.5);
  innerCircle.Size = new UDim2(0, UI_CONSTANTS.SPACING.RADIO_INNER_SIZE, 0, UI_CONSTANTS.SPACING.RADIO_INNER_SIZE);
  innerCircle.BackgroundColor3 = UI_CONSTANTS.TEXT.BUTTON_COLOR;
  innerCircle.BorderSizePixel = 0;
  innerCircle.Visible = selected;
  innerCircle.Parent = radioButton;

  const innerCorner = new Instance("UICorner");
  innerCorner.CornerRadius = new UDim(0.5, 0);
  innerCorner.Parent = innerCircle;

  // Label
  const label = new Instance("TextLabel");
  label.Name = "RadioLabel";
  label.Text = text;
  label.Position = new UDim2(0, 25, 0, 0);
  label.Size = new UDim2(1, -25, 0, UI_CONSTANTS.SPACING.RADIO_SIZE);
  label.BackgroundTransparency = 1;
  label.TextColor3 = UI_CONSTANTS.TEXT.LABEL_COLOR;
  label.Font = Enum.Font.SourceSans;
  label.TextSize = UI_CONSTANTS.TEXT.LABEL_SIZE;
  label.TextXAlignment = Enum.TextXAlignment.Left;
  label.Parent = container;

  // Click handler
  radioButton.MouseButton1Click.Connect(() => {
    onSelect();
  });

  return container;
}

/**
 * Updates the selected state of a radio button
 */
export function updateRadioButtonState(radioContainer: Frame, selected: boolean): void {
  // Find the inner circle by traversing the children
  const radioButton = radioContainer.FindFirstChild("RadioButton") as TextButton | undefined;
  if (radioButton) {
    const innerCircle = radioButton.FindFirstChild("InnerCircle") as Frame | undefined;
    if (innerCircle) {
      innerCircle.Visible = selected;
    }
  }
}

import { BaseService } from "../../../shared/services/base/BaseService";
import { ConfigGUIServerService } from "../configGUIServer.service";
import { GraphInitializerService } from "../graphInitializer.service";
import { initializeDev2Features } from "./dev2features";
import { makeOriginBlock } from "../../../shared/modules/makeOriginBlock";

// Origin configuration for 3D positioning
const ORIGIN = {
  x: 0,
  y: 0, // BASE_Y from RENDERER_CONSTANTS
  z: 0,
};

// Maximum number of data items to generate
const MAX_DATA_ITEMS = 200;

export class GameService extends BaseService {
  private configGUIServer?: ConfigGUIServerService;
  private graphInitializer: GraphInitializerService;
  private myStuffFolder!: Folder;
  private gameStarted = false; // Flag to prevent duplicate initialization

  constructor() {
    super("GameService");
    this.graphInitializer = new GraphInitializerService();
  }

  public startGame(): void {
    if (this.gameStarted) {
      return;
    }

    this.gameStarted = true;

    // Create or find the MyStuff folder at the start
    this.myStuffFolder = game.Workspace.FindFirstChild("MyStuff") as Folder;
    if (!this.myStuffFolder) {
      this.myStuffFolder = new Instance("Folder");
      this.myStuffFolder.Name = "MyStuff";
      this.myStuffFolder.Parent = game.Workspace;
      // Track the folder for cleanup
      this.addInstance(this.myStuffFolder);
    }

    // Skip initial data generation - let user generate via GUI
    // The GUI starts in enhanced mode with its own configuration

    // Create orientation reference block at origin
    if (true) {
      makeOriginBlock({
        origin: { x: ORIGIN.x, y: ORIGIN.y + 20, z: ORIGIN.z }, // Offset cube by 20 in Y
        parent: this.myStuffFolder,
      });
    }

    // Initialize the configuration GUI server with origin
    this.configGUIServer = new ConfigGUIServerService(
      this.myStuffFolder,
      new Vector3(ORIGIN.x, ORIGIN.y, ORIGIN.z)
    );

    // Set up graph initializer with the GUI server
    this.graphInitializer.setConfigGUIServer(this.configGUIServer);

    // Initialize the graph with configuration including max data items
    this.graphInitializer.initializeGraphWithMaxItems(MAX_DATA_ITEMS);

    // Initialize dev2 features
    if (false) {
      initializeDev2Features(this.myStuffFolder);
    }
  }

  /**
   * Custom cleanup logic
   */
  protected onDestroy(): void {
    // Destroy services in reverse order of creation
    if (this.configGUIServer) {
      this.configGUIServer.destroy();
      this.configGUIServer = undefined;
    }

    const initializer = this.graphInitializer as unknown as {
      destroy?: () => void;
    };
    if (initializer.destroy) {
      initializer.destroy();
    }
  }
}

/**
 * Standardized label block maker following IMaker pattern
 */

import { ILabelBlockMakerConfig } from "./standardizedInterfaces";
import { createLabelsForBlock } from "./utilities";
import { LABEL_BLOCK_CONSTANTS } from "./constants";
import { padNumber } from "../../utils/stringUtils";

/**
 * Default values for label block properties
 */
const LABEL_BLOCK_DEFAULTS = {
  size: 4,
  color: new Color3(0.5, 0.5, 0.5),
  material: Enum.Material.SmoothPlastic,
  transparency: 0,
  anchored: true,
  castShadow: false,
  position: new Vector3(0, 0, 0),
  rotation: new Vector3(0, 0, 0),
};

/**
 * Creates a label block part with standardized configuration
 * @param config - Standardized label block configuration
 * @returns The created label block part
 */
export function makeLabelBlock(config: ILabelBlockMakerConfig): Part {
  // Extract configuration with defaults
  const {
    id = 1,
    position = LABEL_BLOCK_DEFAULTS.position,
    rotation = LABEL_BLOCK_DEFAULTS.rotation,
    size = LABEL_BLOCK_DEFAULTS.size,
    color = LABEL_BLOCK_DEFAULTS.color,
    material = LABEL_BLOCK_DEFAULTS.material,
    transparency = LABEL_BLOCK_DEFAULTS.transparency,
    anchored = LABEL_BLOCK_DEFAULTS.anchored,
    castShadow = LABEL_BLOCK_DEFAULTS.castShadow,
    labels = {},
    textBoxOverrides = {},
    parent,
  } = config;

  // Create the cube part
  const block = new Instance("Part");
  block.Name = `${LABEL_BLOCK_CONSTANTS.NAME_PREFIX}${padNumber(
    math.floor(tonumber(id) || 1), 
    LABEL_BLOCK_CONSTANTS.PAD_LENGTH
  )}`;

  // Apply properties
  block.Size = new Vector3(size, size, size);
  block.Position = position;
  block.Orientation = rotation;
  block.Anchored = anchored;
  block.TopSurface = Enum.SurfaceType.Smooth;
  block.BottomSurface = Enum.SurfaceType.Smooth;
  block.CastShadow = castShadow;
  block.Color = color;
  block.Material = material;
  block.Transparency = transparency;

  // Apply any additional properties that might be passed
  // (removed the generic property application for type safety)

  // Set parent if provided
  if (parent) {
    block.Parent = parent;
  }

  // Add labels to each face
  // Check if labels object has any entries
  const labelKeys = [];
  for (const [key] of pairs(labels)) {
    labelKeys.push(key);
  }

  if (labels && labelKeys.size() > 0) {
    // Create face mapping
    const faceMap: [keyof typeof labels, Enum.NormalId][] = [
      ["front", Enum.NormalId.Front],
      ["back", Enum.NormalId.Back],
      ["left", Enum.NormalId.Left],
      ["right", Enum.NormalId.Right],
      ["top", Enum.NormalId.Top],
      ["bottom", Enum.NormalId.Bottom],
    ];
    createLabelsForBlock(block, labels as any, textBoxOverrides, faceMap);
  }

  return block;
}

/**
 * Array validation utilities for bounds checking and type safety
 */

interface ArrayValidationOptions {
  maxSize?: number;
  minSize?: number;
  allowEmpty?: boolean;
  itemValidator?: (item: unknown) => boolean;
}

interface ArrayValidationResult<T = unknown> {
  isValid: boolean;
  error?: string;
  sanitized?: T[];
}

// Default array size limits
export const ARRAY_LIMITS = {
  MAX_NODES: 1000,
  MAX_LAYERS: 20,
  MAX_CONNECTIONS: 5000,
  MAX_LABELS: 10,
  MAX_SAFE_SIZE: 10000,
} as const;

/**
 * Validates array bounds and optionally validates each item
 */
export function validateArray<T extends defined>(
  input: unknown,
  options: ArrayValidationOptions = {}
): ArrayValidationResult<T> {
  // Check if input is an array
  if (!typeIs(input, "table")) {
    return { isValid: false, error: "Input must be an array" };
  }

  const arr = input as T[];

  // Check empty array
  if (arr.size() === 0 && !options.allowEmpty) {
    return { isValid: false, error: "Array cannot be empty" };
  }

  // Check minimum size
  if (options.minSize !== undefined && arr.size() < options.minSize) {
    return {
      isValid: false,
      error: `Array must have at least ${options.minSize} items`,
    };
  }

  // Check maximum size
  const maxSize = options.maxSize || ARRAY_LIMITS.MAX_SAFE_SIZE;
  if (arr.size() > maxSize) {
    return {
      isValid: false,
      error: `Array exceeds maximum size of ${maxSize} items`,
    };
  }

  // Validate each item if validator provided
  if (options.itemValidator) {
    const sanitized: T[] = [];

    for (let i = 0; i < arr.size(); i++) {
      if (!options.itemValidator(arr[i])) {
        return {
          isValid: false,
          error: `Invalid item at index ${i}`,
        };
      }
      sanitized.push(arr[i]);
    }

    return {
      isValid: true,
      sanitized,
    };
  }

  return {
    isValid: true,
    sanitized: [...arr],
  };
}




/**
 * Checks for circular references in an array
 */
export function hasCircularReference(arr: unknown[]): boolean {
  const seen = new Set<unknown>();

  function checkCircular(obj: unknown): boolean {
    if (obj === undefined || !typeIs(obj, "table")) {
      return false;
    }

    if (seen.has(obj)) {
      return true;
    }

    seen.add(obj);

    if (typeIs(obj, "table")) {
      for (const [_, item] of pairs(obj as unknown[])) {
        if (checkCircular(item)) {
          return true;
        }
      }
    } else {
      for (const [_, value] of pairs(obj)) {
        if (checkCircular(value)) {
          return true;
        }
      }
    }

    seen.delete(obj);
    return false;
  }

  return checkCircular(arr);
}

/**
 * GUI Layout Manager
 * Manages automatic positioning of GUI components to prevent overlaps
 */

import { GUI_CONSTANTS } from "./constants";

export class GuiLayoutManager {
  private currentY: number = 40; // Start after title
  private readonly padding: number = 10;
  private readonly parent: Frame;

  constructor(parent: Frame) {
    this.parent = parent;
  }

  /**
   * Get next Y position and update current position
   */
  public getNextPosition(componentHeight: number): UDim2 {
    const position = new UDim2(0, 10, 0, this.currentY);
    this.currentY += componentHeight + this.padding;
    return position;
  }

  /**
   * Get current Y position without updating
   */
  public getCurrentY(): number {
    return this.currentY;
  }

  /**
   * Reset layout to initial position
   */
  public reset(): void {
    this.currentY = 40;
  }

  /**
   * Add extra spacing
   */
  public addSpacing(pixels: number): void {
    this.currentY += pixels;
  }

  /**
   * Create a scrolling frame if content exceeds available space
   */
  public createScrollingFrame(): Frame {
    const scrollFrame = new Instance("ScrollingFrame");
    scrollFrame.Name = "ContentScrollFrame";
    scrollFrame.Size = new UDim2(1, 0, 1, -120); // Leave more space for buttons at bottom
    scrollFrame.Position = new UDim2(0, 0, 0, 40); // Start after title
    scrollFrame.BackgroundTransparency = 1;
    scrollFrame.BorderSizePixel = 0;
    scrollFrame.ScrollBarThickness = 8;
    scrollFrame.ScrollBarImageColor3 = new Color3(0.3, 0.3, 0.3);
    scrollFrame.CanvasSize = new UDim2(0, 0, 0, this.currentY);
    scrollFrame.Parent = this.parent;

    // Cast to Frame for compatibility with component APIs
    return scrollFrame as unknown as Frame;
  }

  /**
   * Update canvas size based on content
   */
  public updateCanvasSize(scrollFrame: Frame): void {
    if (scrollFrame.IsA("ScrollingFrame")) {
      scrollFrame.CanvasSize = new UDim2(0, 0, 0, this.currentY + 20); // Add some bottom padding
    }
  }
}

