/**
 * Utility functions for rope label creation
 */

import { ROPE_LABEL_CONSTANTS } from "./constants";
import { ParsedRelation } from "./interfaces";

/**
 * Parses a relation name into its components
 * Pattern: "source_RELATION_target" -> { source, relation, target }
 */
export function parseRelationName(
  relationName: string,
  relationTypeName: string
): ParsedRelation {
  if (!relationName) {
    return {
      source: ROPE_LABEL_CONSTANTS.DEFAULTS.SOURCE_TEXT,
      relation: relationTypeName,
      target: ROPE_LABEL_CONSTANTS.DEFAULTS.TARGET_TEXT
    };
  }

  // Pattern: "source_RELATION_target" -> { source, relation, target }
  const splitPattern = `${ROPE_LABEL_CONSTANTS.PARSING.SEPARATOR}${relationTypeName}${ROPE_LABEL_CONSTANTS.PARSING.SEPARATOR}`;
  const splitIndex = relationName.find(splitPattern);

  if (splitIndex && splitIndex[0] !== undefined) {
    const beforeIndex = splitIndex[0];
    const afterIndex = beforeIndex + splitPattern.size();

    const source = relationName.sub(1, beforeIndex - 1);
    const target = relationName.sub(afterIndex);

    // Replace underscores with spaces using gsub
    const sourceFormatted = formatNodeName(source);
    const targetFormatted = formatNodeName(target);

    return {
      source: sourceFormatted,
      relation: relationTypeName,
      target: targetFormatted,
    };
  }

  // Fallback
  return {
    source: ROPE_LABEL_CONSTANTS.DEFAULTS.SOURCE_TEXT,
    relation: relationTypeName,
    target: ROPE_LABEL_CONSTANTS.DEFAULTS.TARGET_TEXT
  };
}

/**
 * Formats a node name by replacing underscores with spaces
 */
export function formatNodeName(name: string): string {
  return name.gsub("_", " ")[0];
}

/**
 * Calculates the label position between two attachments
 */
export function calculateLabelPosition(
  attachment1: Attachment,
  attachment2: Attachment
): Vector3 {
  const pos1 = attachment1.WorldPosition;
  const pos2 = attachment2.WorldPosition;

  // Calculate midpoint
  const midpoint = pos1.add(pos2).div(2);

  // Add Z offset for label visibility
  return new Vector3(
    midpoint.X,
    midpoint.Y,
    midpoint.Z + ROPE_LABEL_CONSTANTS.LABEL.Z_OFFSET
  );
}

/**
 * Validates if a relation name follows the expected format
 */
export function validateRelationFormat(relationName: string): boolean {
  const parts = relationName.split(ROPE_LABEL_CONSTANTS.PARSING.SEPARATOR);
  return parts.size() >= ROPE_LABEL_CONSTANTS.PARSING.MIN_PARTS;
}

import { padNumber } from "../../utils/stringUtils";
import { HEXAGON_CONSTANTS } from "./constants";

export function generateHexagonName(stackIndex: number, hexIndex: number): string {
  const hexStr = padNumber(hexIndex, 3);
  const stackStr = padNumber(stackIndex, 3);
  return `${HEXAGON_CONSTANTS.NAME_PREFIXES.HEXAGON}${hexStr}-${HEXAGON_CONSTANTS.NAME_PREFIXES.STACK}${stackStr}`;
}

export function createCenterCube(
  centerPosition: [number, number, number],
  hexIndex: number,
  stackIndex: number
): Part {
  const centerCube = new Instance("Part");
  centerCube.Name = `${HEXAGON_CONSTANTS.NAME_PREFIXES.CENTER_CUBE}-${HEXAGON_CONSTANTS.NAME_PREFIXES.HEXAGON}${padNumber(
    hexIndex,
    3
  )}-${HEXAGON_CONSTANTS.NAME_PREFIXES.STACK}${padNumber(stackIndex, 3)}`;
  centerCube.Size = new Vector3(
    HEXAGON_CONSTANTS.CENTER_CUBE.SIZE,
    HEXAGON_CONSTANTS.CENTER_CUBE.SIZE,
    HEXAGON_CONSTANTS.CENTER_CUBE.SIZE
  );
  centerCube.Position = new Vector3(
    centerPosition[0],
    centerPosition[1],
    centerPosition[2]
  );
  centerCube.Anchored = true;
  centerCube.Transparency = HEXAGON_CONSTANTS.CENTER_CUBE.TRANSPARENCY;
  centerCube.Color = Color3.fromRGB(
    HEXAGON_CONSTANTS.CENTER_CUBE.COLOR_RGB[0],
    HEXAGON_CONSTANTS.CENTER_CUBE.COLOR_RGB[1],
    HEXAGON_CONSTANTS.CENTER_CUBE.COLOR_RGB[2]
  );
  centerCube.CastShadow = false;

  return centerCube;
}

export function createCenterAttachment(
  centerCube: Part,
  hexIndex: number,
  stackIndex: number
): Attachment {
  const centerAttachment = new Instance("Attachment");
  centerAttachment.Name = `${HEXAGON_CONSTANTS.NAME_PREFIXES.CENTER_ATTACHMENT}-${HEXAGON_CONSTANTS.NAME_PREFIXES.HEXAGON}${padNumber(
    hexIndex,
    3
  )}-${HEXAGON_CONSTANTS.NAME_PREFIXES.STACK}${padNumber(stackIndex, 3)}`;
  centerAttachment.Position = new Vector3(0, 0, 0);
  centerAttachment.Parent = centerCube;

  return centerAttachment;
}

export function calculateBarDimensions(width: number, height: number) {
  // Calculate bar dimensions for a hexagon
  // For a regular hexagon with width W:
  // - Each side length = W/2 (radius)
  // - To form a solid hexagon with 3 overlapping bars, bar width = radius * 3
  const radius = width / 2;
  const barLength = radius;
  const barWidth = radius * math.sqrt(3); // Proper width for solid hexagon
  const barHeight = height;

  return {
    barLength,
    barWidth,
    barHeight,
    radius
  };
}

/**
 * Base class for block creation with common functionality
 * Part of F002 refactoring to consolidate block creation logic
 */

import { BLOCK_CONSTANTS } from "../constants/blockConstants";

export abstract class BaseBlockCreator {
  /**
   * Create a base Part with standard properties
   */
  protected createBasePart(name: string, size: Vector3, position: Vector3): Part {
    const part = new Instance("Part");
    part.Name = name;
    part.Size = size;
    part.Position = position;
    part.Anchored = true;
    part.TopSurface = Enum.SurfaceType.Smooth;
    part.BottomSurface = Enum.SurfaceType.Smooth;
    part.CanCollide = true;
    return part;
  }

  /**
   * Apply material, color, and transparency to a part
   */
  protected applyMaterial(
    part: Part, 
    material: Enum.Material, 
    color: Color3, 
    transparency: number
  ): void {
    part.Material = material;
    part.Color = color;
    part.Transparency = transparency;
  }

  /**
   * Create a block with full configuration
   */
  protected createBlock(config: {
    name: string;
    size: Vector3;
    position: Vector3;
    material: Enum.Material;
    color: Color3;
    transparency: number;
    canCollide?: boolean;
  }): Part {
    const part = this.createBasePart(config.name, config.size, config.position);
    this.applyMaterial(part, config.material, config.color, config.transparency);

    if (config.canCollide !== undefined) {
      part.CanCollide = config.canCollide;
    }

    return part;
  }

  /**
   * Calculate block dimensions with buffer
   */
  protected calculateBlockDimensions(
    bounds: { minX: number; maxX: number; minZ: number; maxZ: number },
    height: number = BLOCK_CONSTANTS.DIMENSIONS.DEFAULT_HEIGHT,
    buffer: number = BLOCK_CONSTANTS.DIMENSIONS.SHADOW_BUFFER
  ): { size: Vector3; position: Vector3 } {
    const width = bounds.maxX - bounds.minX + buffer * 2;
    const depth = bounds.maxZ - bounds.minZ + buffer * 2;
    const centerX = (bounds.minX + bounds.maxX) / 2;
    const centerZ = (bounds.minZ + bounds.maxZ) / 2;

    return {
      size: new Vector3(width, height, depth),
      position: new Vector3(centerX, 0, centerZ) // Y will be set by caller
    };
  }

  /**
   * Get color from array with wraparound
   */
  protected getColorFromArray(colors: Color3[], index: number): Color3 {
    return colors[index % colors.size()];
  }

}

