 * Converts legacy config to standardized config
 */
export function convertLegacyHexagonConfig(legacy: HexagonConfigLegacy): IHexagonMakerConfig {
  const config: IHexagonMakerConfig = {
    id: legacy.id,
    width: legacy.width,
    height: legacy.height,
    labels: legacy.labels,
    stackIndex: legacy.stackIndex,
    hexIndex: legacy.hexIndex,
    guid: legacy.guid,
  };

  // Convert position
  if (legacy.centerPosition) {
    config.position = new Vector3(
      legacy.centerPosition[0],
      legacy.centerPosition[1],
      legacy.centerPosition[2]
    );
  }

  // Convert bar properties
  if (legacy.barProps) {
    if (legacy.barProps.Color) {
      config.barColor = new Color3(
        legacy.barProps.Color[0],
        legacy.barProps.Color[1],
        legacy.barProps.Color[2]
      );
    }
    if (legacy.barProps.Material) {
      config.barMaterial = legacy.barProps.Material as unknown as Enum.Material;
    }
    if (legacy.barProps.Transparency !== undefined) {
      config.barTransparency = legacy.barProps.Transparency;
    }
  }

  return config;
}

/**
 * Platform and Shadow Manager
 * Handles creation of platform and shadow blocks
 */

import { PlatformBlockCreator } from "../../blocks/platformBlockCreator";
import { ShadowBlockCreator } from "../../blocks/shadowBlockCreator";
import { BLOCK_CONSTANTS } from "../../constants/blockConstants";
import { LAYOUT_CONSTANTS } from "../../constants/layoutConstants";
import {
  LaneBounds,
  PlatformShadowResult
} from "../types";

export class PlatformShadowManager {
  private platformCreator: PlatformBlockCreator;
  private shadowCreator: ShadowBlockCreator;

  constructor() {
    this.platformCreator = new PlatformBlockCreator();
    this.shadowCreator = new ShadowBlockCreator();
  }

  /**
   * Creates platform and shadow blocks based on lane bounds
   */
  public createPlatformAndShadow(
    targetOrigin: Vector3,
    parentFolder: Folder,
    allLaneBounds: LaneBounds
  ): PlatformShadowResult {
    // Create platform block
    const platform = this.platformCreator.createPlatformBlock({
      origin: targetOrigin,
      parent: parentFolder,
      height: BLOCK_CONSTANTS.DIMENSIONS.UNIFORM_SHADOW_THICKNESS,
      size: BLOCK_CONSTANTS.DIMENSIONS.PLATFORM_SIZE
    });

    // Create shadow block sized to encompass all lanes
    const shadow = this.shadowCreator.createGroupShadowBlock({
      origin: targetOrigin,
      parent: platform,
      height: BLOCK_CONSTANTS.DIMENSIONS.UNIFORM_SHADOW_THICKNESS,
      width: allLaneBounds.width + LAYOUT_CONSTANTS.SHADOW_PADDING.X_PADDING * 2,
      depth: allLaneBounds.depth + LAYOUT_CONSTANTS.SHADOW_PADDING.Z_PADDING * 2,
      buffer: BLOCK_CONSTANTS.DIMENSIONS.SHADOW_BUFFER
    });

    return { platform, shadow };
  }

  /**
   * Cleans up any existing platform and shadow blocks
   */
  public cleanupExistingBlocks(parentFolder: Folder): void {
    // Delete any existing platform and shadow blocks
    const existingPlatform = parentFolder.FindFirstChild("PlatformBlock");
    if (existingPlatform) {
      existingPlatform.Destroy();
    }

    // Also check for legacy flat block
    const existingBlock = parentFolder.FindFirstChild("FlatBlockFoundation");
    if (existingBlock) {
      existingBlock.Destroy();
    }
  }
}

/**
 * Standardized hex stack maker following IMaker pattern
 */

import { makeHexagon } from "../hexagonMaker";
import { IHexStackMakerConfig } from "./standardizedInterfaces";
import { HEX_STACK_CONSTANTS } from "./constants";
import { generateStackName, calculateLevelPosition } from "./utilities";

/**
 * Default color palette as Color3 array
 */
const DEFAULT_COLOR_PALETTE = HEX_STACK_CONSTANTS.DEFAULT_COLOR_PALETTE.map(
  color => new Color3(color[0], color[1], color[2])
);

/**
 * Creates a hex stack model with standardized configuration
 * @param config - Standardized hex stack configuration
 * @returns The created hex stack model
 */
export function makeHexStack(config: IHexStackMakerConfig): Model {
  // Extract configuration with defaults
  const {
    id = 1,
    position = new Vector3(0, 0, 0),
    width = HEX_STACK_CONSTANTS.DEFAULT_WIDTH,
    height = HEX_STACK_CONSTANTS.DEFAULT_HEIGHT,
    count = HEX_STACK_CONSTANTS.DEFAULT_COUNT,
    colors = DEFAULT_COLOR_PALETTE,
    stackIndex = 1,
    parent,
  } = config;


  const colorPalette = colors.size() > 0 ? colors : DEFAULT_COLOR_PALETTE;
  const hexagons: Model[] = [];

  // Create the stack model
  const stackModel = new Instance("Model");
  const stackName = generateStackName(stackIndex);
  stackModel.Name = stackName;

  // Create stacked hexagons
  for (let level = 0; level < count; level++) {
    const levelPosition = calculateLevelPosition(
      [position.X, position.Y, position.Z], // Convert to array for compatibility
      level,
      height
    );
    const levelColor = colorPalette[level % colorPalette.size()];

    // Create a hexagon for this level using standardized version
    const hexModel = makeHexagon({
      id: `${id}_level${level + 1}`,
      position: new Vector3(levelPosition[0], levelPosition[1], levelPosition[2]),
      width: width,
      height: height,
      barColor: levelColor,
      stackIndex: stackIndex,
      hexIndex: level + 1,
      parent: stackModel,
    });

    hexagons.push(hexModel);
  }

  // Set parent if provided
  if (parent) {
    stackModel.Parent = parent;
  }

  return stackModel;
}

