export function createGlobalSettings({
  parent,
  spacing,
  onSpacingChange
}: GlobalSettingsProps): Frame {
  // Create container
  const container = new Instance("Frame");
  container.Name = "GlobalSettings";
  container.Size = new UDim2(1, -20, 0, GUI_CONSTANTS.ENHANCED.GLOBAL_SETTINGS_HEIGHT);
  container.Position = new UDim2(0, 0, 0, 0); // Position will be set by layout manager
  container.BackgroundColor3 = new Color3(0.15, 0.15, 0.15);
  container.BorderSizePixel = 0;
  container.Parent = parent;

  const containerCorner = new Instance("UICorner");
  containerCorner.CornerRadius = new UDim(0, 4);
  containerCorner.Parent = container;

  // Title
  const title = new Instance("TextLabel");
  title.Size = new UDim2(1, -10, 0, 25);
  title.Position = new UDim2(0, 5, 0, 5);
  title.BackgroundTransparency = 1;
  title.Font = GUI_CONSTANTS.TYPOGRAPHY.TITLE_FONT;
  title.Text = "Global Settings";
  title.TextColor3 = GUI_CONSTANTS.COLORS.TEXT;
  title.TextScaled = true;
  title.Parent = container;

  // Add spacing controls
  createSpacingControls({
    parent: container,
    spacing,
    onSpacingChange
  });

  return container;
}

/**
 * Strongly typed node type definitions
 * Part of F002 refactoring for improved type safety
 */

// Core node type unions
export type PersonNodeType = "man" | "woman" | "child" | "grandparent";
export type AnimalNodeType = "Animals"; // Currently only one animal group type
export type NodeType = PersonNodeType | AnimalNodeType;

// Property type definitions
export interface PersonProperties {
  age: number;
  petType: string;
  petColor: string;
  firstName: string;
  lastName: string;
  countryOfBirth: string;
  countryOfResidence: string;
}

export interface AnimalProperties {
  animalType: string;
}

// Node property union
export type NodeProperties = PersonProperties | AnimalProperties;

// Type guards
export function isPersonNodeType(nodeType: string): nodeType is PersonNodeType {
  return ["man", "woman", "child", "grandparent"].includes(nodeType);
}

export function isAnimalNodeType(nodeType: string): nodeType is AnimalNodeType {
  return nodeType === "Animals";
}

export function isValidNodeType(nodeType: string): nodeType is NodeType {
  return isPersonNodeType(nodeType) || isAnimalNodeType(nodeType);
}

